css:
@-moz-document domain(tweetdeck.twitter.com) {

html.dark body, html.dark .app-content, html.dark .app-columns-container, html.dark .column { background-color: #16171a; }
html.dark .column-header, html.dark .column-header-temp { background-color: #16171a; }
html.dark .stream-item { border-bottom-color: #16171a; background-color: #16171a; }
html.dark .scroll-styled-v::-webkit-scrollbar-track, html.dark .scroll-styled-h::-webkit-scrollbar-track { border-left-color: #16171a; }
html.dark .column-header { border-bottom-color: #16171a }
html.dark .column-message { background-color: #16171a; }
html.dark .column-title-edit-box { background-color: #16171a; }
html.dark .scroll-styled-v::-webkit-scrollbar-thumb, html.dark .scroll-styled-h::-webkit-scrollbar-thumb { background-color: #34363d; }
html.dark .app-nav-tab-text { color: #34363d }
html.dark .app-header, html.dark .app-navigator, html.dark .app-title { background-color: #fff; }
html.dark .btn-compose { background-color: #16171a; }
html.dark .btn-compose:hover { background-color: #00aeef; }
html.dark .app-search-fake { color: #16171a; border: 1px solid #16171a; }
html.dark { color: #fff }
html.dark .column-nav-item { color: #16171a; background-color: #fff; }
html.dark .column-nav-link { color: #16171a; }
html.dark .app-nav-link, html.dark .app-nav-link-text { color: #16171a }

html.dark .column-nav-updates, html.dark .column-nav-link.is-selected, html.dark .column-nav-link:hover, html.dark a, html.dark .other-replies-link, html.dark a:hover, html.dark a:focus, html.dark a:active, html.dark .other-replies-link:hover, html.dark .btn-neutral-positive { color: #00aeef; }

html.dark .attach-compose-buttons .tweet-button, html.dark .compose { background-color: #34363d; }

}
