css:
@-moz-document url-prefix("https://avenoel.org/")
{
   html
  {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;
    background-image: url(https://i.imgur.com/jeZWUIG.gif);
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
  }

  @font-face
  {
    font-family: 'conthraxsemibold';
    src: url('https://dl.dropboxusercontent.com/s/48tnyv0jrx17pn7/conthrax-sb-webfont.woff2') format('woff2'),
         url('https://dl.dropboxusercontent.com/s/pxw2uvyg50xsik1/conthrax-sb-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
  } 
  
  @font-face
  {
    font-family: 'aurebeshregular';
    src: url('https://dl.dropboxusercontent.com/s/s1vk7udw6g71hfo/aurebesh-webfont.woff2') format('woff2'),
         url('https://dl.dropboxusercontent.com/s/emz6m04drlybaqy/aurebesh-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
  }
  
  @font-face
  {
    font-family: 'sf_distant_galaxyregular';
    src: url('https://dl.dropboxusercontent.com/s/fg1yf58zc6aeo9b/sf_distant_galaxy-webfont.woff2') format('woff2'),
         url('https://dl.dropboxusercontent.com/s/a6nbyzc05d6baw3/sf_distant_galaxy-webfont.woff') format('woff');
    font-weight: 400;
    font-style: normal;
  }

  body
  {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;
    background-color: rgba(78, 234, 243, 0);
  }

  main.container .container-content section
  {
     /*couleur de fond (derrière la liste des sujets et les blocs messages)*/
    background-color: rgb(78, 234, 243);
    opacity: 0.89;
  }

  tbody
  {
    background-color: rgb(128, 248, 255);
    opacity: 0.8;
 /*fond caché derrière la liste des sujet, faire attention à sa couleur en cas de transparence*/;
  }

  td
  {
    background-color: rgb(128, 248, 255);
 /*fond des lignes de la liste des sujets*/;
  }

  tr
  {
    background-color: rgb(128, 248, 255);
 /*fond de l'en tête de la liste des sujets*/
  }

  .active
  {
   font-family: 'aurebeshregular';
  }

  circle.background
  {
   display : none;
  }

  ellipse.stroke
  {
   display :none;
  }

  .onesignal-bell-svg
  {
   background-image: url(https://i.imgur.com/UnjyrEF.png) !important;
  }

  .form-control
  {
   font-family: sans-serif;
  }

  .breadcrumb a
  {
   color: rgb(12, 74, 77);
   font-family: 'aurebeshregular'; 
  }

  .breadcrumb
  {
    background-color: rgb(128, 248, 255); /*fond de la bande où il est écrit "avenoel.org / forum*/
  }

  tr.active, tr.active td
  {
    background-color: rgb(65, 202, 246) !important; /*couleur de fond lorsque le sujet est suivi et qu'il y a une réponse*/;
  }

  .btn.btn-primary
  {
    background-color: rgb(78, 234, 243) !important;
    font-family: conthraxsemibold;
 /*fond des boutons normaux*/;
  }

  .navbar li.active a
  {
    background-color: rgb(221, 51, 51) !important;
 /*fond lorsque un des onglets de la barre de navigation au dessus est actif*/
    opacity: 0.45;
  }

  .foreground
  {
    background-image: url(https://i.imgur.com/UnjyrEF.png) !important;
    opacity: 0;
  }

  .aside img
  {
    display: none;
  }

  .aside a
  {
    font-family: 'aurebeshregular';
  }

  .aside
  {
    background-image: url(https://i.imgur.com/oWtqDQZ.gif) !important;
    background-color: rgba(128, 248, 255, 0.34) !important;
    weight: 150px !important;
    height: 300px !important;
  }

  .bloc-title
  {
    color: #00a5a1;
    font-family: 'aurebeshregular';
  }

  .hidden-xs.hidden-sm.date
  {
    font-family: sans-serif;
  }

  label
  {
    color: #0077b3#25636c;
  }

  a, th, .topics-author, .topics-amount, .topics-date, .topic-title
  {
    color: #0B3861;
    font-family: 'conthraxsemibold';
  }

  .breadcrumb li.active
  {
    color: #0077b3 !important;
  }

  .row.topic-message.odd
  {
    background-color: #5dddea;
 /*fond messages impairs*/;
  }

  .row.topic-message
  {
    background-color: rgb(108, 220, 247);
 /*fond messages pairs*/;
  }

  .message-content
  {
    color: #0077b3;
  }

  .message-username a
  {
    color: #0077b3 !important;
  }

  .message-infos
  {
    color: #0077b3;
  }

  .message-content-quote
  {
    background-color: rgb(97, 209, 236) !important;
 /*fond des citations*/;
  }

  .message-content-quote a, .message-content-quote i
  {
    color: #0077b3;
  }

  .profile-wrapper, .profile-title, profile-stats
  {
    background-color: rgba(128, 248, 255, 0.19) !important;
 /*fond infos profil en dessous de l'avatar*/
    color: rgb(255, 255, 255) !important;
  }



  .profile-title
  {
    color: #0077b3 !important;
  }

  section
  {
    color: #0077b3;
  }

  .pagination.pagination-topic a
  {
    color: #0077b3 !important;
  }

  .chat-message, .chat-message-username a
  {
    color: #0077b3 !important;
  }

  .link
  {
    font-family: sans-serif;
    color : #034363;
  }

  .aside a
  {
    display: none;
  }

  .aside li:before 
  {
    display: none !important;
  }

  .spoiler-content
  {
    background-color: rgb(23, 234, 246) !important; /*fond spoiler*/
  }

  .grey-btn.btn
  {
    background-color: rgb(86, 246, 255); /*fond des boutons restans*/
  }

  .form-control.post-content, .form-control /*champ de nouveau message*/
  {
    opacity: 0.85;
    color: #0077b3;
  }

  a:hover
  {
    color: #0077b3;
  }

  .form-group.preview
  {
    background-color: rgb(128, 248, 255); /*ond prévisualisation*/
    color: #0077b3;
  }

  .profile-biography
  {
    background-color: rgb(128, 248, 255) !important; /*fond profil*/
    color: #0077b3;
  }

  .profile-wrapper-right td, .profile-wrapper-right tr, .profile-wrapper-right tbody, .profile-stats td, .profile-stats tr, .profile-stats tbody {
background-color: rgba(0, 0, 0, 0);
}

  a:focus
  {
    color: #0077b3;
  }

  .admin a
  {
    color: red !important;
  }

  .mod a
  {
    color: orange !important;
  }

  .active .topics-author a
  {
    color: white;
  }

  .row-form-inline
  {
   color: #0077b3; 
  }

  .chat-form
  {
    background-color: rgb(128, 248, 255);
  }

  .btn-danger
  {
    background-color: rgb(128, 248, 255); /*fond bouton suivre*/
    border-color: rgb(128, 248, 255);
    font-family :conthraxsemibold;
  }

  .btn-primary
  {
    background-color: rgb(160, 250, 255); /*fond bouton suivre*/
    border-color: rgb(128, 248, 255);
    font-family :conthraxsemibold;
    color : white !important;
  }

  .special a
  {
    color: rgb(128, 248, 255) !important;
  }

  .topics-author .special
  {
    color: #0B2161;
  }

  .topics-author a
  {
    color: #0B2161;
  }

  .btn.btn-primary:hover
  {
    background-color: rgb(84, 255, 139) !important; /*couleur survol bouton*/
  }

  .btn.grey-btn:hover
  {
    background-color: rgb(128, 248, 255) !important;
  }

  .navbar li a:hover
  {
    background-color: rgb(183, 28, 28) !important; /*couleur survol navbar en haut*/
    opacity : 0.3;
  }
  
  .navbar-logo img
  {
    background-image: url(https://i.imgur.com/a4J187v.png)!important;
    max-width: 200px;
    max-height: 41px;
    width: 100%;
    height: auto;
    padding-bottom: 41px!important;
  }

  .message-quote img
  {
    background-image: url(https://i.imgur.com/i3qInTE.png);
    max-width: 16px;
    max-height: 16px;
    padding-right: 16px!important;
  }
  
  .message-edit img
  {
    background-image: url(https://i.imgur.com/iD1b6hC.png);
    max-width: 16px;
    max-height: 16px;
    padding-right: 16px!important;
  }
  
  .message-delete img
  {
    background-image: url(https://i.imgur.com/JgehoGf.png);
    max-width: 16px;
    max-height: 16px;
    padding-right: 16px!important;
  }

  @media screen and (max-width: 640px) {
  .glyphicon 
  {
   background-size: 100%;
   background-repeat: no-repeat;
   background-position: center; 
  }
  } 
  
  .glyphicon-envelope
  {
    content: "\2709";
    background-image: url(https://i.imgur.com/HA62Vqp.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }
  
  .glyphicon-envelope.active
  {
    content: "\2709";
    background-image: url(https://i.imgur.com/GkFBxrk.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }
  
  .glyphicon-cog
  {
    content: "\2709";
    background-image: url(https://i.imgur.com/odssf4x.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }
  
  .glyphicon-off
  {
    content: "\2709";
    background-image: url(https://i.imgur.com/CjzbFgQ.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }

  .message-permalink
  {
   font-family: aurebeshregular;
  }

  .btn-primary
  {
   color: #069a98;
   font-family: 'sf_distant_galaxyregular';
  }

  .glyphicon-refresh
  {
   color: #136ea2;
  }


  .navbar-fixed-top
  {
   background-image: url(https://i.imgur.com/DJlH7Dz.png);
  }

  .footer
  {
   background-image: url(https://i.imgur.com/DJlH7Dz.png);
  }

  .navbar-nav a
  {
   font-family: 'SF Distant Galaxy' !important;
   color: #e6d542 !important;
  }

.text-muted
{
color : #74d8e3 !important;
}

.text-muted a
{
color : #4cd8e1;
font-family : sans-serif;
}

}
