css:
@-moz-document regexp("https?://(?!plus|notifications|docs|drive|mail|fonts|photos|calendar|get|productforums|hangouts).*.google.[a-z.]{2,6}/(?!_/chrome/newtab?|culturalinstitute|maps).*") {
body.hp #hplogo,
body.hp #fbar,
body.hp #prm-pt,
#footer,
.logo-subtext,
.gb_gf,
#fbar,
#prm-pt [aria-label="promo"] {
    display: none!important
}
:not(#⁠) {
    color: #161616!important
}
:not(#⁠) option {
    color: #000000!important
}
:not(#⁠):link {
    color: #000000!important
}
:not(#⁠):visited {
    color: #A0A0A0!important
}
::selection {
    background-color: #FFFFFF!important;
    color: #000000!important;
    text-shadow: none!important
}
::-moz-selection {
    background-color: #FFFFFF!important;
    color: #000000!important;
    text-shadow: none!important
}
html:not(#⁠),
#lst-ib:not(#⁠),
#hdtb:not(#⁠),
.sfbg.nojsv:not(#⁠),
[role="menu"]:not(#⁠),
[role="listbox"]:not(#⁠),
[aria-hidden="false"]:not(#⁠),
[aria-expanded="true"]:not(#⁠),
[role="dialog"]:not(#⁠),
.crp:not(#⁠),
.cdr_dlg:not(#⁠),
#hdtb-msb:not(#⁠),
#searchform:not(#⁠),
.jfk-scrollbar:not(#⁠),
.jfk-bubble:not(#⁠),
#qbp:not(#⁠),
#kbd:not(#⁠),
ul:not(#⁠),
header:not(#⁠),
[dir="ltr"]:not(#⁠) {
    background: url(https://images.alphacoders.com/748/748078.png)#000000 fixed!important;
    background-size: cover!important
}
.ab_button,
[role="button"]:not(.gb_Rb),
[type="submit"],
[type="button"],
.goog-flat-menu-button:not(#⁠) {
    background: transparent!important
}
#content,
#canvas3d,
.sfbgg,
.card-mask,
.ZvpjBb {
    background-image: none!important
}
#sfdiv,
#fkbx {
    border: 1px solid #FFF!important
}
#sfdiv.sbhcn {
    border: 1px solid #A0A0A0!important
}
#sfdiv.sbfcn {
    border: 1px solid #4D90FE!important
}
:not(#sfdiv):not(.mn-dwn-arw):not(._Bs):not(.goog-flat-menu-button-dropdown):not(#gt-src-wrap):not( #gt-res-wrap):not([type="email"]):not([type="password"]):not([type="text"]) {
    border: none!important
}
[aria-hidden="false"],
[role="listbox"],
[aria-expanded="true"]:not([role="button"]):not(#hdtbMenus),
[aria-haspopup="true"]:not([type="text"]):not([role="button"]),
.ab_dropdown,
g-menu {
    box-shadow: 0 0 1px #FFFFFF!important
}
:not([title]):not(.gb_qc),
[title^="Results"] {
    background-color: transparent!important
}
body.hp #lga {
    background: url(https://upload.wikimedia.org/wikipedia/commons/4/4b/Your_name_movie_logo.png)no-repeat center 109px!important;
    background-size: 272px 92px!important
}
#logo,
#logocont {
    background: url(https://vignette.wikia.nocookie.net/logopedia/images/f/f7/2000px-Google_2015_monochrome.svg.png/revision/latest?cb=20150901175304
)no-repeat center!important;
    background-size: 92px 30px!important
}
#logo>*,
#logocont>* {
    opacity: 0!important
}
.jfk-button-img {
    filter: invert(1)hue-rotate(180deg);
    -webkit-filter: invert(1)hue-rotate(180deg)
}
:not(#⁠) #gbwa>div:first-child>a,
:not(#⁠) #gbwa+div a>div {
    background-image: url(http://ssl.gstatic.com/gb/images/v1_76783e20.png)!important;
    background-size: 92px 2541px!important
}
:not(#⁠) #gbwa>div:first-child>a {
    background-position: -60px -1675px!important
}
:not(#⁠) #gbwa+div a>div {
    background-color: rgba(255, 255, 255, .55)!important;
    background-position: -92px -1921px!important
}
}

