css:
@-moz-document url("https://www.zhihu.com/"), regexp(".*zhihu\\.com\\/(explore|topic|notifications).*") {
body {
  background: #39464f
}
.AppHeader {
  background: #263238
}
.AppHeader-navItem.is-active {
  color: #d7ccc8
}
.App-main {
  background: #37474F !important
}
.TopstoryHeader-navItem {
  color: #B0BEC5
}
.TopstoryHeader-navItem:hover {
  color: #fff
}
.TopstoryHeader-rightItem:hover {
  color: #fff
}
.Card {
  background: #546E7A
}
.Card.TopstoryItem {
  background: linear-gradient(to right, #455A64, #546E7A)
}
.ContentItem-title a:hover {
  color: #fff
}
.ContentItem-actions {
  background: linear-gradient(to right, #455A64, #546E7A)
}
.RichContent-inner {
  color: #B0BEC5
}
.RichContent-inner:hover {
  color: #e0e0e0 !important
}
.ContentItem-more {
  color: #7986CB
}
.ContentItem-more:hover {
  color: #4FC3F7
}
.VoteButton {
  color: #B0BEC5
}
.AuthorInfo-badgeText {
  color: #bcaaa4
}
.RichText a:not(.video-box):not(.link-box):not(.member_mention):not(.VideoCard-link):not(.UserLink-link):hover {
  color: inherit
}
.GlobalSideBar-navLink {
	color: #B0BEC5
}
.GlobalSideBar-navLink:hover {
  background: #607D8B;
  color: white;
}
.GlobalSideBar-navNumber {
  background: inherit
}
.GlobalSideBar-navLink:hover .GlobalSideBar-navNumber {
  background: inherit
}
a {
  color: #CFD8DC
}
a:hover {
  color: #fff
}
}

@-moz-document url("https://www.zhihu.com/") {
.Comments-withPagination,
.CommentTopbar,
.Comments-footer {
  background: #607D8B;
  border: 0;
}
.Button--withIcon.Button--link,
.Button--withIcon.Button--plain,
.CommentItem-content,
.CommentItem-time {
  color: #eceff1
}
.CommentItem-footer .Button+.Button {
	color: #B0BEC5
}
.CommentItem-footer .Button+.Button:hover {
  color: #fafafa
}
.CommentItem-likeBtn {
  color: #263238
}
.CommentItem:not(:first-child):after {
	border-bottom-color: #78909C
}
.comments-pagination {
	border-top: none
}
.PaginationButton--current {
	color: #fff
}
}

@-moz-document regexp(".*zhihu\\.com\\/explore.*"), regexp(".*zhihu\\.com\\/topic.*"), regexp(".*zhihu\\.com\\/notifications.*") {
body {
  color: #B0BEC5
}
.zu-top {
  background-color: #263238
}
.feed-item+.feed-item,
.explore-side-section .list>li+li {
  border-top: 1px solid #546E7A
}
.page-explore .page-title {
  border-bottom: 1px solid #546E7A
}
.summary-wrapper .author-link-line,
.summary-wrapper .badge-summary {
  color: #CFD8DC
}
.author-link-line+.bio {
  color: #bcaaa4
}
html.no-touchevents .zh-summary:hover a.toggle-expand,
html.no-touchevents .zh-summary:hover .summary-tail-button {
  background: #698ebf
}
.badge-summary a {
  color: #009688
}
.topics-plaza {
  background: linear-gradient(to right bottom, #455A64, #546E7A);
  border: none;
}
.zm-side-nav-link,
.topic-feed-title,
.topics-plaza a.text
{
  color: #CFD8DC
}

.zm-side-nav-link.active,
html.no-touchevents .zm-side-nav-link:hover {
  background: linear-gradient(to right, #455A64, #546E7A);
  color: #fefefe;
  border-left: 0
}

.zg-btn-blue {
	background-color: #607d8b
}
}

@-moz-document regexp(".*zhihu\\.com\\/explore.*"), regexp(".*zhihu\\.com\\/topic.*"), regexp(".*zhihu\\.com\\/notifications.*") {
.zu-top-nav-li.current {
  background-image: none;
  background: #536d7a
}
.top-nav-profile .zu-top-nav-userinfo {
	background: #283337 !important;
}
.top-nav-profile:hover .zu-top-nav-userinfo {
  background-image: none;
  background: #546E7A !important;
}
.top-nav-profile a {
  background-color: #536d7a
}
.zu-top-nav-userinfo.selected, html.no-touchevents .top-nav-profile:hover .zu-top-nav-userinfo {
	background-image: none !important
}
html.no-touchevents .top-nav-dropdown a:hover {
  background-color: #607d8b
}
}
