css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Full HD comment visibility
   --------------------------
   For use on either smaller displays, or displays at
   a distance which makes comment text hard to read.
*/

@-moz-document domain("www.youtube.com"){
  
  .ytd-video-secondary-info-renderer ytd-expander[collapsed] > #content.ytd-expander{
    max-height: 70px !Important;
  }
  
  .ytd-comment-renderer ytd-expander[collapsed] > #content.ytd-expander{
    padding-bottom: 4px !Important;
  }
  
  .iron-autogrow-textarea{
    margin-bottom: 4px !Important;
    height: 100% !Important;
  }

  #description,
  .ytd-commentbox *,
  .ytd-comments yt-formatted-string:nth-child(2){
    font-size: 1.8rem !Important;
  }
}
