css:
@-moz-document url-prefix("https://unboxholics.com/forum") {
body {
color: #000 !important;
}
h1, h2, h3, h4, h5, h6 {
    color: #212121 !important;
}
}

@-moz-document url-prefix("https://unboxholics.com/register") {
body {
color: #000 !important;
}
}

@-moz-document domain("unboxholics.com") {
body {
    background-repeat: repeat;
    font-family: 'Google Sans', Roboto, RobotoDraft, Helvetica, Arial, sans-serif!important;
    background-color: #121212 !important;
    color: #e3e0e0;
}
.loader-mask {
    background-color: #1f1f1f !important;
}
.loader {
    color: #ef172f;
}
.logo{
background-image: url(https://unboxholics.com/theme/site/img/uh_logo_white.png);
    background-size: contain;
background-repeat:no-repeat;
background-position-y:center
}
.nav-icon-toggle__inner,.nav-icon-toggle__inner:before,.nav-icon-toggle__inner:after {
background-color:#fff
}
#instagram-embed-0{
    box-shadow: 0px 0px 20px 5px rgba(0, 0, 0, .25)!important;
    border:none!important;
    border-radius: 10px!important;
    background-color: #1f1f1f!important;
}
.sidenav {
background-color:#1f1f1f
}
p.follow-us-box {
    background-color: #1f1f1f;
}
.sidenav__menu-url:focus {
    color: #ffffff;
}
.sidenav__menu-url {
color:#fff
}

.sidenav__menu-toggle {
color:#fff
}

.sidenav__menu-dropdown {
background-color:#111
}

.sidenav__menu-dropdown a,.sidenav__menu-dropdown:hover,.sidenav__menu-dropdown .sidenav__menu-toggle {
color:#fff
}

.sidenav__menu--is-open>a {
background-color:#ef172f;
color:#fff
}

.sidenav__menu-dropdown {
background-color:#121212
}

.sidenav__menu li {
border-bottom:1px solid #ef172f
}

.sidenav__menu-dropdown li:first-child {
border-top:1px solid #ef172f
}

.sidenav__close-button {
color:#fff
}
.sponsored-label {
    background-color: #ff172b;
    padding: 55px;
}
li.merch:hover {
    background-color: #1f1f1f;
    transition: all .3s ease-in-out;
}
li.merch {
    background-color: #ef172f;
    transition: all .3s ease-in-out;
}

article.entry.mb-0.article {
    background-color: #1f1f1f;
}
.thumb--size-6{
    -webkit-mask-image: linear-gradient(to bottom, rgba(0, 0, 0, 1)60%, rgb(0 0 0 / 0%));
    height: 809px;
}
    @media only screen and (min-width: 992px){
.content-box--top-offset {
    margin-top: -332px;
}
}
#profile.tabs__content,.comment-notification-container.fancybox-content, .likers-container.fancybox-content, .report-content.fancybox-content {
    background-color: #1f1f1f;
}
.tabs__url,.tabs__item--active .tabs__url {
    color: #ffffff;
    background-color: #1f1f1f;
    border:none
}
ul.tabs__list,.tabs__url:hover {
    background-color: #1f1f1f;
        color: #ed1630;
}
.notification-item {
    color: #fff;
}
ul.nav__dropdown-menu.user-ul.user-notifications-list.d-none.display-user-menu {
    background-color: #1f1f1f;
}
li.notification-item:hover {
    background-color: #1f1f1f;
    color: #ed1630;
}

.nav__holder {
    background-color: #1f1f1f;
    -webkit-box-shadow: 0 2px 10px rgba(0,0,0,.1);
    box-shadow: 0 2px 10px #000;
}

.nav__menu>li>a {
    font-family: "Source Sans Pro",sans-serif;
    color: #ffff ;
}
.single-post__entry-title {
background-color:#1f1f1f00!important;
padding:0!important
}

.nav__search-trigger {
    color: #ffffff;
}
section.section.tab-post.mb-16 {
    background-color: #1f1f1f;
}
h1, h2, h3, h4, h5, h6 {
    color: #ffffff;
}
.style-default .card, .style-default .sidebar .widget, .style-default .content-box {
    -webkit-box-shadow: 0px 0px 20px 5px rgba(0, 0, 0, .25);
    box-shadow: 0px 0px 20px 5px rgba(0, 0, 0, .25);
}
.card__body {
    background-color: #1f1f1f;
}

.style-rounded .trending-now, .style-rounded .content-box, .style-rounded .card, .style-rounded .card__body, .style-rounded input, .style-rounded select, .style-rounded textarea,  .style-rounded .owl-prev, .style-rounded .owl-next, .style-rounded .thumb__img-holder, .style-rounded .entry__play-time, .style-rounded .pagination__page, .style-rounded #back-to-top, .style-rounded .sidebar .widget {
    background-color: #1f1f1f;
}

.title-wrap--line {
    border-top: 3px solid #ef172f;
}
.site-content{
    padding-top: 58px;
}
.entry-comments {
    top: 0px!important;
    box-shadow: 0 2px 10px #000;
    background-color: #1f1f1f;
}
.title-wrap.title-wrap--line.comment-slider-title-wrap {
    background-color: #1f1f1f;
}
button.close_comments {
    top: 10px;
    font-size: 40px;
}
.close {
    color: #ef172f;
    text-shadow: 0 1px 0 #000;
    opacity: 1;
}
.close:focus, .close:hover {
    color: #fff;
    opacity: 1;
}
p {
    color: #d2cfcf;
}
.nav__dropdown-menu {
    background-color: #1f1f1f;
        box-shadow: 0px 0px 20px 5px rgba(0, 0, 0, .25);
}

.nav__dropdown-menu>li>a {
    color: #d2cfcf;
}
.tab-post__tabs .tabs__item a {
    color: #d2cfcf;
}
.entry__img-holder:hover + div.entry__body > div > h2 > a,
.entry__img-holder:hover + div.entry__body.card__body > h2 > a,
.entry__img-holder:hover + div.entry__body.post-list__body.card__body > h2 > a,
.entry__img-holder:hover + div.entry__body.card__body > div.entry__header > h2 > a,
.entry__title:hover a {
    color: #ef172f;
}
.moon {
    color: #ff172d;
}
.nav__search-box {
    background-color: #1f1f1f;
}
.nav__search-input {
    color: #fff;
}
.btn-button:hover, .btn-button:focus {
    color: #ef172f;
    background-color: #121212!important;
}
#nav__search-box > form > button{
    
border-top-color: rgb(239, 23, 47) !important;
    border-top-style: solid !important;
    border-top-width: 1px !important;
    border-right-color: rgb(239, 23, 47) !important;
    border-right-style: solid !important;
    border-right-width: 1px !important;
    border-bottom-color: rgb(239, 23, 47) !important;
    border-bottom-style: solid !important;
    border-bottom-width: 1px !important;
    border-image-source: initial !important;
    border-image-slice: initial !important;
    border-image-width: initial !important;
    border-image-outset: initial !important;
    border-image-repeat: initial !important;
}
.tab-post__tabs .tabs__item--active a {
    color: #ef172f;
}
input, select, textarea {
    border: 1px solid #ef172f;
}

.breadcrumbs {
    background-color: #1f1f1f;
    box-shadow: 0px 0px 20px 5px rgba(0, 0, 0, .25);
}

.entry__meta a:hover {
    color: #fff;
}
a:hover, a:focus {
    text-decoration: none;
    color: #fff;
}
.entry__tags-label {
    color: #ef172f;
}
blockquote p {
    color: #d2cfcf;
}
select {
    color: #fff;
}
.paginate-results.show-more{
    filter:invert(91%)
}
.show-more .btn-dark {
    filter: invert(100%);
}
.comment-body {
    border-top: 1px solid #ef172f;
}
div.reviews-breadcrumbs .breadcrumbs {
    background-color: #1f1f1f!important;
}

div#extra-fields {
    border-radius: 20px;
    background-color: #ef172f2e;
}
ul.pagination li {
    background-color: #1f1f1f;
}
.entry-navigation a {
    color: #ef172f;
}
.entry-navigation a:hover {
    color: #fff;
}

textarea {

    color: white;
}

::selection {
    color: #fff;
    background: #ef172fb8;
}
div#main-container h1 {
    background-color: #1f1f1f;
}
}
