css:
@-moz-document domain(www.tumblr.com) {

.l-header-container--refresh {
    box-shadow: 0 0 0 1px hsl(275.3, 100%, 85.7%);
    background-color: #f2d6ff99;
}

.compose-button {
    fill: #e9b7ff;
}

.tab_bar .tab.active, .tab_bar .tab.active .tab_anchor, .tab_bar .tab.selected, .tab_bar .tab.selected .tab_anchor {
    color: #ffd7f5;
}

.tab_bar .tab_anchor {
    color: #ffacd8;
}

.ui_search.active .search_query, .ui_search.selected .search_query {
    background: #ffd6f4;
    color: #ebb6ff;
}

.search_popover {
    background-color: #f2d4ff;
}

.selected .tab_notice {
    background: #ffc9f3;
}

.tab-notice--outlined {
    border: 2px solid #ff9cf0e6;
}

.popover_header {
    background: #fcecff;
    border-bottom: 1px solid #f0a1ff;
    color: #ffb7e7;
}

.post.new_post .new_post_label {
    color: #e5a9ff;
}

.new_post_label i.icon_post_text, 
.new_post_label i.icon_post_photo, 
.new_post_label i.icon_post_quote,
.new_post_label i.icon_post_link,
.new_post_label i.icon_post_chat,
.new_post_label i.icon_post_audio,
.new_post_label i.icon_post_video {
    color: #e1a7ff;
}

.post.new_post {
    background-color: #f5d4ff;
    width: 542px;
    margin-top: 10px;
}

.post.post_full {
    background: #f5d4ff;
    border: 1px solid #fff
}

.post.post_full {
    background: #f5d4ff;
    border: 1px solid #e5b4ff;
    border-radius: 10px;
}

.post_full .post_header .post_info .post_info_link:first-child {
    color: #f1a9ff;
}

.post_full .post_info .post_info_fence .reblog_source .post_info_link {
    color: #FF99DD;
}

.post .post_info .reblog_icon {
    color: #fff1f8;
}

.reblog_follow_button .follow-text, .worded-follow-button .follow-text {
    color: #e2a9ff;
}

.post_full .post_header, .post_full .post_header .post_info .post_info_link {
    color: #FF99DD;
}

.reblog-list, .reblog-trail--original-post {
    border-top: 1px solid #E5B4FF;
}

.post_full .post_tags .post_tag {
    color: #f9d;
}

.post_full .post_footer {
    color: #FF99DD;
}

.post_controls .post_control::before {
    color: #FF99DD;
}

.post .post-source-footer {
    background: #f9ecff;
    color: #edc4ff;
    border: 1px solid #edc4ff;
    border-radius: 10px;
    margin-left: 3px;
}

.post-composer_note-post .note_item, .post_full.is_note .post-body .note_item, .post_full.is_note .post_body .note_item {
    background-color: #ffeaf7;
    border: 1px solid #ffc1eb;
    border-radius: 10px;
}

.post-composer_note-post .answerer .name, .post-composer_note-post .asker .name, .post_full.is_note .answerer .name, .post_full.is_note .asker .name {
    color: #f0b3ff;
}

.post-composer_note-post .asker, .post-composer_note-post .answerer, .post_full.is_note .asker, .post_full.is_note .answerer {
    color: #ffa3dd;
}

.l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column .right_column {
    background-color: #efcaff;
    width: 320px;
    border-radius: 10px;
    border: 1px solid #E5B4FF;
}

.l-container.l-container--two-column-dashboard .l-content, .l-container.l-container--two-column .l-content {
    background-color: #F2DCFBCC;
    border-radius: 10px;
    border: 1px solid #E5B4FF;
    width: 1000px;
}

.identity .right_column {
    position: relative;
    width: 229px;
    margin-top: 10px;
    margin-right: 5px;
}

.identity .controls_section.user_list li .follow_list_item_blog::before {
    background-image: linear-gradient(90deg,rgba(54,70,93,0),#EFCAFF);
    border-right: 5px solid #EFCAFF;
}

.identity .controls_section.user_list li:active .follow_list_item_blog::before, .identity .controls_section.user_list li:hover .follow_list_item_blog::before {
    background-image: linear-gradient(90deg,rgba(68,83,104,0),#EFCAFF);
    border-right: 5px solid #EFCAFF;
}

.radar .radar_footer .radar_avatar::before {
    background-image: linear-gradient(to right, rgba(54,70,93,0), #EFCAFF);
}

.plus-follow-button {
    background: hsla(283.4, 100%, 94.3%, 0.9);
    color: #E5B4FF;
}

.radar .radar_controls {
    background-color: #F5D4FF;
}

.identity .right_column::after {
    background: linear-gradient(180deg,transparent ,rgba(54,70,93,0));
}

.identity .controls_section li.account_header, .identity .controls_section li.contributors_header, .identity .controls_section li.editors_header, .identity .controls_section li.section_header {
    color: hsla(324.5, 100%, 95.7%, 0.9);
}

.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_title {
    color: hsla(327.3, 100%, 95.7%, 0.9);
}

.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name {
    color: hsla(283.7, 100%, 82%, 0.7);
}

.l-container.l-container--two-column-dashboard .left_column {
    background-color: transparent;
}

.radar .radar_tumblelog_label .name {
    color: rgba(255, 230, 245, 0.83);
}

.post_avatar .post_avatar_link {
    background: #F2DCFBCC 50% 50% no-repeat;
    border: 3px solid #FFBAE1;
    border-radius: 10px;
}

.post_avatar {
    border-radius: 3px;
    background-color: transparent;
}

.chrome.blue {
    border-color: #529ecc;
    background: #ffcfec;
    color: #ffb4de;
    border: 1px solid #ffb4de;
    border-radius: 5px;
    margin: 5px;
}

.l-container.l-container--flex .l-content {
    padding: 0;
    background-color: #f6e2ff80;
    border: 1px solid #E1B6FF;
}

.search_sub_header .search_term_heading {
    color: #f1c2ff;
    margin-top: 10px;
}

.search_sub_header .search_control.related_searches {
    color: hsla(324.8, 100%, 85.3%, 0.9);
}

.search_control.related_searches .search_control_link {
    color: hsla(287.7, 100%, 84.7%, 0.9);
}

.post_brick {
    border-radius: 3px;
    background: #ffecf7;
    border: 1px solid #ffc2eb;
}

.post_brick .post_tags .post_tag {
    color: #f2c9ff;
}

.dashboard_options_form, .tag_psa {
    border: 1px solid #ecb9ff;
    background-color: rgba(255,255,255,0.05);
    color: #e797ff;
}
    
.notification .notification_sentence .username, .notification .notification_sentence .banner_text {
    color: rgba(255, 171, 227, 0.91);
}
    
.notification {
    color: rgba(238, 172, 255, 0.74);
}
    
.notification .notification_sentence a {
    font-weight: normal;
    text-decoration: none;
    color: rgba(238, 172, 255, 0.74);
}
    
.peepr-body .indash_blog .post {
    background: #ffeffc;
}

a, body {
    color: #e797ff;
}

body {
    background: url(/*[[background]]*/) repeat center fixed! important;
}
    
/* Start http://www.cursors-4u.com */ body, a:hover {cursor: url(http://cur.cursors-4u.net/others/oth-8/oth713.ani), url(http://cur.cursors-4u.net/others/oth-8/oth713.png), progress !important;} /* End http://www.cursors-4u.com */
    
}
