css:
@-moz-document url-prefix("https://psnprofiles.com") {
/*             script built based on the theme                 **
**               by azekthi on userstyles.org                  **
** https://userstyles.org/styles/128408/psnprofiles-dark-theme */

html,
body,
.leaderboard .total,
#leaderboard big,
.leaderboard.zebra big,
div.title-bar.description span,
ul.trophy_count .level,
.leaderboard .points {
    color: #bbb;
}
table.gameInfo tr td:first-child {
    color: #bbb!important;
}
span.typo-top-date,
span.typo-bottom-date {
    color: #bbb!important;
}
html body#ipboard_body {
    color: #000;
}
div.dropdown.grow {
    background: #1d0467 !important;
}
a.dropdown-toggle,
div.button-bar.flex > div.dropdown.grow > a.trophy{
     background-color: rgba(0,0,0,0.4);   
}
div.session {
 background:none!important;   
}
.small_info,
.small-info {
    color: #CCC;
}
#content.page {
    background: #4F4F4F;
}
.element {
    background: #000;
    border-color: #999;
}
table.zebra tr:nth-child(odd),
ul.zebra li:nth-child(odd) {
    background: #3A3A3A;
}
table.zebra tr:nth-child(even),
ul.zebra li:nth-child(even),
.odd,
.box,
#facebox .inner,
#facebox div.title-bar {
    background: #4f4f4f;
}
#trophyTips .element .box {
    background: #333 !important;
}
tr.completed,
tr.me{
    background: #09520c !important;
}
tr.completed >td > a,
tr.completed >td >div >span > a,
tr.me > td> a{
    color: #b9e2b0 !important;
}
a.icon-sprite.trophy.bronze,
.tableofcontents li a.bronze{
 color:#f58450!important;   
}
a.icon-sprite.trophy.silver,
.tableofcontents li a.silver{
 color:#bbb!important;   
}
a.icon-sprite.trophy.gold,
.tableofcontents li a.gold {
 color: #eab55e!important;
}

a.icon-sprite.trophy.platinum,
.tableofcontents li a.platinum {
    color: #bbd1ff!important;
}
tr.platinum {
    background: #00195e !important;
}
tr.platinum >td > a,
tr.platinum >td >div >span > a{
    color: #bbd1ff !important;
}
.xs-hide.lg-show a,
.typo-top,
.typo-bottom {
    color: #ccc;
}
.xs-hide.lg-show a:hover {
    color: #fff!important;
}
.zebra a,
#slider .element .box table a,
.ellipsis a,
.row a,
h3.grow a,
div > a,
center > a,
ul.dropdown-menu > li > a,
div.grow h3 > a,
ul.fr-dropdown-list > li > a {
    color: #bbdefb !important;
    text-shadow: 0.5px 0.5px #000;
}
.social-links {
    display: none!important;
}
.fr-toolbar,
.custom-theme.fr-toolbar,
.fr-command.fr-btn {
    background: #4f4f4f!important;
    color: #bbb!important
}
.zebra a:hover,
ul.fr-dropdown-list > li >a:hover,
ul.dropdown-menu > li > a:hover,
#slider .element .box table a:hover,
.ellipsis a:hover,
.row a:hover,
div > a:hover,
center > a:hover,
h3.grow a,
div.grow h3 > a {
    color: #fff !important;
}
table.zebra td img[src="/lib/img/icons/platinum_icon_off.png"],
table.zebra td img[src="/lib/img/icons/complete_icon_off.png"] {
    opacity: 0.5;
}
.button.green {
    background: #0b5f07!important;
    color: #b9e2b0 !important;
}
.red {
 color:#ffcdd2!important;   
}
span.small_info_green,
a.green{
color: #b9e2b0 !important;}
picture.game > img {
    background: black!important;
}
picture.trophy > img {
    background: black!important;
}
picture.trophy.earned {
    border: 4px solid #b9e2b0 !important
}
a.dropdown-toggle.cf {
 background:none!important;   
}
text {
    color: #000!important;
    fill: #fff!important;
}
div.share-links {
    display: none!important;
}
.gr__psnprofiles_com > body > div.title-bar,
.banner div.title-bar,
.title-bar.width.no-banner {
    background: #1d0467 !important;
}
div.title {
    background: #1d0467!important;
}
#reCaptcha > div {
    margin: 0 auto;
}
div.title-bar h3 {
    color: #f3f3f3;
}
div.title-bar .navigation li a {
    color: #CCC;
}
div.title-bar .navigation li.active a,
div.title-bar .navigation li a:hover {
    color: #FFF;
}
div.title-bar .navigation li.active a {
    border-bottom: #00195e!important;
}
div.title-bar .navigation li a,
div.title-bar h3 a,
div.title-bar h3 {
    text-shadow: none;
}
.sidebar.large .element.no-top-radius .box table:nth-child(2) {
    background: #222!important;
}
#facebox .title-bar h1 {
    color: #fff !important;
    text-shadow: 1px 1px black !important;
}
#facebox .content .bottom {
    background: #222;
}
.box {
    border-color: #898989;
}
.leaderboard-top {
    background: #222;
}
div.leaderboard-top .tab {
    background: #111;
}
div.leaderboard-top .tab.active,
div.leaderboard-top .tab:hover {
    background: #333;
    color: #fff;
}
input {
    background: black!important;
    color: #bbb!important;
}
.typo-top-small{
 color:#bbb;   
}

div.fr-wrapper.show-placeholder >  span.fr-placeholder{
    color:#bbb}


/* Guides */

div.guide,
div.box.light,
.custom-theme.fr-box.fr-basic .fr-wrapper {
    background: none!important;
}
.roadmap-trophies .trophy.earned,
.tableofcontents li.earned {
    background: #09520c !important
}
table.zebra th {
 background:none!important;   
}
.tableofcontents li,
.tableofcontents li.earned {
    border-top-color: #898989 !important;
    border-bottom-color: #898989 !important;
}
.roadmap-trophies .trophy {
    background: rgba(255, 255, 255, 0.02);
}
.roadmap-trophies li span,
.tableofcontents li span,
.guide,
.custom-theme.fr-box.fr-basic .fr-element {
    color: #ddd;
}
.custom-theme.fr-box.fr-basic .fr-wrapper {
 background:none!important;   
}
#Roadmap h1,
.guide h1 {
    color: #fff;
}
.guide h2 {
    color: #eee;
}
#messageList .message > div.guide{
 background:none!important;
}
.CodeMirror {
    color: #ddd;
    background: #222;
}
.guide table {
    background: #303030;
}
.guide table tr:nth-child(even) td {
    background: #2A2A2A;
}
.dropdown-menu,
ul.fr-dropdown-list li > a {
    background-color: #4f4f4f!important;
    background: #4f4f4f!important;
}
.dropdown-menu li > a:hover,
ul.fr-dropdown-list > li >a:hover {
    background: #1d0467!important;
}
.button.blue {
    background: #1d0467!important;
}
.form,
label.checkbox {
    color: #bbb !important;
}
textarea,
select {
    background: #000!important;
    color: #bbb!important;
}
.divider {
    background-color: #bbb!important;
    border-bottom: 1px solid #bbb!important;
}
.guide table tr td,
.guide table th,
.guide table.invisible tr td,
.guide table.invisible th {
    color: #ccc;
}
.guide table th {
    background: #222;
}
.guide a:hover {
    color: #fff;
}
/* removes blue bottom border from banners sitewide */

.banner {
    border-bottom: 0px solid #336291!important;
}
/* removes blue bottom border from top bar sitewide */

.header-border {
    height: 0px!important;
}
span.separator.completion-status > span.platinum.earned,
span.separator.completion-status > span.platinum,
span.separator.completion-status > span.earned.completion,
span.separator.completion-status > span.completion,
.create-guide.green.button,
.col-xs-8 > .box:nth-of-type(2),
div.hidden-trophies,
div.premium-bar,
div.navigation.md-show > ul > li:nth-child(2),
div.navigation.md-show > ul > li:nth-child(7),
div.navigation.md-show > ul > li:nth-child(8),
div.navigation.md-show > ul > li:nth-child(9),
div.col-lg-4.col-xs-5 > div.box.no-top-border:nth-of-type(3),
div.col-lg-4.col-xs-5 > div.v-align.flex.title:nth-of-type(2),
div.col-lg-4.col-xs-5 > div.box.no-top-border:nth-of-type(2),
div.col-lg-4.col-xs-5 > div.v-align.flex.title:nth-of-type(1) {
    display: none!important;
}
.box + div.title {
    margin-top: 0px!important;
}
svg > g > path {
    fill: #4f4f4f!important;
}
tspan,
g > text {
    color: #bbb!important;
    fill: #bbb!important;
}
}
