css:
@-moz-document url(https://www.techbargains.com) {
.coupon-feed,
.rail,
.right-rail,
.carousel,
.bottom-info,
.see-deal,
.breadcrumb,
.ratings,
.page-scroller,
.nav-btm-row,
.editors-choice {
  display: none !important;
}

body {
  background: #f2f2f2;
}

#page-content {
	padding-top: 53px;  
}

.main {
  width: 100% !important;
}

header {
  border: 0;
}

#store-detail-page .page-body .container:before {
  background: transparent;
}

.deals-container {
  margin-right: -10px;
  margin-left: -10px;
  display: flex;
  flex-wrap: wrap;
}

.deal {
  width: calc(33.33333% - 20px);
  margin-right: 10px !important;
  margin-left: 10px !important;
  box-shadow: 0 5px 10px -5px rgba(0,0,0,.08);
}

.deal-feed .deal .content .price {
  width: 100% !important;
  float: none;
}

.deal-feed .deal h1, 
.deal-feed .deal h2 {
  font-size: 20px;
  margin: 15px 0;
  line-height: 1.25;
}

.image.col-sm-4.col-xs-4.text-center,
.content.col-sm-8.col-xs-8 {
  width: 100%;
}

.deal-feed .deal .content .description ul {
  font-size: 12px;
}

.deal-feed .deal .image a {
  margin: 0 auto 10px;
}
}
