css:
@-moz-document domain("siam.ub.ac.id") {
/* global font family */

div,
a,
b,
en,
td {
    font-family: "Comic Sans MS", cursive, sans-serif !important;
}
/* Scrollbar Style START */

body > table:nth-child(2),
body > table:nth-child(3) {
    margin-left: auto!important;
    margin-right: auto!important;
}
body > table:nth-child(3) {
    position: relative;
    left: -10px;
}

body::-webkit-scrollbar {
    width: 1vw;
    background: transparent;
}
body::-webkit-scrollbar-thumb {
    background: -moz-linear-gradient(left, rgba(30, 87, 153, .6) 42%, rgba(30, 87, 153, .6) 42%, rgba(125, 185, 232, .6) 100%);
    background: -webkit-linear-gradient(left, rgba(30, 87, 153, .6) 42%, rgba(30, 87, 153, .6) 42%, rgba(125, 185, 232, .6) 100%);
    background: linear-gradient(to right, rgba(30, 87, 153, .6) 42%, rgba(30, 87, 153, .6) 42%, rgba(125, 185, 232, .6) 100%);
    -webkit-transition-duration: 0.4s;
    /* Safari */
    transition-duration: 0.4s;
}
body::-webkit-scrollbar-thumb:hover {
    background: -moz-linear-gradient(left, rgba(30, 87, 153, 1) 42%, rgba(30, 87, 153, 1) 42%, rgba(125, 185, 232, 1) 100%);
    background: -webkit-linear-gradient(left, rgba(30, 87, 153, 1) 42%, rgba(30, 87, 153, 1) 42%, rgba(125, 185, 232, 1) 100%);
    background: linear-gradient(to right, rgba(30, 87, 153, 1) 42%, rgba(30, 87, 153, 1) 42%, rgba(125, 185, 232, 1) 100%);
}
body::-webkit-scrollbar-track {
    background: transparent;
}
/* Scrollbar Style END */
/* Button Style START */

input[type="button"],
input[type="submit"],
input[class*="button"],
button {
    display: inline-block;
    border-radius: 5px;
    border: none;
    background-color: rgba(159, 212, 235, .7)!important;
    color: rgb(255, 255, 255)!important;
    text-align: center;
    padding: 7px;
    -webkit-transition-duration: 0.4s;
    /* Safari */
    
    transition-duration: 0.4s;
    cursor: pointer;
    margin: 2px;
}
input[type="button"]:hover,
input[class*="button"]:hover,
input[type="submit"]:hover,
button:hover {
    background-color: rgba(18, 84, 163, 1)!important;
    color: rgb(255, 255, 255)!important;
}
button[disabled] {
    background-color: rgba(0, 0, 0, .4)!important;
    cursor: not-allowed!important;
}
button[disabled]:hover {
    background-color: rgba(0, 0, 0, 1)!important;
    cursor: not-allowed!important;
}
/* Button Stye END */

body {
    background-color: rgb(0,0,0)!important;
    background-image: url(https://i.imgur.com/pDVL4LT.png)!important; /* background image direct link */
    background-repeat: no-repeat!important;
    background-attachment: fixed!important;
    background-position: 0% 24%!important;
    background-size: 100vw auto!important;
    width: 100vw!important;
    overflow-x: hidden;
}

@media only screen and (max-width:1326px){
    body {
    	background-size: cover!important;
        background-position: 5% 24%!important;
    }
}


/* main panel background color */
.text2 {
    background-color: rgba(255, 255, 255, .5)!important;
    border-radius: 15px!important;
    padding: 10px!important;
}
.text {
    background: transparent!important;
    color: #1B2D70!important;
}
.textWhite {
    background-color: rgba(0, 0, 0, .4)!important;
    color: rgb(255, 255, 255)!important;
}
#TB_overlay.TB_overlayBG {
    background-color: rgba(0, 0, 0, .7)
}
#TB_window {
    background: transparent!important;
    border-color: rgba(0, 0, 0, 0)!important;
}
#TB_window #TB_title {
    background-color: rgba(83, 136, 159, .5)!important;
    border-radius: 5px!important;
    margin-bottom: 6px!important;
}
#TB_closeAjaxWindow {
    width: 0px!important;
    visibility: hidden!important;
}
#TB_ajaxWindowTitle {
    float: none!important;
    margin: auto!important;
    text-align: center!important;
    font-weight: bold!important;
    font-size: large!important;
}
#TB_window #TB_ajaxContent {
    padding: 0px!important;
    width: auto!important;
    height: auto!important;
    max-height: 250px!important;
    background-color: rgba(255, 255, 255, .6)
}
#TB_window #TB_ajaxContent > table > tbody > tr {
    background-color: rgba(255, 255, 255, .0);
    color: #1B2D70!important;
}
}

@-moz-document url("https://siam.ub.ac.id/index.php"), url("https://siam.ub.ac.id/") {
.frame-login {
    background-color: rgba(255, 255, 255, .5)!important;
}
.body-selma.login-selma {
    position: fixed!important;
    top: 0px!important;
    left: 0px!important;
    width: 100%!important;
    height: 100%!important;
}
.inner-warning {
    background-color: rgba(159, 212, 235, .4)!important;
    color: rgba(18, 84, 163, 1)!important;
    font-weight: lighter!important;
}
body > div.wrapper.clear > div > div.footer-login.row.clear > div {
    width: 100%!important;
}
body > div.wrapper.clear > div > div.header-login.row.clear > div.frame-title.logo.w-40.left > div > h1 > img {
    opacity: .7!important;
}
body > div.wrapper.clear > div > div.footer-login.row.clear > div > div > strong::after {
    white-space: pre;
    content: "    |    Tema 'Nagi no Asukara' oleh Wahyu Darkflame";
    font-weight: 500!important;
}
body > div.wrapper.clear > div > div.footer-login.row.clear > div > div::after {
    white-space: pre;
    content: "Diperbarui: 06 Agustus, 2018 - Versi Tema: 1.4.1";
    font-weight: 300!important;
    font-style: italic;
    font-size: x-small;
}
}

@-moz-document regexp("https:\\/\\/siam\\.ub\\.ac\\.id\\/(?!index\\.php).+") {
img[src="images/title-en.png"] {
    content: url("https://i.imgur.com/pxR82HH.png")!important;
    /* Header picture */
}
body > table:nth-child(3) > tbody > tr:nth-child(1) > td.text2 > table:nth-child(3) > tbody > tr:nth-child(1) > td.text > strong > big {
    color: #1B2D70!important;
}
body > table:nth-child(3) > tbody > tr:nth-child(1) > td.text2 > table:nth-child(3) > tbody > tr:nth-child(1) > td.text > div > div.textgreen > b {
    color: rgb(18, 84, 163)!important;
}
area[shape="rect"] {
    opacity: 0!important;
}
body > table:nth-child(3) > tbody > tr:nth-child(1) > td.text2 > table:nth-child(3) > tbody > tr:nth-child(1) > td:nth-child(1),
body > table:nth-child(3) > tbody > tr:nth-child(1) > td.text2 > table:nth-child(3) > tbody > tr:nth-child(1) > td:nth-child(2) {
    visibility: hidden!important;
}
body > table:nth-child(3) > tbody > tr:nth-child(1) > td.text2 > table:nth-child(3) > tbody > tr:nth-child(1) > td.text {
    margin-left: -100px!important;
}
img[src*="images/lbottom"],
img[src*="images/rbottom"],
img[src*="images/pixel.gif"],
td[background*="images/right"],
td[background*="images/left"],
td[background*="images/bottom"],
img[src*="linesec"] {
    visibility: collapse!important;
}
td[background*="images/left"] {
    width: 0px!important;
}
table > tbody > tr > td[bgcolor="#cccccc"] {
    background: transparent!important;
}
td[colspan="4"] > div[style^="margin-top:10px; padding:5px;"],
a[href="akun.php"] > div {
    visibility: hidden!important;
    height: 0!important;
    margin-top: -20px;
    padding: 0px!important;
}
body > table:nth-child(3) > tbody > tr:nth-child(1) > td.text2 > div:nth-child(1) > div {
    visibility: hidden!important;
}
body > table:nth-child(3) > tbody > tr:nth-child(1) > td.text2 > div:nth-child(1) {
    margin-right: 0px!important;
}
}

@-moz-document url("https://siam.ub.ac.id/akademik.php") {
body > table:nth-child(3) > tbody > tr:nth-child(1) > td.text2 > div:nth-child(12) > div:nth-child(1),
