@font-face {
  font-family: 'Nunito Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Nunito Sans Regular'), local('NunitoSans-Regular'), url(https://fonts.gstatic.com/s/nunitosans/v3/iJ4p9wO0GDKJ-D5teKuZqo4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2212, U+2215;
}

div.text_to_html p img {
    width: 100px;
    min-width: 100px !important;
    height: 100px !important;
}
img.icon {
    display: none;
}
div.column.c1 a {
    white-space: nowrap;
    font-size: 15px;
}
#page-wrap2 {
    background: none !important;
}
#custommenu {
    width: 100%;
}
#wrapper {
    padding: 0 20px;
}
a.yui3-menuitem-content {
    color: #fff;
    text-transform: uppercase;
    font-size: 15px;
    font-weight: bold;
    font-family: 'Nunito Sans';
}
.yui3-menu-horizontal li {
    float: none;
    display: inline-block;
}
#custommenu .yui3-menuitem-active {
    background: none;
}
h1.headermain {
    font-size: 40px;
    font-family: Montserrat;
    text-shadow: none;
}
div.text_to_html p {
    text-align: center;
}
div.mod-indent {
    background-color: #fff;
}
li.section.main.clearfix {
    background: white;
    box-shadow: 0 0 5px 0 rgba(0, 0, 0, .2);
    margin-bottom: 15px !important;
    border: none;
}
h3.sectionname {
    color: #333 !important;
    font-size: 15px !important;
}
.path-course-view .weeks .content,
.path-course-view .topics .content,
.path-course-view .weeks .section,
.path-course-view .topics .section {
    background-color: #fff;
}
.path-course-view .section .mod-indent {
    padding: 0;
}
#page-course-view-topics .course-content ul.topics li.section .left {
    min-height: unset;
    width: unset;
}
div.left.side {
    background-color: #fff;
}
#page-course-view-topics .course-content ul.topics li.section .content {
    margin: 0;
    min-height: initial;
    padding-bottom: 5px;
}
.sitetopic .section .activity:hover,
.course-content .section .activity:hover {
    background-color: #fff;
}
.path-course-view .section .content .section {
    margin-left: 20px;
}
#inst8798 div.content {
    display: none;
}
#instance-8797 div.content {
    display: none;
}
#inst8797 div.content {
    display: none;
}
#inst-8799 div.content {
    display: none;
}
#inst8799 div.content {
    display: none;
}
#inst8800 div.content {
    display: none;
}
#inst8800 {
    display: none;
}
#inst8799 {
    display: none;
}
#inst8798 {
    display: none;
}
#inst8797 {
    display: none;
}
#page-content #region-post {
    border-radius: 10px;
    box-shadow: 0 0 5px 0 rgba(0, 0, 0, .2);
}
#region-post-wrap-2 {
    background: #fff;
    padding: 20px;
}
#region-post-wrap-1 {
    padding: 0;
}
#page-content #region-post .region-content {
    padding: 0;
}
block_search_forums block {
    display: none;
}
.block_search_forums.block {
    display: none;
}
.block_calendar_upcoming.block {
    display: none;
}
.block_recent_activity.block {
    display: none;
}
#page-content #region-main .region-content {
    padding-left: 45px;
}
.no-overflow p {
    margin-bottom: 0;
}
.no-overflow {
    padding: 0;
}
#page-wrap1 {
    background: linear-gradient(to bottom right, #026496, #047bb6) !important;
    height: 200px;
    box-shadow: 0 2px 10px 0 rgba(0, 0, 0, .2);
}
#page-footer {
    display: none;
}
p.tagline {
    color: #fff;
    text-shadow: none;
}
ul.first-of-type {
    text-align: center;
}
div.content {
    padding: 10px;
}
#region-post-wrap-2 div.no-overflow {
    overflow: unset;
}
#custommenu .yui3-menu-label,
#custommenu .yui3-menuitem-content {
    color: #fff;
}
body {
    background: none;
    font-family: 'Nunito Sans';
}
h2 {
    font-family: 'Nunito Sans';
}
.navbar .wrapper a{
    color: #fff;
}
.navbar .wrapper span{
    color: #fff;
}
}

@-moz-document url("http://moodle.kdupg.edu.my/login/index.php") {
#page-wrap2 {
    background: none;
}
#page-wrap1 {
    background: linear-gradient(to bottom right, #026496, #047bb6) !important;
    height: 200px;
    box-shadow: 0 2px 10px 0 rgba(0, 0, 0, .2);
}
h1.headermain {
    text-align: center;
    display: inline-block;
    overflow: hidden;
    text-indent: -9999px;
}
.loginbox .loginform .form-input input {
    padding: 11px 10px;
    width: 150px;
    border: 1px solid #ccc;
    margin-bottom: 10px;
    font-family: 'Nunito Sans';
}
input[type='submit'] {
    border: none !important;
    display: block;
    background-color: #047bb6;
    color: #fff;
    font-family: 'Nunito Sans';
    height: 44px;
    width: 200px;
}
#region-header.inside .headermain::before {
    font-size: 1em;
    content: 'Login';
    display: inline-block;
    white-space: nowrap;
    position: absolute;
    right: 48%;
    color: white;
    text-indent: 11002px;
}
.loginbox .loginpanel .desc {
    display: none;
}
input[type='submit']:hover {
    background-color: #0097e2;
    color: #fff;
}
.loginbox .loginform .form-label {
    margin-top: 11px;
}
.loginbox h2 {
    padding: 0;
}
.loginbox .subcontent {
    margin: 0;
    padding: 0;
}
.guestform input[type='submit'] {
    margin: auto;
}
.guestform {
    margin-top: 20px;
    text-align: center;
}
.navbar .wrapper a{
    color: #2d83d5;
}
.breadcrumb li:last-child {
    display: none;
}
.navbar {
    margin-top: 75px;
    margin-bottom: 0;
}
.loginbox {
    margin-top: 0;
}
}

@-moz-document url-prefix("http://moodle.kdupg.edu.my/course/") {
#page-course-view-topics .course-content ul.topics li.section .left,
#page-course-view-topics .course-content ul.topics li.section .right {
    display: none;
}
ul.topics li.section {
    margin-bottom: 15px;
}
.course-content ul.topics {
    margin-top: 40px;
}
.navbar .wrapper a{
    color: #fff;
}
.navbar .wrapper span{
    color: #fff;
}
h3{
    font-family: 'Nunito Sans';
}
.course-content .section.main .content{
    padding-bottom: 5px;
}
}

@-moz-document regexp("http://moodle.kdupg.edu.my/") {
.sitetopic .section li.activity,
.course-content .section li.activity {
    padding: 10px;
    box-shadow: 0 0 5px 0 rgba(0, 0, 0, .2);
    margin-bottom: 15px;
}
#page-site-index #region-header {
    margin-bottom: 65px;
}
li.activity.forum.modtype_forum {
    background-color: #fff;
    box-shadow: 0 0 5px 0 rgba(0, 0, 0, .2);
    margin-bottom: 10px !important;
    padding: 10px;
}
}
