css:
@-moz-document domain("app.nuclino.com") {
body,
div {
    background-color: #333 !Important;
    color: #ddd;
}
.ProseMirror a {
    color: #3282ad !important;
}
.ProseMirror a {
    color: #2C75FF;
    -webkit-transition: color .5s cubic-bezier(1, 1, 1, 1);
    transition: color .5s cubic-bezier(1, 1, 1, 1);
}
p strong {
    color: #fff;
}
p.caption strong {
    color: #fff !important;
}
.ProseMirror p strong {
    color: #fff;
}
.ProseMirror p {
    color: #ccc;
}
h1 {
    color: #fff !important;
}
.ProseMirror h1 {
    text-shadow: none !important;
    color: #fff !important;
}
h2 {
    color: #5f7081;
}
.ProseMirror h2 {
    text-shadow: none !important;
    color: #5f7081 !important;
}
h3 {
    color: #ddd;
}
::selection {
    background-color: #4183C4 !important;
    color: #fff !important;
}
::-moz-selection {
    background-color: #4183C4 !important;
    color: #fff !important;
}
.ProseMirror h1:hover,
.ProseMirror h2:hover,
.ProseMirror h3:hover,
.ProseMirror h4:hover,
.ProseMirror h5:hover,
.ProseMirror h6:hover,
.ProseMirror p:hover {
    background-color: #222 !Important;
}
