css:
@-moz-document domain("assoziations-blaster.de")
{body {
    background-image:none;
    background-color:#000!important;
    color:#bbb;
    --accent: #5beaa5;
    font-family:'Segoe UI', Sans-Serif;
}
#ad1, #ad2 {
    filter:invert() hue-rotate(180deg) contrast(80%) brightness(50%);
    opacity:.5;
}
td>small>font>a>img {
    display:inline;
    visibility: visible;
    filter:grayscale(100%) invert() brightness(20%)
}
table.info {
    border:none;
    border-spacing:8px;
}
.info td {
    background-color:#000;
    transition:.2s ease;
    border:2px solid transparent;
}
.info td {
    padding:.5em;
}
.info tr td:nth-child(2), font {
    color:#bbb;
}
.info tr td:nth-child(2):hover {
    border:2px solid #333;
    background-color:#202020;
}
td {
    background-color:#000!important;;
    color:#fff;
    font-family:'Segoe UI', Sans-Serif;
    font-weight:400;
    font-size:1.25em;
}
h1 {
    font-family:'Segoe UI', Sans-Serif;
    font-weight:200;
    font-size:3em;
    margin:0;
}
h1::first-letter {
    text-transform:  uppercase;
    font-size: 1.2em;
}
.text h3 {
    font-family:'Segoe UI', Sans-Serif;
    font-weight:200;
    margin:0;
}
h1 a {
    color:var(--accent)!important
}
#head h1 a {
    color:#fff!important;
	text-align:center;
}
.text h3 a {
    color:var(--accent)
}
#head {
    margin:2em 0;
}
a {
    color:inherit;
    border:none;
    text-decoration:none;
    position:relative;
    transition:.2s ease;
}
p a:after {
    z-index:-1;
    content:'';
    position:absolute;
    top:0;
    left:-3px;
    width:calc(100% + 4px);
    height:calc(100% - 2px);
}
p a:hover:after {
    border:1px solid #333;
    background-color:#202020;
}
a:hover {
    color:#fff;
    background-color:transparent;
}

div.text {
    background-image:none;
    font-family:'Segoe UI', Sans-Serif;
    font-weight:400;
    font-size:1.2em;
    border-bottom:1px solid #333;
}
p {
    letter-spacing:0.8;
    line-height:1.3
}
.blasttext {
    letter-spacing:0.8;
    line-height:1.3;
    font-weight:300;
}
strong {
    font-weight:100;
    color:var(--accent);
}

.info tr:nth-child(1),.info tr:nth-child(2),.info tr:nth-child(3),.info tr:nth-child(4), .info tr:nth-child(6),
.mininavbox, #head h3, #head small,
.midhead, body>hr, body>small {
    display:none;
}

font[color='#cccccc'],td img {
    display:none;
}
input,textarea {
    color:#999;
    margin:0;
    outline:none;
    resize:vertical;
    font-family:'Segoe UI', Sans-Serif;
    transition:.2s ease;
    width:140%;
    position:relative;
    left:-20%;
}
textarea {
    border:1px solid #333;
    background-color:#202020;
}
input[type=text] {
    font-size:1.2em;
    width:80%;
    left:10%;
    border:1px solid transparent;
    background-color: transparent;
}
input[type=text]:focus,input[type=text]:hover {
    border:1px solid #333;
    background-color:#202020;
}
input[type=submit] {
    text-transform:uppercase;
    font-size:1.25em;
    font-weight:200;
    margin:1em 0;
    width:80%;
    left:10%;
    border:1px solid transparent;
    background-color: transparent;
    cursor:pointer;
}
input[type=submit]:hover, input[type=submit]:active, input[type=submit]:focus {
    border:1px solid #333;
    background-color:#202020;
}
center>table:nth-child(1) td>small>font {
    visibility:hidden;
}
font b a {
    visibility:visible;
    font-family:'Segoe UI', Sans-Serif;
    font-weight:200;
    color:var(--accent)
}
.nohover, .nohover:hover {
	background-color:transparent;
}
body>center>table:last-of-type {
    visibility: hidden;
}
}
