css:
@-moz-document domain("indigo.cafe") {
#global-menu li.selected a {
    color: #5ac800 !important;
}
#global-menu li.selected a {
    color: #5ac800 !important;
}
#global-menu li.selected a::before {
    color: #5ac800 !important;
}
#global-menu li a:hover,
#global-menu li button:hover {
    box-shadow: inset 0 -4px 0 -1px #5ac800;
}
#global-menu #global-my-menu .symbol::before {
    font-size: 18px;
    line-height: 18px;
    width: 22px;
    text-align: center;
    color: #5ac800;
    position: absolute;
    top: 50%;
    margin-top: -9px;
}
h2.label {
    border-bottom: 3px solid #5ac800;
    color: #5ac800;
    font-weight: normal;
    font-size: 16px;
    padding: 17px 15px 10px;
    line-height: 1;
    margin: 0;
}
.user-data h4 span {
    background-color: #5ac800;
    padding: 2px 12px 1px;
    display: block;
    white-space: nowrap;
    margin-bottom: -1px;
    font-size: 12px;
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    -ms-border-radius: 20px;
    -o-border-radius: 20px;
    border-radius: 20px;
}
.sidebar-setting .sidebar-menu-post::before {
    content: "p";
    color: #5ac800;
}
.sidebar-setting .sidebar-menu-replies::before {
    content: "r";
    color: #5ac800;
}
.sidebar-setting .sidebar-menu-empathies::before {
    content: "e";
    color: #5ac800;
}
.post-meta .empathy-added + .empathy::before,
.reply-meta .empathy-added + .empathy::before {
    color: #5ac800;
}
.post-meta .empathy-added + .empathy,
.reply-meta .empathy-added + .empathy {
    color: #5ac800;
}
h2.reply-label {
    clear: both;
    background: #5ac800;
    color: #FFF;
    border-top: 1px solid #4faf00;
    padding: 3px 10px 1px;
}
.feeling-selector .feeling-button.checked {
    color: #98ff42;
}
.textarea-with-menu.active-text .textarea-menu-text,
.textarea-with-menu.active-memo .textarea-menu-memo,
.textarea-with-menu.active-poll .textarea-menu-poll {
    background: -webkit-gradient(linear, left top, left bottom, from(#5ac800), to(#5ac800)) 0 0;
    background-color: rgba(0, 0, 0, 0);
}
.dialog .window-title {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    word-wrap: normal;
    height: 34px;
    padding: 0 10px;
    border-top: 1px solid #81e52e;
    border-bottom: 1px solid #5ac800;
    background: #5ac800;
    color: #fff;
    font-size: 16px;
    font-weight: bold;
    line-height: 34px;
    text-align: center;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.3);
    -webkit-border-radius: 5px 5px 0 0;
    -moz-border-radius: 5px 5px 0 0;
    -ms-border-radius: 5px 5px 0 0;
    -o-border-radius: 5px 5px 0 0;
    border-radius: 5px 5px 0 0;
}
.user-organization {
    display: block;
    font-size: 12px;
    color: #5ac800;
    font-style: italic;
}
.user-sidebar .follow-button::before {
    color: #5ac800;
    font-size: 13px;
    content: "Q";
}
#footer-inner:after {
    content: "Miiverse Theme v2.3 Made by TylerZ1184";
    text-align: center;
    color: #969696;
    font-size: 12px;
}
.user-sidebar .friend-button::before {
    color: #F58F00;
    font-size: 15px;
    content: "F";
}
.index-memo h2:not(.label),
#help .help-content h2 {
    background: #5ac800;
}
.tab2 a.selected,
.tab3 a.selected {
    color: #FFF;
    background: url("https://i.imgur.com/VvvUhDy.gif") repeat-x 0 bottom #81e52e;
    background-color: #81e52e;
    background: -webkit-gradient(linear, left top, left bottom, from(#81e52e), to(#5ac800));
    background: -webkit-linear-gradient(top, #81e52e #5ac800);
    background: -moz-linear-gradient(top, #81e52e #5ac800);
    background: -ms-linear-gradient(top, #81e52e #5ac800);
    background: -o-linear-gradient(top, #81e52e #5ac800);
    background: linear-gradient(top, #81e52e #5ac800);
}
.post-meta .link-confirm::before {
    content: "o";
    font-size: 18px;
    color: #006eff;
}
a {
    color: #006eff;
    text-decoration: none;
}
.news-list a.link {
    color: #5ac800;
}
.community-switcher .community-switcher-tab.selected {
    background: -webkit-gradient(linear, left top, left bottom, from(#81e52e), to(#5ac800));
}
#help .help-content h3 {
    clear: both;
    margin-top: 1.5em;
    border-bottom: 2px solid #5ac800;
    font-size: 14px;
    font-weight: bold;
}
.list .toggle-button .follow-button:before {
    color: #5ac800;
    content: "Q";
}
#global-menu #global-menu-logo img {
    visibility: hidden;
    width: 165px;
    height: 30px;
}
#global-menu #global-menu-logo {
    background: url('https://i.ibb.co/DYqSnK9/mlogo.png') no-repeat center;
}
.guest #global-menu-login input {
    visibility: hidden;
}
.guest #global-menu-login {
    background: url('https://i.ibb.co/f2MdDjC/mloggin.png') center no-repeat;
}
.open-spin,
.loading-spinner {
    width: 22px;
    height: 22px;
    content: url(https://i.ibb.co/s30JfMb/mloading.gif);
    animation: spin 0.0s;
}
#splatoon {
    display: none;
}
#nprogress {
    pointer-events: none;
}
#nprogress .bar {
    background: linear-gradient(45deg, #5ac800, #5ac800);
    position: fixed;
    z-index: 1031;
    top: 0;
    left: 0;
    width: 100%;
    height: 2px;
}
#nprogress .peg {
    display: block;
    position: absolute;
    right: 0px;
    width: 100px;
    height: 100%;
    box-shadow: 0 0 10px #5ac800, 0 0 4px #5ac800;
    opacity: 1.0;
    -webkit-transform: rotate(3deg) translate(0px, -4px);
    -ms-transform: rotate(3deg) translate(0px, -4px);
    transform: rotate(3deg) translate(0px, -4px);
}
.nprogress-custom-parent {
    overflow: hidden;
    position: relative;
}
.nprogress-custom-parent #nprogress .spinner,
.nprogress-custom-parent #nprogress .bar {
    position: absolute;
}
.icon-container.official:after,
.official-tag {
    margin-top: 0px;
    margin-left: 0px;
    content: url(https://i.ibb.co/PCZMfCD/mverify.png);
}
.post-permalink-feeling-icon.official:after {
    content: url(https://i.ibb.co/PCZMfCD/mverify.png);
}
.spoiler-button.checked:before {
    color: #5ac800;
}
.spoiler-button.checked {
    color: #5ac800;
}
#memo-drawboard-page button.selected {
    background: #5ac800;
}

@media screen and (max-width: 980px) {
    #global-menu li.selected a {
        border-bottom: 2px solid #5ac800;
    }
}
.list .toggle-button .follow-done-button:before {
    color: #5ac800;
    content: "v";
}
.news-community-badge {
    background: #5ac800 !important;
}
.sidebar-setting .sidebar-menu-info:before {
    content: "w";
    color: #5ac800 !important;
}
.sidebar-setting .sidebar-menu-guide:before {
    content: "g";
    color: #5ac800 !important;
}
#cookie-policy-notice .cookie-policy-notice {
    background-color: #5ac800 !important;
}
#cookie-policy-notice .cookie-policy-notice:hover {
    background-color: #61d700 !important;
}
.post-meta .yeah-added + .yeah,
.reply-meta .yeah-added + .yeah {
    color: #5ac800;
}
.post-meta .yeah-added + .yeah:before,
.reply-meta .yeah-added + .yeah:before {
    color: #5ac800;
}
.post-poll .poll-option .poll-background {
    background: #5ac800 !important;
}
#reply-content .list .my, .messages .post.my {
    background-color: #effbe7;
}
#reply-content .list > li.my:hover, .messages .post.my:hover {
    background-color: #e6f9da;
}
.post-poll .poll-votes {
    color: #5ac800 !important;
}
.post-poll .poll-option .poll-background {
    background: #5ac800 !important;
}
.post-poll .poll-votes {
    color: #5ac800 !important;
}
#wrapper {
    background: url('data:image/png;base64,');
    background-color: #f4f4f4;
}
#wrapper, .button, input, textarea, .yeah-button-text {
    font-family: helvetica,arial,meiryo,'\30e1 \30a4 \30ea \30aa ',sans-serif !important;
}
}

