css:
@-moz-document domain("app.glip.com") {
#leftrail-header {
	background: none!important;
	position: static!important;
}
.leftrail {
	background: none!important;
	position: static!important;
}
.plugintab,
.grouptab {
	background: none!important;
}
#stream_submit .group-selector,
.leftrail .grouplist {
	background: none!important;
}
.main>.ui-sortable {
	height: 100vh!important;
	position: absolute!important;
	background: #ECF1F2;
}
.rightrail {
	background: #ECF1F2;
}
.leftrail .new {
	height: 20px;
	line-height: 20px;
	top: 35%
}
.plugintab .new {
	right: -1px;
}
}

@-moz-document domain("app.glip.com") {
.ui-sortable .grouptab {
	height: 30px!important;
	background: none;
	line-height: 30px;
}
body .ui-sortable .grouptab.selected {
	height: 30px!important;
	line-height: 30px;
}
#body .plugintab.selected {
	height: auto;
	vertical-align: middle;
}
#body .plugintab {
	height: auto;
	padding: 10px 10px!important;
}
.presence {
	margin-top: 10px;
}
.plugintab label {
	font-size: 20px;
	padding: 0 10px!important;
	vertical-align: middle;
}
.plugintab label,
.plugintab.selected label {
	padding: 0px 0px!important;
}
.plugintab .folder-settings,
.plugintab .ico.plus {
	font-size: 20px;
	margin-top: 0px;
}
}

@-moz-document domain("app.glip.com") {
button:not(.start-phone):not(.rc-btn-tooltip-wrapper):not(.telephony-btn-widget),
ul.alphabet_filter>li.selectedLetter,
#body .bright-btn:not(.cancel_meeting),
#body .ui-sortable .grouptab.selected,
#body .plugintab.selected,
#body input[type=submit],
#body .compose input[type=submit],
#body menu.dropdown li.rollover:hover,
#body menu.dropdown li.hover,
#body .compose .compose_type:hover,
#body .compose .compose_type.active,
ul.alphabet_filter>li:hover,
.collapse-button:hover {
	background: #0FA6A5!important
}
.team_row:hover .ico,
.clickable:hover,
.person_row:hover .ico,
.non-tour #company_team .team_row:hover .team_name,
.non-tour #my_teams .team_row:hover .team_name,
.person_row:hover .person_name,
a.at_mention_post,
a:not(.cancel_meeting),
.mimic_a {
	color: #0FA6A5!important
}
.rightrail .sep label {
	background: none;
}
menu.dropdown,
.hint,
.compose-popup {
	border: none;
	box-shadow: rgba(228, 228, 228, 0.88) 0px 0px 30px 0px
}
}

@-moz-document domain("app.glip.com") {
a.at_mention_post[mid='21125873667'],
body[theme="rc"] a.at_mention_post[mid='21125873667'] {
	background: #fea63a
}
.presence.online,
.presence.inactive {
	background: #89B44C
}
.presence.offline {
	background: #BBC9CC
}
.collapse-button:hover {
	background:none!important;
}        
}
