css:
/* i really want this to be global */
@font-face {
  font-family: 'Roboto';
  src: local('Roboto'), local('Roboto'), /* in case the font is installed */
       url(https://fonts.googleapis.com/css?family=Roboto:400,500,700) format('woff');
}

body {
    font-family: 'Roboto', sans-serif;
    background-image: url("https://www.toptal.com/designers/subtlepatterns/patterns/memphis-colorful.png");
}

font[color="red"] > b {
    background: red;
    padding: 4px;
    color: #FFFFFF;
}

.logininfo::before {
    font-family: FontAwesome;
    content: "\f023";
    margin-right: 4px;
}
h1.headermain {
    color: #000;
    font-family: 'Roboto', sans-serif !important;
    margin: 0 0 10px 0;
    font-size: 150%;
}
h1.headermain:before {
    font-weight: 300;
    font-family: FontAwesome;
    content: "\f19c";
    margin-right: 4px;
}
div.navbar {
    padding: 13px 0px 0px 0;
    margin: 5px 0 0 0;
    clear: both;
    background-image: none;
    height: 34px;
    border: none;
}
div#header div.i3 {
    background-image: none;
    background-color: #fff;
    box-shadow: 0px 2px 18px -3px rgba(0, 0, 0, 0.4);
    border-radius: 4px;
    padding: 25px;
    margin: 15px;
    color: #000;
}
div#header div.i2 {
    background-image: none;
    padding: 0;
}
h1.headermain {
    color: #000;
    font-family: Verdana, Helvetica, Courier, sans-serif;
    margin: 0 0 10px 0;
    font-size: 150%;
}
div#header div.i1 {
    background-image: none;
    background-color: transparent;
    padding: 0;
}
div#header div.bb {
    background-image: none;
    background-repeat: no-repeat;
    background-position: 100% 100%;
    margin: 0;
}
div#header div.bb div {
    background-image: none;
    background-repeat: no-repeat;
}
div#header div.bt {
    background-image: none;
    margin: 0;
}
div#header div.bt div {
    background-image: none;
}
div#header div.i3 {
    background-image: none;
    border: none;
    padding: 25px;
    margin: 15px;
}
div.headermenu div.logininfo {
    background: rgba(50, 78, 144, 0.3);
    padding: 6px;
    border-radius: 4px;
}
a:link,
a:visited {
    color: #0b0600;
    text-decoration: none;
}
h2.headingblock {
    font-size: 18px;
    background: #efefef;
    color: #011505;
    border-radius: 5px;
    border: none;
    padding: 6px;
    text-align: center;
}
div.sideblock div.header div.i1 {
    background: none;
}
div.sideblock div.header div.bt {
    background: none;
}
div.sideblock div.bt div {
    background: none;
}
div.sideblock div.header div.bt div {
    background: none;
}
div.sideblock div.header div.i2 {
    background: none;
}
div.sideblock div.i2 {
    background: none;
}
div.sideblock div.i1 {
    background: none;
}
div.sideblock div.bb {
    background: none;
}
div.sideblock div.bb div {
    background: none;
}
div.sideblock div.content {
    padding: 12px;
}
div.sideblock div.header div.i3 {
    padding: 4px;
}
td#left-column {
    padding-top: 10px;
    width: 220px !important;
}
.wrap.wraplevel2 {
    border-radius: 6px;
    border: 1px solid #000;
    background: #24262a;
}
table#layout-table td#middle-column {
    padding: 10px;
}
table#layout-table td#middle-column {
    padding: 10px 22px 0 22px;
    max-width: 442px !important;
}
td#middle-column div.bt {
    background: none;
}
td#middle-column div.bt div {
    background: none;
}
td#middle-column div.i1 {
    background: none;
}
td#middle-column div.i2 {
    background: none;
}
td#middle-column div.i2 {
    padding: 0px;
}
td#middle-column div.i3 {
    background-color: #FFFFFF;
    padding: 10px;
    border-radius: 4px;
    width: 96%;
}
td#middle-column tr.section {
    background: none;
}
td#middle-column tr.section {
    background-position: center bottom;
    border: 1px solid #05B1F5;
}
.section.separator {
    background: #fff !important;
    border: #fff !important;
}
#course-view .hidden td.side {
    background: #05B1F5;
    color: #fff;
}
div.sideblock div.i3 {
    background: none;
    padding: 0;
    border-style: none;
}
div.sideblock div.content {
    color: #000;
    background-color: #f0f0f0;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
}
div.sideblock div.header div.i3 {
    padding: 6px;
    background: #000;
    border: none;
}
div.sideblock div.title h2 {
    font-size: 1.1em;
    line-height: 1.1em;
    color: #FFF;
}
input[id^="searchform_search"] {
    border: 2px solid #000;
    border-top-left-radius: 10px;
    border-bottom-left-radius: 10px;
    padding-left: 10px;
}
#searchform_button {
    background: #fff;
    border: 2px solid;
    width: 38px;
    height: 23px;
    border-top-right-radius: 10px;
    border-bottom-right-radius: 10px;
}
#searchform_button:hover {
    cursor: pointer;
}
td#middle-column div.bb {
    background: none;
}
td#middle-column div.bb div {
    background: none;
}
div#content div.ccbox div.i3 {
    background-color: #fff;
}
div#content div.ccbox div.i3 {
    background: #c6cfeb;
}
body#user-index #showall {
    border: 2px solid #1e66f9;
    padding: 6px;
    width: 100px;
    text-align: center;
    margin: 0px auto;
    border-radius: 2px;
}
body#user-index #showall:hover {
    background: #1e66f9;
}
.navigation select {
    background-color: #FFFFFF;
    border: 2px solid #000;
}
.tabrow0 a {
    background: none;
}
.tabrow0 a span {
    background: none;
}
.tabrow0 a:hover {
    background: none !important;
}
.tabrow0 a span:hover,
.last:hover {
    background: none !important;
}
table.generaltable th.header {
    background: #139bff;
    padding: 10px !important;
    border-top: 6px solid #0067b1;
}
table.generaltable td.cell {
    border-color: #0067B1;
}
h2.main {
    color: #0A285B;
    background: none;
    border: none;
}

body.nolayouttable div#content div.bt {
    background: none;
}

body.nolayouttable div#content div.bt div {
    background: none;
}

body.nolayouttable div#content div.i1 {
    background: none;
}

body.nolayouttable div#content div.i1 div {
    background: none;
}

body.nolayouttable div#content div.i2 {
    background: none;
}

body.nolayouttable div#content div.i2 div {
    background: none;
}

body.nolayouttable div#content div.bb {
    background: none;
}

body.nolayouttable div#content div.bb div {
    background: none;
}

input[id^="togglehide"] {
    background: white;
    border: 3px solid #139bff;
    border-radius: 30px;
}

.error {
    font-size: 1.2em;
    color: #FFFFFF;
    background: red;
    padding: 4px;
    border-radius: 2px;
}

.loginbox.clearfix.twocolumns {
    background: url("https://www.toptal.com/designers/subtlepatterns/patterns/stardust.png") !important;
    color: #FFF;
    border-radius: 8px;
}

input[type="submit"] {
    background: #FFF;
    border: 0px;
    padding: 4px;
    border-radius: 2px;
    text-align: center;
    margin: 0px auto;
}
