css:
@-moz-document domain("ruby-doc.org") {
body {
    background: #2f2f2f;
    color: #c7c7c7;
}
#header {
    background-blend-mode: multiply;
    background-color: #2f2f2f;
}
h1, h2, h3, h4, h5, h6 {
    color: #b7b7b7;
    padding: 5px 30px;
    background-color: #3a3a3a;
}

h2 a, h2 a:visited, h2 a:link {
    color: #7b7b7b!important;
}
p, address {
    font-size: 1.1em;
    margin-bottom: 17px;
}
div#documentation h1 {
    color: #ff471b;
    font-family: 'PT mono', 'Prestige Elite Std', 'PT mono', consolas;
    font-size: 2.6em;
    text-shadow: 1px 1px 6px #8a2f0d, 2px 2px 1px #821919, 3px 3px 0px #7d0000;
}
div#description h2 {
    margin-top: 100px;
    margin-bottom: 10px;
    color: #d2d2d2;
}
div.method-detail h2 {
    padding: 16px 45px 16px;
    margin-top: 6px;
}
.documentation-section div.method-detail {
    margin-top: 100px!important;
    background-color: #282828;
    padding: 5px 0px!important;
}
#description {
    font-size: 100%;
    color: #a9a9a9;
}
#description dl, #documentation dl {
    border: 8px solid #353535;
    border-width: 0 3px 3px;
}
#description dt, #documentation dt {
    background: #353535;
}
#constants-list > dl, #attributes-list > dl {
    margin: 70px 0px 0 20px;
    border: 0;
}
ul li {
    margin-left: 17px;
}
#metadata ul li {
	margin-left: 0px    
}
ul.link-list .type {
    background: #565555;
}
#files ul, #file-metadata ul {
    color: white;
    opacity: 0.8;
}
pre {
    background: #222;
    color: #74e248;
    margin-bottom: 18px;
    overflow: visible;
    font-size: 13.5px;
    line-height: 22px;
}
#constants-list > dl dd, #attributes-list > dl dd, #documentation dd.description > dl {
    color: #c7c7c7;
    border:none;
}
#constants-list > dl dd p {
    margin: 10px 0 0 0;
}
div#constants-list > dl dt a {
    color: #e42020!important;
    font-family: monospace;
    font-size: 1.6em;
    border: none;
}
div#constants-list > dl dd p:nth-child(even) {
    color: white;
    text-shadow: 0 0 3px #5a5a5a;
    font-weight: bold;
    font-family: monospace;
    font-size: 1.2em;
}
#constants-list > dl dt {
    padding: 0;
    margin: 30px 0 -5px 0;
}
pre.ruby {
    background: #212121;
}
.ruby-comment {
    color: #6ddc43;
    font-weight: normal;
    background: transparent;
}
#documentation h3.section-header {
    background-color: #424242;
    color: #efe90b;
    border: 1px solid #3c3c3c;
    margin: 140px 0 -60px;
    padding: 20px 40px;
}
#documentation .associated h3.section-header {
    margin: 0px !important;
}
#documentation .method-heading {
color: #e2d35e;
    -webkit-font-smoothing: auto;
    background: url(https://ruby-doc.org/images/brick.png) no-repeat left top;
    background-position: 0px 25px;
    margin-left: 5px;
    margin-bottom: 3px;
    opacity: 0.8;
    font-family: monospace;
    font-weight: normal;
    font-size: 1.8em;
    line-height: 1.2em;
    letter-spacing: -0.5px;
    padding-top: 20px;
    padding-bottom: 19px;
}
.method-heading + .method-heading {
    margin-top: -34px;
}
p + p {
    margin-top: -5px;
}
#documentation .method-detail:hover {
    background-color: #434343;
    color: #e8e8e8;
}
a, a:visited, a:link {
    color: #75c0f7 !important;
    border-bottom: dotted #8e8e8e 1px;
}
#project-metadata .section, #metadata .section {
    border: 1px solid #292929;
    background-color: #333;
}
#project-metadata h3.section-header, #metadata h3.section-header {
    border-bottom: 1px solid #2b2b2b;
    background: #434343;
    color: #bbb;
}
:link:hover, :visited:hover {
    border-bottom: 1px solid #b70000;
}
#rd-search-input, input[type=text] {
    background-color: #565656;
    border: 1px inset rgba(130, 129, 129, 0.47);
}
.search-field.active {
    background: #808080;
    color: black;
}
input[type=submit] {
    background-color: #565656;
    padding: 1px 7px 0px;
    border: 1px outset rgba(134, 134, 134, 0.47);
    box-shadow: 1px 1px 4px 1px rgba(0, 0, 0, 0.12);
}
#actionbar li a, #actionbar li a:visited, #actionbar li a:link {
    color: #0c0c0c ! important;
    background-color: #565656;
    padding: 2px 4px;
    border-radius: 5px 5px 0 0;
    border-color: rgba(130, 130, 130, 0.47);
    border-style: outset;
    border-width: 1px;
}
.associated {
    background-color: #333333 ! important;
}
#documentation .associated h3.section-header {
    margin-top: 0px ! important;
    background-color: #464646 ! important;
}
.ng-cm-type {
    color: #8c8c8c;
}
div.method-detail p {
    padding: 0 25px;
}
div.method-detail ul li p {
    padding: 0;
}
.toc .weblinks {
    border-color: #757575;
    background-color: #464646;
}
code {
    font-size: 1.3em;
    font-weight: bold;
    color: white;
    text-shadow: 0px 0px 3px #656565;
    padding: 0px 3px 1px;
    border-radius: 15px;
}
}
