css:
@-moz-document url-prefix("https://homebank.psecu.com/") {
body#balances  {
    background: #f4f4f4 url(https://homebank.psecu.com/images/side-bg.png) repeat-y left top;
        font-family: "Trebuchet MS";
}
#container {
	max-width:100%;
}
#fontSize, #calculator {
	display:none;
}
#memoPostWarning {
 	margin-bottom:25px;
}
    /*table*/
table.datatable {
    border:1px solid #e7e7e7;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    width:99%;
}
table.datatable td, table.datatable tr, table.datatable th {
    border-top: 1px solid #e7e7e7;
    border-bottom: 1px solid #e7e7e7;
}
table.datatable td, table.datatable tr, table.datatable th, table.datatable tr td {
	background:#fff;
}
table.datatable tr.odd td, table.datatable tfoot tr.odd th  {
	background: #f9f9f9;
}
table.datatable tr.even td, table.datatable tfoot tr.even th {
	background: #fff;
}
table.datatable th {
	color:#333;
    font-size: 14px;
}	
table.datatable td, table.datatable tr, table.datatable th {
	padding:9px 15px;
    font-size: 14px;
}
table.datatable td a, table.datatable tr a, table.datatable th a {
	color:#333;
}
SPAN.AddTab {
    border-radius: 0;
    background: #f1f1f1;
    padding: 8px 12px 7px 12px;
    border: 1px solid #e7e7e7;
    margin-top: -1px;
}

table.datatable > tbody > tr:hover > td {
	background: #9bba1210;
}
A.add-link {
    color:#333;
    BACKGROUND: url(https://homebank.psecu.com/images/datatable/add.png) no-repeat left 2px;
}
.AddBar {
	 background-color: #fff;
     border:1px solid #e7e7e7;
     width: 97.5%;
     padding:10px;
}
table.datatable td.paylink a {
    border-radius: 2px;
    background: #9ab911;
    color:#fff;
    box-shadow:none;
    border:none;
    padding: 5px 18px;
}
#btnLogOut a {
    margin-right:2em;
}
}
