css:
@-moz-document domain("twitter.com"), domain("mobile.twitter.com"), domain("www.twitter.com")
{   
.AdaptiveMedia-singlePhoto,
   .AdaptiveMedia-doublePhoto,
   .AdaptiveMedia-triplePhoto,
   .AdaptiveMedia-quadPhoto,
   .AdaptiveMedia-halfWidthPhoto,
   .AdaptiveMedia-twoThirdsWidthPhoto,
   .AdaptiveMedia-threeQuartersWidthPhoto,
   .AdaptiveMedia-halfHeightPhoto,
   .AdaptiveMedia-halfHeightPhotoContainer,
   .AdaptiveMedia-thirdHeightPhoto,
   .AdaptiveMedia-thirdHeightPhotoContainer,
   .AdaptiveMedia-photoContainer img 
{
    display: block !important;
    position: static !important;
    width: 100% !important;
    height: auto !important
}
.AdaptiveMedia 
{
    max-height: none !important;
    max-width: 100% !important;
    min-height: 100px !important
}
    .moments,
    li[data-suggestion-json*="ActivityTweet"],
    li.promoted-trend,
    li.promoted-account,
    div.promoted-tweet,
    div.flex-module.trends-container,
    div.flexmodule.trends,
    div.module.trends,
    div.module.roaming-module.wtf-module.js-wtf-module.has-content,
    div.Footer.module.roaming-module,
    div.ProfileWTFAndTrends,
    div.trends-inner,
    div.flex-module.import-prompt 
{ 
    display: none !important
}

.LiveVideoHomePageModuleContainer {
	display: none;
}

.promoted-tweet {
    display: none;
}

[data-advertiser-id],
.wtf-module.has-content,
.LiveVideoHomePageModuleContainer,
.DismissibleModule,
.Footer{
    display:none;
}

div[data-component-context="suggest_activity_tweet"] {
    display: none;
}

/* This is getting pretty crufty. TODO: cleanup. */
  .stream-item[data-item-type=tweet] .js-macaw-cards-iframe-container,
  .stream-item[data-item-type=tweet] .AdaptiveMedia,
  .stream-item[data-item-type=tweet] .QuoteMedia,
  .stream-item[data-item-type=tweet] .PlayableMedia,
  .tweet .js-macaw-cards-iframe-container,
  .tweet .AdaptiveMedia,
  .tweet .QuoteMedia {
    display: none !important;
  }

  .tweet .js-macaw-cards-iframe-container[data-card-name*="poll"] {
    display: block !important;
  }

  .permalink-tweet .js-macaw-cards-iframe-container,
  .permalink-tweet .AdaptiveMedia,
  .permalink-tweet .QuoteMedia,
  .permalink-tweet .js-media-container,
  .opened-tweet .js-macaw-cards-iframe-container,
  .opened-tweet .AdaptiveMedia,
  .opened-tweet .QuoteMedia,
  .opened-tweet .js-media-container {
    display: block !important;
  }

  .twitter-timeline-link.u-hidden,
  .twitter-timeline-link.u-hiddenVisually {
    clip: auto !important;
    position: relative !important;
    display: inline !important;
  }

  a.twitter-timeline-link.u-hidden::before,
  a.twitter-timeline-link.u-hiddenVisually::before {
    content: " ";
  }

    span.Icon.Icon--bird.Icon--large,    
    div.module.Trends.trends,
    div.RelatedUsers.module
{
    display: none !important
}

body.three-col .wrapper {
    width: 90%;
}
.content-main {
    width: 70%;
}
.dashboard-right {
    display: none;
}
.ProfileSidebar--withRightAlignment{
	display: none;
}

.PermalinkOverlay-modal{
    left: 10%;
    width: 80%;
    margin-left: 0;
}
.permalink-container{
    width: 100%;
}

.u-lg-size2of3, .u-lg-size4of6, .u-lg-size8of12{
width:100%!important;
}

.avatar
{
visibility: hidden !important;
}
}
