css:
@-moz-document domain("feedly.com"), domain("www.feedly.com")
{
/* START STYLE */
.fx .container,
.entryBody {
	width: 100% !important;
	max-width: 100% !important;
}
.headerInfo.headerInfo-article {
	margin-top: 0 !important;
}
.entryholder {
	padding-bottom: 0 !important;
}
.entryholder .fx-button.secondary.visitWebsiteButton {
	display: none !important;
}
.shareBarHolder {
	margin-top: 0 !important;
	margin-bottom: 0 !important;
	position: absolute;
	top: 33px;
	right: 33px;
}
.shareBarHolder .headerInfo {
	margin-top: 0 !important;
}
.headerInfo-expanded-img {
	display: none !important;
}
.shareBarHolder:hover .headerInfo-expanded-img,
.headerInfo-expanded-img.icon-fx-more-ios-md-black {
	display: inline-block !important;
}
.entryBody img,
.entryBody video,
.entryBody embed {
	max-width: 100% !important;
	width: auto !important;
}
#librarytab,
#searchPromo,
.button.primary.small,
.fx-button.primary.small,
#tipsModule_part,
#integrationstab_header,
#proAdModule_part,
button.small.pro.primary,
/*.modal.small, ==> This line prevents the unfollow box from being shown*/
.is-inline.modal-overlay {
	display: none !important;
}

/******************** CARD VIEW ********************/
.fx .entry.u5:nth-child(3n+1) {
	clear: none;
}
.fx .container {
	max-width: none;
}
.fx .entry.u5 {
	margin-left: 0;
	margin-right: 0;
	margin-bottom: 20px;
	margin-top: -8px;
	padding: 4px;
	width: 16.5% !important;
}
.fx .entry.u5:nth-child(3n+1),
.fx .entry.u5:nth-child(3n+2) {
	margin-right: 15px!important;
}

.fx .entry.u5 .title {
	font-size: 14px!important;
	/*line-height: 32px!important;*/
	margin: 0!important;
	padding: 5px 10px!important;
	min-height: 100px!important;
}
.fx .entry.u5 .summary {
	display: none!important;
}
.fx h4,
h4.fx,
.fx-h4 {
	font-size: 1.0rem !important;
}

/******************** MAGAZINE VIEW ********************/
.col-xs-12.col-md-12,
.col-lg-12.col-xl-9 {
	width: 100% !important;
}
.fx .entry.u4 {
	max-width: none;
	padding: 0;
}

/******************** ARTICLE VIEW ********************/
.entryBody {
	max-width: none !important;
}
.u100Entry {
	margin-bottom: 33px!important;
	max-width: 100%!important;
}
.entryHeader {
	margin: 0 -33px!important;
}
.content img {
	max-width: 100% !important;
	width: auto !important;
	height: auto !important;
	margin-left: auto !important;
	margin-right: auto !important;
}
/* END STYLE */
}
