css:
@-moz-document domain("stackoverflow.com")
{
  .container._full #content .inner-content {
        box-sizing: content-box;
        margin: 0 auto;
        padding: 15px;
        width: 98%;
    }
    #mainbar, .mainbar {
        float: left;
        width: 83%;
        margin: 0;
        padding: 0;
    }
    #answers-header {
        margin-top: 10px;
        width: 100%;
    }
    table {
        border-spacing: 0;
        border-collapse: collapse;
        width: 100%;
    }
    .post-text, .wmd-preview {
        width: 100%;
        margin-bottom: 5px;
        word-wrap: break-word;
        font-size: 15px;
        line-height: 1.3;
    }
    .post-editor {
        margin-top: 10px;
        width: 100%;
    }
    body {
        font-size: larger;
        line-height: 1.3em;
    }
    pre {
        margin-bottom: 1em;
        padding: 5px;
        width: auto;
        max-height: 600px;
        overflow: auto;
        font-family: Consolas, Menlo, Monaco, Lucida Console, Liberation Mono, DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace, sans-serif;
        font-size: 20px;
        line-height: 1.3rem;
        background-color: #eff0f1;
    }
    .post-text pre, .wmd-preview pre {
        max-width: 1495px;
    }
    .comments {
        width: 100%;
        margin-top: 10px;
        padding-bottom: 10px;
        border-top: 1px solid #e4e6e8;
    }
    #questions, #answers {
        clear: both;
        width: 100%;
    }
    .answer {
        padding-bottom: 20px;
        padding-top: 20px;
        width: 100%;
    }

#hot-network-questions, /* not related to your work */
.module.community-bulletin, /* never mind what's up on Meta */
#chat-feature, /* no wasting your time in a semi-related chatroom */
#footer-sites /* seriously, stop lookin gfor ways to waste your time */
{
  display: none;
}

/* Ads to join the site. */
#herobox, .s-hero { display: none; }

/* Ads for products. */
.ad, .ad-container { display: none; }
}
