css:
@-moz-document domain("eshoplist.gameqb.net") {
body {
    background: rgba(0, 0, 0, 0) url("https://s26.postimg.org/aahaalst5/bg-dark.png") no-repeat fixed 0 0 / cover ;
    margin: 0;
    padding: 0;
}
#title {
    background-color: #d43b34;
    border-radius: 0;
    box-shadow: 1px 0 5px rgba(0, 0, 0, 0.2);
    font-size: 18px;
    margin-bottom: 20px;
    padding: 10px 0;
    text-shadow: none;
}
#search {
    background: #fff url("https://s26.postimg.org/q7bgtib89/icon-search.png") no-repeat scroll calc(100% - 20px) 48% / auto 35%;
    border-radius: 50px;
    border-width: 0;
    box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.2) inset;
    font-size: 18px;
    padding: 10px 0;
    text-shadow: none;
    width: 500px;
    height: 50px;
    margin-bottom: 20px;
}
span.icon {
    display: none;
}
#query_select {
    color: #ccc;
    font-size: 16px;
}
#query_select > div > a {
    border: 1px solid #ccc;
    border-radius: 50px;
    color: #ccc;
    margin: 0 5px;
    padding: 0 15px;
    text-decoration: none;
    display: inline-block;
}
#query_select > div > a:hover {
    background-color: #01bbe1;
    border-color: #01bbe1;
    color: #fff;
}
#query_select > div:nth-child(-n+6), #query_select > div:nth-child(6) {
    margin-bottom: 10px;
}
#query_select > div:nth-child(3), #query_select > div:nth-child(4) {
    display: inline-block;
}
#query_select > div:nth-child(4)::before {
    content: "，";
    margin-left: -5px;
}
#game_count {
    background-color: rgba(255, 255, 255, 0.15);
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1) inset;
    border-radius: 99px;
    color: #fff;
    font-size: 24px;
    margin: 5px auto 15px;
    padding: 10px 0;
    width: 350px;
    max-width: 80%;
}
#query_select > div:nth-child(6) {
    color: #ff443b !important;
}
.container {
    margin-top: 30px;
    column-gap: 20px;
    max-width: 1170px;
}
.game_box {
    background-color: rgba(0,0,0,0.3);
    border-color: #ff443b;
    border-radius: 0;
    border-style: solid;
    border-width: 0 0 0 3px;
    box-shadow: none;
    opacity: 1;
    padding: 0 0 20px 0;
    position: relative;
    transition: .3s;
    width: 100%;
}
.game_box:hover {
    box-shadow: 2px 2px 10px rgba(0,0,0,0.2);
}
#box {
	margin-top: 12px;
    padding: 0;
}
#onsale_box {
    bottom: 10px;
    box-shadow: none;
    color: #01bbe1;
    font-size: 14px;
    padding: 0;
}
#onsale_box::before {
    background-color: #01bbe1;
    border-radius: 50px;
    color: #fff;
    content: "!";
    display: inline-block;
    height: 18px;
    line-height: 18px;
    margin-right: 5px;
    text-align: center;
    width: 18px;
}
#c_name {
    color: #fff;
    display: block;
    font-size: 20px;
    font-weight: 600;
    margin-bottom: -1.1em;
    padding: 10px 0 0;
}
#name {
    color: rgba(255, 255, 255, 0.3);
    display: block;
    font-size: 12px;
    font-style: normal;
    padding: 0 0 15px 0;
}
.game_box > * {
    justify-content: left !important;
    text-align: left !important;
}
#pricebox {
    background-color: transparent;
    border-radius: 0;
}
#pricebox > *:not(#showname) {
	padding-left: 20px;
}
#showname {
	background: rgba(1, 187, 225, 0.3);
    padding-left: 20px;
    padding-right: 20px;
}
.best_circle, .bad_circle {
    background-color: transparent;
    border-width: 0;
    height: 20px;
    width: 20px;
    margin-right: 5px;
}
#lower_box > *, #higher_box > * {
	float: left;
    text-align: left;
    font-size: 16px;
}
#price_title {
	margin-left: 0;
    margin-right: 0;
}
#lower_box {
	background-color: transparent;
    height: auto;
    margin-right: 10px;
}
#lower_contry {
    color: #fff;
    font-size: 16px;
}
#lower_contry, #higher_contry {
    width: 75px;
}
#lower_price {
    background-color: transparent;
    box-shadow: none;
    color: #ff443b;
    font-size: 16px;
    height: auto;
    text-shadow: none;
    font-weight: 600;
}
#higher_price {
    background-color: transparent;
}
#pricetip {
    font-size: 14px;
    margin: 0 3px;
    font-weight: bold;
    color: #fff;
}
#higher_box {
    background-color: transparent;
    margin-bottom: 0px;
    height: auto;
    margin-right: 10px;
}
#higher_box > * {
	color: rgba(255,255,255,0.3);
}
#higher_box::before {
    background-image: linear-gradient(to right, rgba(255,255,255,0.3) 10%, rgba(255,255,255,0) 0%);
    background-position: bottom;
    background-size: 8px 1px;
    background-repeat: repeat-x;
    content: "";
    height: 1px;
    left: 0;
    position: absolute;
    top: -6px;
    width: calc(100% - 15px);
}
hr {
    display: none;
}
#box_hr {
    background: rgba(255, 255, 255, 0.2) none repeat scroll 0 0;
    height: 20px;
    margin: 0 0 0 15px;
    width: 1px;
}
footer {
    border-width: 0;
    color: #ccc;
    font-size: 14px;
    margin: 60px 0 20px;
}
footer a {
    color: #01bbe1;
}
#guidebtn {
    background-color: #01bbe1 !important;
    border-radius: 5px 0 0 5px;
    font-size: 18px;
    padding: 15px 5px 15px 8px;
    height: auto;
    right: 0 !important;
    text-shadow: none;
    writing-mode: vertical-lr;
}
.svg-inline--fa.fa-w-16 {
    left: 3px;
    margin-bottom: 3px;
    position: relative;
}
.ui-widget-overlay {
    background: rgba(0, 0, 0, 0.6) none repeat scroll 0 0;
    opacity: 1;
}
#msg {
	left: calc(50% - 55px);
	top: calc(35%);
    margin: auto;
}

/*RWD*/
@media screen and (max-width: 768px){
    #search {width: 85%;}
    span.icon {top: 0;}
    .container {margin: 30px auto 0;}
    #lower_price, #higher_price {width: auto;}
    div#guidebtn {writing-mode: inherit; padding: 5px 15px; border-radius: 3px 3px 0 0; font-size: 14px; top: auto !important; right: 10px !important; bottom: 0;}
    .svg-inline--fa.fa-w-16 {left: 0; margin-bottom: 0; margin-right: 5px;}
}
}
