css:
@-moz-document domain("eshoplist.gameqb.net") {
body {
    background-color: #f7f7f7;
    margin: 0;
    padding: 0;
}
#title {
    background-color: #ff443b;
    border-radius: 0;
    box-shadow: 1px 0 5px rgba(0, 0, 0, 0.2);
    font-size: 18px;
    margin-bottom: 20px;
    padding: 10px 0;
    text-shadow: none;
}
#search {
    background: #fff url("https://s26.postimg.org/q7bgtib89/icon-search.png") no-repeat scroll calc(100% - 15px) 48% / auto 40%;
    border-radius: 3px;
    border-width: 0;
    box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.2) inset;
    font-size: 16px;
    padding: 10px 0;
    text-shadow: none;
    width: 500px;
    margin-bottom: 20px;
}
span.icon {
    display: none;
}
#query_select {
    color: #333;
    font-size: 16px;
}
#query_select > div > a {
    border: 1px solid #ccc;
    border-radius: 50px;
    color: #333;
    margin: 0 5px;
    padding: 0 15px;
    text-decoration: none;
    display: inline-block;
}
#query_select > div > a:hover {
	background-color: #01bbe1;
    border-color: #01bbe1;
    color: #fff;
}
#query_select > div:nth-child(-n+6), #query_select > div:nth-child(6) {
    margin-bottom: 10px;
}
#query_select > div:nth-child(3), #query_select > div:nth-child(4) {
    display: inline-block;
}
#query_select > div:nth-child(4)::before {
	content: "，";
    margin-left: -5px;
}
#game_count {
    background-color: #333;
    border-radius: 99px;
    color: #fff;
    font-size: 24px;
    margin: 5px auto 15px;
    padding: 10px 0;
    width: 350px;
    max-width: 80%;
}
#query_select > div:nth-child(6) {
    color: #ff443b !important;
}
.container {
	margin-top: 30px;
    column-gap: 20px;
    max-width: 1170px;
}
.game_box {
    background-color: #fff;
    border-color: #ff443b;
    border-radius: 5px;
    border-style: solid;
    border-width: 2px;
    box-shadow: none;
    opacity: 1;
    padding: 0 0 15px 0;
    position: relative;
    transition: .3s;
    width: 100%;
}
.game_box:hover {
    box-shadow: 2px 2px 10px rgba(0,0,0,0.2);
}
#onsale_box {
    color: #333;
    font-size: 14px;
    margin-bottom: 0;
}
#onsale_box::before {
    background-color: #333;
    border-radius: 50px;
    color: #fff;
    content: "!";
    display: inline-block;
    height: 18px;
    line-height: 18px;
    margin-right: 5px;
    text-align: center;
    width: 18px;
}
#box {
	padding: 10px 0 0 0;
}
#c_name {
    color: #fff;
    display: block;
    font-size: 20px;
    font-weight: 600;
    margin-bottom: -1.1em;
    padding: 10px 0 0;
    background-color: #ff443b;
    text-align: center !important;
}
#name {
    color: rgba(255, 255, 255, 0.6);
    background-color: #ff443b;
    display: block;
    font-size: 12px;
    font-style: normal;
    padding: 0 0 13px 0;
    text-align: center !important;
}
.best_circle, .bad_circle {
    background-color: transparent;
    border-width: 0;
    height: 20px;
    width: 20px;
    margin-right: 5px;
}
#box_hr {
	width: 1px;
    background-color: rgba(255,255,255,0.5);
    height: 30px;
}
#higher_box #box_hr {
	background-color: rgba(0,0,0,0.1);
}
#price_title, #higher_contry {
	font-size: 16px;
}
#lower_box {
	background: #01bbe1;
    width: calc(90% - 40px);
    margin: 0 auto;
    padding: 10px 20px;
    border-radius: 40px;
    height: 35px;
}
#lower_contry {
    color: #fff;
    font-size: 16px;
}
#lower_contry, #higher_contry {
    width: 75px;
}
#lower_price {
    background-color: transparent;
    box-shadow: none;
    color: #fff;
    font-size: 16px;
    height: auto;
    text-shadow: none;
    font-weight: 600;
    width: auto;
    margin-right: 10px;
}

#higher_price {
    font-size: 16px;
    margin-right: 10px;
    width: auto;
    background-color: transparent;
}
#pricetip {
    font-size: 14px;
    font-weight: bold;
    margin: 0 2px;
}
#higher_box {
    filter: grayscale(100%);
    margin: 0 auto;
    width: calc(90% - 40px);
    padding: 10px 20px;
    background-color: #efefef;
    border-radius: 50px;
    height: 35px;
}
#higher_box #price_title, #higher_contry, #higher_price {
	color: #999;
}
hr {
    display: none;
}
#area_box {
	margin-top: 15px;
}
footer {
    border-width: 0;
    color: #333;
    font-size: 14px;
    margin: 60px 0 20px;
}
footer a {
    color: #01bbe1;
}
#guidebtn {
    background-color: #01bbe1 !important;
    border-radius: 5px 0 0 5px;
    font-size: 18px;
    padding: 15px 5px 15px 8px;
    right: 0 !important;
    text-shadow: none;
    writing-mode: vertical-lr;
    height: auto;
}
.svg-inline--fa.fa-w-16 {
    left: 3px;
    margin-bottom: 3px;
    position: relative;
}
.ui-widget-overlay {
    background: rgba(0, 0, 0, 0.6) none repeat scroll 0 0;
    opacity: 1;
}
#msg {
	left: calc(50% - 55px);
	top: calc(35%);
    margin: auto;
}

/*RWD*/
@media screen and (max-width: 768px){
    #search {width: 85%;}
    span.icon {top: 0;}
    .container {margin: 30px auto 0;}
    div#guidebtn {writing-mode: inherit; padding: 5px 15px; border-radius: 3px 3px 0 0; font-size: 14px; top: auto !important; right: 10px !important; bottom: 0; box-shadow: 0 0 0 1px rgba(255, 255, 255, 0.5);}
    .svg-inline--fa.fa-w-16 {left: 0; margin-bottom: 0; margin-right: 5px;}
}	
}
