css:
@-moz-document domain("scrooge.co.uk"), domain("skroutz.gr"), domain("alve.com") {
header,
body
{
	background-color: black;
}

/*User Page*/
	.user-info,
	.avatar-box,
	.account-menu,
	#null
	{
		filter: invert(1) !important;
	}

/*Categories*/
	#categories_show ol.paginator,
	#categories_show .cross-categories,
	#categories_show #react-posts-bottom,
	#categories_show .guide-cover,
	#categories_show .best-picks-clear-winner,
	#categories_show li.card,
	#categories_show li.card .pic,
	#categories_show .sku .pic,
	#categories_show .page-title,
	#null
	{
		filter: invert(1) !important;
	}
	
	/*Fashion*/
	section.main-sidebar.js-main-sidebar,
	ul.color-filter li,
	#null
	{
		filter: invert(1) !important;
	}

/*Product pages*/
	#skus_show .scrollable,
	#skus_show #modal-container,
	#skus_show img,
	#skus_show .media-img,
	#null
	{
		filter: invert(1) !important;
	}

/*Search Results*/
	#search_search main.content,
	#search_search main .pic,
	#null
	{
		filter: invert(1) !important;
	}

	#manufacturer_show main.content,
	#manufacturer_show main .pic,
	#null
	{
		filter: invert(1) !important;
	}
	

/*Main page*/
	#categories_index #history,
	#categories_index #history .pic,
	#categories_index #popular,
	#null
	{
		filter: invert(1) !important;
	}

/*Categories*/
	#categories_subcats .pop-manufacturers,
	#categories_subcats .minisku-section > h2.title,
	#categories_subcats li.card,
	#categories_subcats a.pic,
	#categories_subcats .page-title,
	#null
	{
		filter: invert(1) !important;
	}
	
/*Global*/
	/*search suggestions is obstructed*/
	div #suggestions_container,
	#null
	{
		z-index: 99999999 !important;
	}

	.notification,
	.lsu,
	footer,
	#null
	{
		filter: invert(1) !important;
	}
}
