css:
@-moz-document domain(v3g.ladypopular.ru), domain(socapp.castlot.ru), domain(v3gfacebook.ladypopular.com), domain(v3.g.ladypopular.com), domain(v3.g.ladypopular.bg)
{
.new-popup {
    top: 5%;
}

ul.members {
    list-style: decimal;
    font-size: 10px;
}

.guild-page li.guild-member {
    padding: 5px 1px;
        margin-left: 16px !important;
}


body #pageWrapper #content .wrapper, .wrapper.wide {
    padding: 30px 0;
}

.guild-show {
    padding: 60px 0;
}


#profilePage .rank-top-control {
    margin-top: -20px !important;
}


 #infoBar:before {
    background: url(http:) transperent no-repeat;
    color: #fff;
    display: block;
    position: absolute;
    top: 0px;
    left: -168px;
    width: 170px;
    height: 90px;
    z-index: 2;
    padding: 5px;
    white-space:pre;
}


.chat-wraper textarea, .chat-wraper input[type="text"] {
    height: 70px;
    max-height: 100px;
    min-height: 50px;
}




body, #navbar, body #pageWrapper #content.duel-search, body #pageWrapper #content.duel-html,  #floor, #floorLight, body #pageWrapper #content, #header {
    background: url(http:) #000 repeat-x !important;
}


.mall-stores, .page-background, .centerContent, .rank-top-control, .guild-page, .boxHolder, .mall-wraper, .jobs,  #mainNav, .fp-wraper, #infoBar {
filter: brightness(80%);
}

.play-video {
    display: block !important;
}

.bfMirror-frame, .ladyMirror-frame {
    background: url(http:) no-repeat;
    height: 295px;
}


.ladyMirror-holder #bodyWrapper, .bfMirror-holder #bodyWrapper {
    left: -170px;
    top: -80px;
}

.ladyMirrorWrapper, .bfMirrorWrapper {
    width: 208px;
    height: 285px;
    left: 0px;
    top: 25px;
    background-color: #666 !important;
}
}
