/* ==UserStyle==
@name           Wikipedia Dark Cyan (like Cyanogenmod)
@namespace      adventuretc
@version        3.0.0
@description    A dark version of Wikipedia.org
@author         Cruto
==/UserStyle== */


/*

darkest background color (used on the body):
	background-color: #191919!important;
 
less dark background color and border for that (used on the main text):
	background-color: rgba(100, 100, 100, 0.2)!important;
	border-color: rgba(220, 220, 220, 0.2)!important;

light marker color 1 (infobox):
	background-color: rgba(220, 220, 220, 0.1)!important;


cyan (used for links)
	color: #32a9da!important;

activated cyan (links)
	color: #6ad3ff!important;

cyan tint 1 (navigation box):
	color: #2B657D!important;

cyan tint 2 (navigation box):
	color: #25586E!important;

cyan tint 3 (navigation box):
	color: #284C5C!important;



 */
 
@-moz-document domain("wikipedia.org")
{
    #p-logo > a
    {
        /*  background-image: url(http://i.imgur.com/vSvAdyp.png)!important;
    */
        background-image: none !important;
    }
}

@-moz-document domain("wikimedia.org")
{
    #p-logo > a
    {
        /* background-image: url(http://img2.wikia.nocookie.net/__cb20111127203041/fairytail/es/images/c/c9/Wikimedia_Commons.png)!important;
        background-size: contain!important;
    */
    }
}

@-moz-document domain("wikipedia.org"),
domain("wikimedia.org"),
domain("wikimediafoundation.org")
{
    body
    {
        background-color: #191919!important;
        background-image: none!important;
        background-attachment: fixed!important;
        background-repeat: repeat-x!important;
    }

    a
    {
        color: #32a9da!important;
    }

    a:hover
    {
        color: #6ad3ff!important;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    caption
    {
        color: rgba(220, 220, 220, 1)!important;
    }

    #mw-head-base,
    #mw-page-base,
    #mw-head,
    #mw-panel
    {
        background-color: rgba(20, 20, 20, 0)!important;
        background-image: none!important;
    }

    #content.mw-body
    {
        background-color: rgba(100, 100, 100, 0.2)!important;
        border-color: rgba(220, 220, 220, 0.2)!important;
    }

    *
    {
        border-color: rgba(220, 220, 220, 0.2)!important;
    }
    
    p,
    dd,
    .sisterproject
    {
        color: rgba(220, 220, 220, 0.8)!important;
    }

    .hauptartikel-pfeil,
    i,
    strong
    {
        color: #fff!important;
    }

    .settings-menu-items .menu-section:hover
    {
        background-color: rgba(220, 220, 220, 0.05)!important;
        color: rgba(220, 220, 220, 0.8)!important;
    }

    .settings-menu-items .menu-section
    {
        color: rgba(220, 220, 220, 0.6)!important;
    }

    .uls-input-settings .uls-input-settings-inputmethods-list
    {
        background-color: rgba(220, 220, 220, 0.1)!important;
    }

    /*td > h2,
    td > div > h2,
    dt
    {
        color: rgba(220, 220, 220, 0.6)!important;
        background-color: rgba(220, 220, 220, 0.01)!important;
    }
    */
    
    li /* needed for reference tooltips */
    {
		background-color: rgba(220, 220, 220, 0.0)!important;
	}
    
	li,
    td,
    tr,
    th,
    td > h2,
    td > div > h2,
    dt
    {
        color: rgba(220, 220, 220, 0.6)!important;
        background-color: rgba(220, 220, 220, 0.0)!important;
    }
    
    
	tr > th
    {
        background-color: rgba(220, 220, 220, 0.1)!important;
    }

    ul
    {
        list-style-image: none!important;
    }

    dt
    {
        color: rgba(220, 220, 220, 0.8)!important;
        background-color: rgba(220, 220, 220, 0.01)!important;
        text-shadow: 0px 2px 0px #111!important;
    }

    .rellink
    {
        color: #daef8f!important;
        background-color: rgba(220, 220, 220, 0.01)!important;
    }

    code,
    .mw-geshi,
    pre,
    .catlinks,
    .NavContent > p
    {
        color: rgba(220, 220, 220, 0.6)!important;
        background-color: rgba(100, 200, 255, 0.1)!important;
    }

    #hauptseite .inhalt,
    .infobox
    {
        background-color: rgba(220, 220, 220, 0.1)!important;
    }

    .toptextcells > tbody tr td div
    {
        background-color: transparent!important;
    }

    .infobox > div,
    div.flaggedrevs_short
    {
        background-color: #444!important;
        color: #eee!important;
    }

    #mw-fr-revisiondetails
    {
        background-color: rgba(20, 20, 20, 0.9)!important;
    }

    #mw-panel.collapsible-nav .portal
    {
        background-image: none!important;
    }

    [id^=\"wpTextbox\"]{
 background-color: rgba(20,
    20,
    20,
    1.0)!important;
    color: #ddd!important;
}

div.thumbinner,
div.thumbinner > a > img,
#toc,
.toccolours,
#catlinks,
div[id^=\"mw-Anonymous\"],
 #filetoc
{
    background-color: rgba(20, 20, 20, 0.3)!important;
    border-color: rgba(220, 220, 220, 0.1)!important;
}

#ChangeToU2014_v1_anononly
{
    border-color: rgba(220, 220, 220, 0.1)!important;
    background: rgba(20, 20, 20, 0.3)!important;
}

#ChangeToU2014_v1_anononly *,
div[id^=\"mw-Anonymous\"],
 #stockphoto_base > span > span:last-of-type
{
    color: #aaa!important;
}

div.vectorMenu h3 a
{
    background: none!important;
}

div.thumbinner > .thumbcaption,
#siteSub
{
    color: rgba(220, 220, 220, 0.5)!important;
}

.hintergrundfarbe5,
table
{
    background-color: rgba(220, 220, 220, 0.05)!important;
}

div#simpleSearch
{
    background-color: rgba(220, 220, 220, 0.05)!important;
    background-image: none!important;
}

#searchInput
{
    color: #eee!important;
}

.imeselector-menu,
div.vectorMenu ul,
#language-settings-dialog,
.grid.uls-menu.uls-wide.callout
{
    background-color: rgba(20, 20, 20, 0.95)!important;
}

#languagesettings-panels .menu-section.active,
#languagesettings-panels .menu-section.active:hover,
.uls-language-settings-close-block
{
    background-color: rgba(220, 220, 220, 0.15)!important;
    color: #eee!important;
}

.uls-menu .search,
.uls-menu .search > .ten.columns > #search-input-block > #languagefilter,
.uls-menu .search > .ten.columns > #search-input-block > #filtersuggestion,
#uls-no-found-more
{
    background: rgba(220, 220, 220, 0.05)!important;
    color: #eee!important;
}

.settings-text
{
    color: #777!important;
}

#languagesettings-settings-panel
{
    background-color: rgba(220, 220, 220, 0.05)!important;
}

.imeselector a,
.ime-disable
{
    color: rgba(220, 220, 220, 0.5)!important;
}

div.vectorTabs,
div.vectorTabs ul,
div.vectorTabs ul li,
div.vectorTabs ul li span
{
    background-image: none!important;
    background-color: transparent!important;
    border: none!important;
}

div.vectorTabs ul li
{
    box-shadow: inset 0px -30px 25px -35px rgba(160, 160, 160, 0.5)!important;
    margin-left: -1px!important;
    transition: 0.4s!important;
}

div.vectorTabs ul li:hover
{
    box-shadow: inset 0px -25px 25px -35px rgba(255, 255, 255, 1)!important;
    margin-left: -1px!important;
    transition: 0.4s!important;
}

.wikitable.float-right > tbody > tr > th a
{
    color: #999!important;
    text-decoration: underline!important;
}

.wikitable.float-right > tbody > tr > th a:hover
{
    color: #eee!important;
    text-decoration: underline!important;
}

div.NavHead,
table.wikitable > tr > th,
table.wikitable > * > tr > th,
/*
table.wikitable > * > tr:last-of-type > td,
*/
#mw-content-text > p > span
{
    background: rgba(255, 255, 255, 0.1)!important;
}

.body > select
{
    background-color: #222!important;
    padding-top: 1px!Important;
    height: 22px!Important;
    color: #aaa!important;
    width: 130px!Important;
    border: none!important;
    box-shadow: inset 0px 15px 10px -10px #303030, inset 0px 1px 1px #777!important;
    margin: 5px 0px 5px 0px!important;
    border-radius: 20px!important;
    transition: 0.4s!important;
}

input[type=\"button\"]{
 background-color: #222!important;
height: 23px!Important;
color: #aaa!important;
width: 130px!Important;
border: none!important;
box-shadow: inset 0px 15px 10px -10px #303030,
inset 0px 1px 1px #777!important;
margin: 5px 0px 5px 0px!important;
border-radius: 20px!important;
transition: 0.4s!important;

}

input[type=\"button\"]:hover{
 background-color: #303030!important;
height: 23px!Important;
color: #aaa!important;
width: 130px!Important;
border: none!important;
box-shadow: inset 0px 15px 10px -10px #404040,
inset 0px 1px 1px #888!important;
margin: 5px 0px 5px 0px!important;
border-radius: 20px!important;
transition: 0.4s!important;

}

.tex
{
    background-color: #eee!important;
    padding: 10px!important;
    border-radius: 5px!important;
}

.bookshelf-container .bookend
{
    background-image: none!important;
}

.search-container.language-search > form > label
{
    color: #eee!important;
}

.divider
{
    opacity: 0.4!important;
}

fieldset
{
    background-color: rgba(200,
    200,
    200,
    0.2)!important;
}

.central-featured-lang > .link-box > em
{
    color: #888!important;
}

.central-featured-lang > .link-box > small
{
    color: #666!important;
}

h1.central-textlogo > img
{
    visibility: hidden!important;
}

h1.central-textlogo
{
    background-image: none!important;
    background-repeat: no-repeat!important;
    background-position: center!important;
}

#wpTextbox1
{
    background-color: #000 !important;
}

.mw-highlight
{
    background-color: #000 !important;
}


.hatnote
{
    color: rgba(220, 220, 220, 0.5)!important;
}

.suggestions,
.suggestions-results,
.suggestions-result,
.mw-searchSuggest-link
{
	color: rgba(220, 220, 220, 0.8)!important;
	background-color: #191919!important;
}

/* math images are black on transparent */
.mwe-math-fallback-image-inline
{
	filter:invert(75%);
}

.referencetooltip > li /* reference tooltips background color */
{
	background-color: #191919!important;
}

.navbox
{
	background-color: #191919!important;
}

.navbox {
	border:1px solid rgba(220, 220, 220, 0.3)!important;
}
.navbox,.navbox-subgroup {
	background: rgba(220, 220, 220, 0.0)!important;
}
.navbox-list {
	border-color: rgba(220, 220, 220, 0.0)!important;
}
tr + tr > .navbox-abovebelow,tr + tr > .navbox-group,tr + tr > .navbox-image,tr + tr > .navbox-list {
	border-top:2px solid rgba(20, 20, 20, 0.5)!important;
}
.navbox th,.navbox-title {
	background-color: rgba(220, 220, 220, 0.2)!important;
	
	/* make navbox title more noticable */
	color: rgba(220, 220, 220, 1)!important;
}
.navbox-abovebelow,th.navbox-group,.navbox-subgroup .navbox-title {
	background-color: rgba(220, 220, 220, 0.1)!important;
}
.navbox-subgroup .navbox-group,.navbox-subgroup .navbox-abovebelow {
	/*background-color: rgba(220, 220, 220, 0.05)!important;*/
}
.navbox-even {
	background: rgba(220, 220, 220, 0.05)!important;
}
.navbox-odd {
	/*background:transparent*/
}
/* TODO
 * 
 * clicked citation background color
 * 
 * a keresési elemeknél nincs kiemelés, stb.
 * 
 * a szerkesztő oldalon fekete alapon fekete szöveg van
 * 
 */

.mw-mmv-ttf-normal,
.mw-mmv-post-image,
.mw-mmv-ttf-ellipsis-container,
.mw-mmv-image-metadata,
.mw-mmv-untruncated
{
	background-color: #191919!important;
}


/* remove white background image */
#p-cactions-label
{
	background: none !important;
}



/* the search type selector is the wrong color */
.mw-search-profile-tabs
{
	background-color: rgba(220, 220, 220, 0.2)!important;
}


}
