css:
@-moz-document url-prefix("https://searx.laquadrature.net/"), url-prefix("https://s3arch.eu/"), url-prefix("https://searx.de/"), url-prefix("https://searx.info/"), url-prefix("https://searx.me/"), url-prefix("https://search.koehn.com/"), url-prefix("https://searx.brihx.fr/"), url-prefix("https://searx.oe5tpo.com/"), url-prefix("https://search.homecomputing.fr/"), url-prefix("https://searx.potato.hu/"), url-prefix("https://search.jpope.org/"), url-prefix("https://searx.aquilenet.fr/"), url-prefix("https://search.azkware.net/"), url-prefix("https://searx.netzspielplatz.de/"), url-prefix("https://searx.nulltime.net/"), url-prefix("https://suche.elaon.de/"), url-prefix("https://www.heraut.eu/searx/"), url-prefix("https://citizen-cam.de/"), url-prefix("https://searx.at/"), url-prefix("https://searx.info/"), url-prefix("https://searx.drakonix.net/"), url-prefix("https://search.alecpap.com/"), url-prefix("http://searx.techregion.de/"), url-prefix("https://search.st8.at/"), url-prefix("https://search.paviro.de/") {
div.container:nth-child(2) {
	color: black;
	background: white;
	margin-left: 7cm;
	margin-right: 7cm;
	margin-top: 20px;
	box-shadow: rgba(0, 0, 0, 0.19) 0px 2px 3px 0px, rgba(0, 0, 0, 0.12) 0px 2px 10px 0px;
	border: solid grey 1px;
}
body {
	padding-top: 5px;
	font-size: 16px;
	font-family: Roboto;
	background-image: linear-gradient(#263238, #6B79A0);
}
a:link {
	color: #444140;
}
a:hover {
	color: #01D7D4;
}
#search_form {
	padding-top: 20px;
}
.searx-navbar {
	background: #263238;
	color: #263238;
	height: 50px;
	font-size: 20px;
	padding-top: 20px;
	box-shadow: rgba(0, 0, 0, 0.19) 0px 2px 3px 0px, rgba(0, 0, 0, 0.12) 0px 2px 10px 0px;
}
body > div.searx-navbar > span.pull-right > a:nth-child(1),
body > div.searx-navbar > span.pull-right > a:nth-child(2) {
	color: #01D7D4;
}
.external-link {
	color: #5294E2;
}
.text-muted > small:nth-child(1) {
	color: #2A2A2E;
}
.text-muted > small:nth-child(1) > a:nth-child(1) {
	color: white;
}
.btn.btn-default.btn-xs {
	background: #6B79A0;
	text-decoration: none;
}
.footer {
	padding-right: 5cm;
}
.result.result-default {
	background: rgba(106, 119, 154, 0.045);
	text-shadow: 1px 1px 2px #BFBFBF;
}
}
