css:
@-moz-document url-prefix("https://www.busuu.com"), domain("busuu.com") {
.main-viewport__area{
	background-color: #202020 !important;
    color: white !important;
}
nav, .nav-subtop, .nav-top, .nav-lesson, .nav-top__dropdown-footer{
    background-color: #606060 !important;
    color: #f4f7f9 !important;
}
.nav-top__dropdown-footer :hover{
    color: #38a9f6 !important;
}
.nav-subtop__item{
    color: white !important;
}
.nav-subtop__item :hover{
	color: #38a9f6 !important;   
}
.course-lesson__head{
    background-color: #404040;
    color: white;
}
.course-lesson__body{
    background-color: #606060;
}
.spechar_key{
    color: white;
    background-color: #404040;
}
.unit-bar__time-estimate{
    color: black;
}

.ex-dialogue-sentence__content{
    color: white;
    background-color: #404040;
}
.ex-dialogue__drag-list, .nav-subtop{
    background-color: #606060 !important;
    border-top-color: #7fc861 !important;
}
.fg-bs-black{
 	color: white;   
}
.ex-asset__audio-wrapper{
    color: white;
    background-color: #404040;
}
footer, .footer-main__link{
    background-color: #606060 !important;
	color: white !important;
}
}
