css:
@-moz-document domain("rbxtrading.com") {
html,
  body {
     /* set main bg color behind image*/
    background: #ff6a00;
    /* bg imagee */
    background-image: url(https://www.pixelstalk.net/wp-content/uploads/2016/08/Colorful-Random-Background.jpg);
    /* prevents the bg from scrolling, don't change */
    background-attachment: fixed;
    /* just place the bg image, no repeat, it takes up the space it can */
    background-repeat: no-repeat;
    /* where are we putting the bg image */
    background-position: center;
    /* main text color */
    color: #a928ff;
}
  
.topic-body .cooked {
    /* set the fonts to be used throughout the board, leave it */
    font-family: "Helvetica", "Arial", "Bitstream Vera Sans", "Verdana", sans-serif;
}
    
header.d-header.clearfix {
    /* set a basic color for the header */
    background-color: rgba(20, 188, 183, 0.9);
}

/*ignore this section*/
h2#site-text-logo.text-logo {
    letter-spacing:5px;
    color: rgba(255, 106, 0, 0);
    background-image: url(http://i.imgur.com/Gz0Is5X.png);
    background-size: 750px;
    padding-right: 700px;
    background-repeat: no-repeat;
}
  
i.fa.fa-search {
    /* set the color for the magnifying glass/search button */
    color: #372dfc;
}
  
i.fa.fa-bars {
    /* set the color for the hamburger menu icon */
    color: #372dfc;
}
  
div#main-outlet.wrap {
    /* set a background color behind the "main middle area", 4th number is transparency, 0 for transparent, 1 for solid. */
    background-color: rgba(55, 45, 252, 0.7);
}
  
img#site-logo.logo-small {
    /* when in a thread, brighten up the butterfly logo to be about the main text color #AAA. Change value to 1 to see normal logo */
    -webkit-filter: brightness(3);
}
  
a.widget-link.topic-link {
    /* set the color for the thread title when in a thread */
    color: #c2bfff;
}
  
.extra-info-wrapper .badge-wrapper.bullet span.badge-category {
    /* in a thread, set the color for the category you are in at the top */
    color: #c2bfff !important;
}
  
p {
    /* set post text color */
    color: #c6dcff
  }
}
