@-moz-document domain(mafreebox.freebox.fr) {
:root {
    --red: /*[[primary]]*/;
    --dark-red: /*[[primarydark]]*/;
    --dark-red-gradient: /*[[gradient]]*/;
}
body {
    animation-duration: 2.5s;
    animation-name: pulse;
    animation-iteration-count: infinite;
    animation-direction: alternate;
}

@keyframes pulse {
    from {
        background-image: -webkit-gradient(radial, center top, 0, center top, 700, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 700, #000 100%);
    }
    10% {
        background-image: -webkit-gradient(radial, center top, 0, center top, 701, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 701, #000 100%);
    }
    20% {
        background-image: -webkit-gradient(radial, center top, 0, center top, 702, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 702, #000 100%);
    }
    30% {
        background-image: -webkit-gradient(radial, center top, 0, center top, 703, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 703, #000 100%);
    }
    40% {
        background-image: -webkit-gradient(radial, center top, 0, center top, 704, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 704, #000 100%);
    }
    50% {
        background-image: -webkit-gradient(radial, center top, 0, center top, 705, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 705, #000 100%);
    }
    60% {
        background-image: -webkit-gradient(radial, center top, 0, center top, 706, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 706, #000 100%);
    }
    70% {
        background-image: -webkit-gradient(radial, center top, 0, center top, 707, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 707, #000 100%);
