    90% {
        background-image: -webkit-gradient(radial, center top, 0, center top, 709, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 709, #000 100%);
    }

    to {
        background-image: -webkit-gradient(radial, center top, 0, center top, 710, from(#c5c5c560), to(#0006)), -webkit-gradient(linear, center top, center bottom, from(#101010), to(#000));
        background-image: -moz-radial-gradient(center top, #454545 710, #000 100%);
    }
}

