css:
@-moz-document domain('dict.cc'){
#maincontent > form > table {
    width:100%;
}

#maincontent > form > table td:first-child {
    width:224px;
    position: relative;
	z-index: 10;
}


input#sinp{
	width: 100% !important;
    max-width:450px;
    margin-right: -65px;
    padding-right: 65px !important;
    box-sizing: border-box;
}

@media screen and (max-width: 1200px) {
    
#maincontent, .mc{
	margin-left: 0px !important;
    box-sizing: border-box;
    width: 100%;
    padding: 0px 10px 5px 0px
}

#inotherlangs{
    width: auto !important;
    top: auto !important;
    left: auto !important;
    height: 40px;
    margin-top: -40px;
    background-color: #fff;
    overflow: hidden;
    border-bottom: 4px #ddd solid;
}

#inotherlangs:hover{
    height: auto;
    overflow: hidden;
}

#inotherlangs br{
	display: none;
}
#inotherlangs a{
	display: inline-block;
    margin: 0px 5px;
}


#shst{
    position: static !important;
}

.orangebar, .subnav, .bluebar, .subblue {
	width: inherit;
    height: inherit;
}

.organgebar > div{
    margin-bottom: 50px;
	margin-left: 20px;
}
    
#maincontent > table {
    max-width: 100% !important;
    table-layout: initial !important;
    padding-left: 20px;
    
}

#maincontent > *, #maincontent > form > table {
    margin: auto !important;
}

.td3cml, .td3cmr, .td4cml, .td4cmr, .td7cml, .td7cmr {
    background-image: none !important;
}

.subnav{
border-top: 45px solid #fff !important;
}

#adrig{
	left: -140px !important;
    top: 250px !important;
    background-color: #bbb;
}
#adrig iframe{
	left: auto;
    top: auto;
    display: inherit;
}
    
#adrig:hover{
	left: 0px !important;
}
    
.imgslide{
	top: 30px !important;
}
    
.td2{
	padding:3px !important;
}
 
.td2 img{
    max-width: 100%;
    min-width: 15px;
}
.td3cml img, .td3cmr img, .td4cml img, .td4cmr img, .td7cml img, .td7cmr img {
    max-width: 40% !important;
}
}
}
