css:
@-moz-document domain("capitalism.party") {
body,
body.app-body,
.drawer__inner__mastodon,
.ui,
.static-content {
	background-color: #1c1c1c !important;
    background-image: none;
    color: #fefefe;
}

.column-back-button,
.detailed-status,
.detailed-status__action-bar,
.column-link,
.column > .scrollable,
.column-header,
.column-header__button,
.column-header__button.active:hover,
.drawer__header,
.drawer__inner {
	background-color: #242424;   
    color: #fefefe;
}

.column-subheading {
 	background-color: #232323;   
}

.text-icon-button.active,
.button,
.compose-form__buttons,
.compose-form .compose-form__buttons-wrapper {
	background-color: #3a3a3a;   
    color: #c3c3c3;
}

.column-header__button.active,
.column-header__collapsible-inner,
.search__input {
 	background-color: #3a3a3a;   
}

.autosuggest-account .display-name__account,
.autosuggest-textarea__suggestions,
.autosuggest-textarea__suggestions__item.selected,
.spoiler-input__input,
.autosuggest-textarea__textarea,
.compose-form .autosuggest-textarea__textarea,
.compose-form .compose-form__uploads-wrapper,
.compose-form__modifiers,
.search__input:focus {
 	background-color: #4a4a4a;   
    color: #fefefe;
}

.button:hover {
	background-color: #3f3f3f;
}

.notification__message .fa,
.navigation-bar__profile-edit,
.setting-text,
.column-header__setting-btn,
.column-settings__section,
.setting-toggle__label {
	color: #c1c1c1;   
}

.column-header__setting-btn:hover {
    color: #212121;
}

.react-toggle--checked:not(.react-toggle--disabled) .react-toggle-track {
	background-color: #515151;
}

.react-toggle--checked:hover:not(.react-toggle--disabled) .react-toggle-track {
	background-color: #212121;
}

.react-toggle--checked .react-toggle-thumb {
 	border: none;   
}

.privacy-dropdown__option.active:hover,
.privacy-dropdown__option.active,
.privacy-dropdown__option:hover,
.privacy-dropdown.active .privacy-dropdown__value.active {
 	background-color: #1c1c1c;   
}

.icon-button {
	color: #313131;
}

.icon-button:hover {
	color: #414141;
}

.account .account__display-name,
.status__relative-time,
.notification__message,
.status__display-name,
.muted .status__display-name strong,
.muted .status__content p {
 	color: #919191;   
}
}
