css:
@-moz-document url("https://web.whatsapp.com/") {
:root {
    /* Discord colors */
    --background:          #202225;
    --background-text:     #706E75;
    --main:                #37393F;
    --main-text:           #D7D5D4;
    --main-text-dim:       #73767B;
    --main-highlight:      #33353B;
    --main-secondary:      #41444B;
    --main-secondary-text: #63767D;
    --main-text-icon:      #B9BBBE;
    --sidebar:             #2F3136;
    --sidebar-text:        #8F9297;
    --sidebar-hover:       #34363C;
    --white:               #F3F3F3;
    --black:               #18191C;
    --discord-icon:        #4EB681;
    --link:                #3BA3ED;
    --border-radius:         3px;

    /* Overwrite Whatsapp variables */
    --panel-background: var(--sidebar);
    --panel-background-lighter: var(--sidebar);
    --panel-background-deeper: var(--background);
    --panel-header-background: var(--main);
    --primary: var(--background-text);
    --primary-strong: var(--white);
    --primary-stronger: var(--main-text);
    --secondary: var(--background-text);
    --secondary-lighter: var(--sidebar-text);
    --secondary-stronger: var(--sidebar-text);
    --icon: var(--background-text);
    --teal: var(--discord-icon);
    --background-default: var(--sidebar);
    --background-default-active: var(--main);
    --background-default-hover: var(--main-secondary);
    --border-chatlist: transparent;
    --icon-lighter: var(--sidebar-text);
    --unread-marker-background: var(--discord-icon);
    --icon-search-back: var(--discord-icon);
    --border-stronger: var(--background);
    --conversation-panel-background: var(--main);
    --outgoing-background: var(--main-secondary);
    --outgoing-background-deeper: var(--main-secondary);
    --incoming-background: var(--main-secondary);
    --incoming-background-deeper: var(--main-secondary);
    --rich-text-panel-background: var(--sidebar);
    --compose-background: var(--main-secondary);
    --compose-panel-background: var(--main);
    --input-placeholder: var(--main-secondary-text);
    --border-panel: transparent;
    --overlay-rgb: transparent;
    --incoming-background-rgb: transparent;
    --outgoing-background-rgb: transparent;
    --dropdown-background: var(--main);
    --system-message-background: transparent;
    --intro-background: var(--main);
    --panel-background-deep: var(--background);
    --panel-background-colored: var(--background);
    --button-round-background: var(--main-secondary-text);
    --button-alternative: var(--discord-icon);
    --inverse: var(--main);
    --media-viewer-button-background: var(--main);
    --media-viewer-button-icon: var(--main-text);
    --media-viewer-background: var(--sidebar);
    --icon-strong: var(--sidebar-text);
    --gray-150: var(--discord-icon);
    --app-background-stripe: var(--background);
    --border-list: transparent;
    --message-primary: var(--main-text);
    --compose-primary: var(--main-text);
    --search-input-background: var(--main-secondary);
    --search-container-background: var(--main);
    --compose-input-background: var(--main);
}

/* Sidebar */
._1ecJY {
    padding: 8px 0;
}

/* Sidebar contact */
._2EXPL {
    border-radius: var(--border-radius);
    margin: 0 8px 2px 8px;
}

/* Sidebar active contact */
html[dir] ._2EXPL._1f1zm {
    background-color: transparent;
}

/* Sidebar acitve contact borders */
html[dir] ._2EXPL:hover:after, html[dir] ._2EXPL._1f1zm:after, html[dir] ._3j7s9 {
    border-top: none
}

/* Sidebar hover contact */
html[dir] ._2EXPL.hover {
    background-color: transparent;
}

/* Sidebar active contact name */
._2EXPL._1f1zm ._25Ooe {
    color: var(--white);
}

/* Sidebar active contact content */
._2EXPL._1f1zm .dIyEr, ._2EXPL._1f1zm ._3j7s9 {
    background-color: var(--main);
}

/* Sidebar hover contact content */
._2EXPL.hover .dIyEr, ._2EXPL.hover ._3j7s9 {
    background-color: var(--sidebar-hover);
}

/* Sidebar contact content */
.dIyEr, ._3j7s9 {
    margin-bottom: 2px;
    border-radius: var(--border-radius);
}

/* Sidebar contact image */
html[dir] .dIyEr {
    margin-top: 0;
    border-radius: var(--border-radius) 0 0 var(--border-radius);
}

/* Sidebar contact content */
._3j7s9 {
    border-radius: 0 var(--border-radius) var(--border-radius) 0;
}

/* Sidebar contact name */
._25Ooe {
    color: var(--sidebar-text);
}

/* Sidebar contact recent message */
._1AwDx {
    color: var(--sidebar-text);
}

/* Sidebar contact pinned icon */
._14MyY {
    color: var(--sidebar-text);
}

/* Sidebar search input */
html[dir] ._2MSJr {
    background-color: var(--background);
    border-radius: var(--border-radius);
}

/* Sidebar search input seperator */
html[dir] .gQzdc:after {
    background-color: var(--background);
}

/* Sidebar search input label */
._3F6QL ._2S1VP {
    color: var(--background-text);
}

/* Chat background */
[data-asset-chat-background-light] {
    background-image: none;
}

/* Chat header */
html[dir] ._2y17h {
    background: transparent;
}

/* Chat header border */
html[dir] ._2y17h:after {
    background-color: transparent;
}
html[dir=ltr] ._1iFv8 {
    border-left: none;
}

/* Chat message timestamp */
._1DZAH {
    color: var(--main-text-dim);
}

/* Chat message quote */
.Y9G3K {
    color: var(--main-text);
}

/* Chat message */
.MVjBr._3e2jK {
    background-color: var(--outgoing-background) !important;
}

/* Chat message selected quote */
html[dir] .rstyJ {
    padding-top: 0;
    background-color: transparent;
}

/* Chat message selected quote content */
html[dir=ltr] ._2bdRS {
    margin-left: 15px;
    border-radius: var(--border-radius);
    background-color: var(--main-secondary);
}

/* Chat message selected quote close icon */
html[dir] ._3SX5f {
    background-color: transparent;
}

/* Chat message download button */
._17viz {
    color: var(--main-text-dim);
}

/* Chat message tails */
.tail-container, .tail-container.highlight {
    background-image: none !important;
}

/* Chat message menu text */
._34D8D {
    color: var(--main-secondary-text);
}

/* Links */
a {
    color: var(--link);
}

/* Chat input */
html[dir] ._3pkkz {
    margin: 0 15px 15px 15px;
    padding: 0;
    min-height: unset;
    background-color: var(--main-secondary);
    border: none;
    border-radius: 10px;
}
html[dir] ._3uMse {
    border: none;
}

/* Chat input with emoji keyboard */
.grGJn + ._2tW_W ._3pkkz {
    margin-top: 15px;
}

/* Chat input emoji icon */
.weEq5 {
    border-right: 1px solid var(--main);
}

/* Chat input icons */
._1x_c3, ._2SbJ1 {
    color: var(--main-text-icon);
}

/* Chat input text */
._3F6QL._2WovP ._2S1VP {
    color: var(--white);
}

/* Emoji keyboard icons */
.GclUX {
    color: var(--main-text-icon);
}

/* Emoji keyboard search placeholder */
html[dir=ltr] ._2U5s1::placeholder {
    color: var(--background-text);
}

/* Chat date message */
html[dir] .Zq3Mc {
    text-shadow: none;
    color: var(--main-secondary-text);
    border-radius: 0;
    width: 100%;
    border-bottom: 1px solid var(--main-secondary-text);
}

/* Chat media send background */
html[dir] .JWJN5 {
    background: var(--background);
}

/* Chat media input text */
._3F6QL ._2S1VP {
    color: var(--white);
}

/* Chat media image placeholder */
html[dir] ._1ypOz {
    background: var(--main);
    color: var(--main-text);
}

/* No chat selected header */
html[dir] .app {
    background-image: none !important;
}

body {
    background: var(--background);
}
}
