css:
@-moz-document domain("vaders.tv") {
.listings-details {
	display: none; visibility: hidden;
}

.col-xs-3 {
    display: none; visibility: hidden;
}

.listings-channel {
    display: block!important;
    margin: 0px auto;
}

.listings-channel div {
    margin: 0px auto;
    text-align: center;
}

.col-xs-6 {
 	font-size: 15pt;   
    overflow: visible!important;
    width: 100%!important;
    margin: 0px auto!important;
    line-height: 20px!important;
}

.epg-container ul.listings-grid .listings-program-title {
 	font-size: 14pt;   
}

.epg-container ul.listings-grid .listings-channel {
    height: 45px;
}

.listings-timebar {
 	font-size: 18pt!important;
    padding-top: 8px;
}

.listings-timebar-time {
	border-left: 0px!important;
    padding-left: 5px;
}

.progress-bar {
	background-color:#d64e33!important;   
}

.btn-primary {
    padding: 9px 25px;
}

.btn-default  {
 	padding: 9px;   
}

.input-group input {
 	padding: 19px!Important;
    border: 0px;
}
}
