css:
@-moz-document url-prefix("http://www.apurogol.net/") {
body {
	box-sizing: border-box !important;
	background-color: #f0f0f0 !important;
}
h1 {
	text-transform: uppercase;
}
.sidebar {
	display: none;
}
#bar_equipos {
	margin: 1rem auto !important;
}
#content {
	display: flex;
	flex-direction: column !important;
	width: 100% !important;
	background-color: transparent !important;
	text-align: center;
	justify-content: center !important;
}
#main {
	display: flex;
	flex-direction: column !important;
	width: 100% !important;
	padding: 0 !important;
	justify-content: center !important;
}
#menu,
#outer {
	width: 95% !important;
	display: flex;
	justify-content: center !important;
	flex-direction: column;
}
#footer {
	box-sizing: border-box;
	max-width: 100% !important;
	background-color: #1C2127 !important;
	padding: 0 5rem !important;
}
.post {
	display: flex;
	flex-direction: column !important;
	width: 60% !important;
	background-color: white;
	border-radius: 3px;
	margin: 0 auto;
	padding: 1rem !important;
}
.post > .posted {
	display: none;
}
.post > .title {
	text-align: center;
}
.post > .entry {
	padding: 0 !important;
}
.post > .entry > center > .video-thumbnail > a > img,
.post > .entry > center > img {
	width: 100% !important;
	max-width: 100% !important;
}
#respond {
	display: flex !important;
	flex-direction: column;
	justify-content: center !important;
	margin-top: 1rem;
}
.twitter-tweet,
.post-tags {
	margin: 1rem auto !important;
}
}
