css:
@-moz-document url("https://www.webassign.net/wa-auth/login") {
/*SIGN IN PAGE*/
/*header*/

.header-bar[_ngcontent-c0] {
    background-color: #212121;
    border-bottom: none;
    height: 60px;
}
button.wa-secondary {
    background-color: #B71C1C;
    border: none;
    color: white;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
button.wa-secondary:hover {
    border: none;
    background-color: #C62828;
    box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.14), 0 1px 7px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -1px rgba(0, 0, 0, 0.2);
}
/*sign in*/

html,
.this-is-hydra {
    background-color: #303030;
}
.this-is-hydra h2 {
    color: white;
}
.login-container[_ngcontent-c1] {
    background-color: #303030;
    border: none;
    box-shadow: none;
    color: white;
}
.login-container[_ngcontent-c1] .login-form[_ngcontent-c1] {
    background-color: #303030;
    border: none;
    height: 481px;
}
button.wa-primary {
    background-color: #B71C1C;
    border: none;
    color: white;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
button.wa-primary:hover {
    border: none;
    background-color: #C62828;
    box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.14), 0 1px 7px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -1px rgba(0, 0, 0, 0.2);
}
.auth-container a {
    color: #FF5252;
}
.auth-container a:hover {
    color: #448AFF;
}
.auth-container input[type="email"],
.auth-container input[type="password"],
.auth-container input[type="text"] {
    background-color: #303030;
    border-radius: 2px;
    box-shadow: none;
    padding: 8px 0px 8px 16px;
    border-top-style: none;
    border-right-style: none;
    border-bottom-style: solid;
    border-left-style: none;
    border-bottom-width: 2px;
    border-bottom-color: #505050;
    color: white;
}
.auth-container input[type="email"]:hover,
.auth-container input[type="password"]:hover,
.auth-container input[type="text"]:hover {
    border-color: #b71c1c;
}
.auth-container .forget {
    border-left: 1px solid #505050;
    color: #505050;
}
.auth-container a:hover {
    color: #B71C1C;
}
/*error*/

.error-box[_ngcontent-c2] {
    background-color: rgba(82, 113, 0, 0.1);
    border: none;
    border-radius: 2px;
    color: #FFEB3B;
}
a[_ngcontent-c2] {
    color: #FFEB3B;
}
a[_ngcontent-c2]:hover {
    color: #FFF176;
}
}

@-moz-document url-prefix("https://www.webassign.net/web/Student"), url-prefix("https://www.webassign.net/v4cgi") {
/*MAIN*/

body {
    background-color: #303030;
    color: white;
}
/*app bar*/

.top-nav--logo {
    width: 300px;
    background: url('https://www.webassign.net/wa-auth/images/WebAssign_Logo_Partner_White.svg') no-repeat 0 3px;
    text-indent: 100%;
    white-space: nowrap;
    overflow: hidden;
    background-color: #212121;
}
#topNav {
    background-color: #212121;
    height: 55.2px;
}
.RequirementModal div.RequirementMain h1,
h2 {
    color: white;
}
p {
    color: white;
}
/*middle*/

#middle {
    background: #303030;
}
.middle {
    background-color: #303030;
}
.infoBox h1 {
    margin: -0.4em 0 0.8em 0;
    color: white;
    font: bold 16px arial, helvetica, sans-serif;
}
.row.container {
    background: #424242 !important;
}
span.pButton a,
span.sButton a {
    padding: 12px 1em 6px !important;
    color: white;
}
.sButton {
    background: #B71C1C !important;
    border: none !important;
    color: white;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    height: 42px;
    min-width: 108px;
    margin-bottom: 12px;
}
.sButton:hover {
    background: #C62828 !important;
    border: none !important;
    color: white;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    height: 42px;
    min-width: 108px;
    margin-bottom: 12px;
}
/*navbar*/

#webAssign #studentNav {
    background: #212121;
    height: 29.6px;
}
#studentNav a {
    text-transform: uppercase;
    font-weight: bold;
    font-stretch: expanded;
    font-size: 14px;
    color: white;
    margin-top: 0px;
    padding-left: 13px;
    padding-right: 13px;
    padding-top: 10px;
    padding-bottom: 10px;
}
#studentNav a:hover {
    color: white;
    text-decoration: none;
    border-bottom: 2px solid #616161;
    border-top-style: hidden;
    border-left-style: hidden;
    border-right-style: hidden;
    background-color: #424242;
}
#studentNav a:active {
    border: 2px solid #212121;
    border-bottom-color: #757575;
    border-top-style: hidden;
    border-left-style: hidden;
    border-right-style: hidden;
    background-color: #616161;
}
.pageLeft {
    margin-top: -5px;
    height: 40px;
}
.studentNavBorder {
    margin-top: -2px;
    padding: 0;
    height: 2px;
    background: #B71C1c;
    width: 69px;
    margin-left: 16px;
}
/*wa box*/

.waBox {
    background-color: #424242;
    border-radius: 2px;
    box-shadow: 0 4px 10px #262626;
}
.waBox .title {
    background: #424242;
}
.waBox .top .title h4 {
    color: white;
}
.waBox .top .border {
    background: none;
}
.waBox .top > .left {
    background: none;
}
.waBox .top > .right {
    background: none;
}
.waBox .bottom {
    height: 4px;
    position: relative;
    clear: both;
}
/*\\\\\Assignments\\\\\*/

.assignmentSummary .infoGroup {
    border-top: none;
}
.waBox .content {
    border-left: none;
    border-right: none;
    border-radius: 2px;
    box-shadow: 0 4px 10px #262626;
}
.waBox .subTools {
    background: none;
    border: none;
}
.waBox .bottom .border {
    border-bottom: none;
}
.waBox .bottom > .right {
    background: none;
}
.waBox .bottom > .left {
    background: none;
}
.waQBox .standard,
.waQBox .crop {
    background: #424242;
}
.waQBox > .qUtility > .standard,
.waQBox > .qUtility > .crop {
    border: none;
    border-radius: 2px;
}
font {
    color: yellow;
}
.waQBox {
    background: #424242;
    border-radius: 2px;
    box-shadow: 0 4px 10px #262626;
}
.waQBox .top {
    background: #424242;
}
.waQBox .top .border,
.waQBox .bottom .border {
    background: #424242;
}
.waQBox .title {
    background: none;
    border-left: none;
    border-right: none;
}
.waQBox .top > .left {
    background: none;
}
.waQBox .top > .right {
    background: none;
}
.waQBox .bottom > .left {
    background: none;
}
.waQBox .bottom > .right {
    background: none;
}
.waQBox .top .border {
    border-top: none;
}
.waQBox .bottom .border {
    border: none;
}
.waQBox > .qUtility > .qButtons {
    background: none;
    border: none;
    padding: 0px;
}
.waQBox .title .detailsLink.expand {
    color: #FF5252;
}
a,
a:link,
a:active,
a:visited {
    color: #FF5252;
}
.manualGrading.not_graded {
    border-color: #303030;
}
#webAssign textarea {
    color: white;
    border-top: none;
    border-right: none;
    border-bottom: 1px solid #B71C1C;
    border-left: none;
    border: 1px;
    background: #303030;
}
#webAssign input {
    background-color: #303030;
    border-top: none;
    border-right: none;
    border-bottom: 1px solid #B71C1C;
    border-left: none;
    color: white;
}
.static {
    background: #424242;
    border: 1px solid #303030;
}
.submitSaveStandard .sButton {
    background: #B71C1C !important;
    border: none !important;
    color: white;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    height: 42px;
    min-width: 108px;
    margin-bottom: 12px;
}
.submitSaveStandard .sButton:hover {
    background: #C62828 !important;
    border: none !important;
    color: white;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    height: 42px;
    min-width: 108px;
    margin-bottom: 12px;
}
.finalSubmitBox .finalSubmitBoxBorder {
    background: #303030;
    border: none;
}
.finalSubmitBox .bBorder {
    background: #303030;
    border: none;
}
.finalSubmitBox .tBorder {
    border-bottom: none;
}
.finalSubmitBox .borderWrap {
    background: #303030;
}
.finalSubmitBox .lTopCorner {
    background: #303030;
}
.fButton {
    background: #B71C1C !important;
    border: none !important;
    color: white;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    height: 42px;
    min-width: 108px;
    margin-bottom: 12px;
}
.fButton:hover {
    background: #C62828 !important;
    border: none !important;
    color: white;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    height: 42px;
    min-width: 108px;
    margin-bottom: 12px;
}
/*hint/feedback popup*/
.y-tip {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
}
.y-tip .nw, .y-tip .n, .y-tip .tip-text, .y-tip .sw, .y-tip .s {
    background: #303030;
}
.y-tip .s {
    height: 0px;
}
.y-tip .nw {
    border-radius: 2px 2px 0 0;
}
.y-tip .sw {
    border-radius: 0 0 2px 2px;
}
.bContent {
    border: none;
    background: #303030;
}
.y-tip .bShim, .y-tip .bHandle, .y-tip .nCarrot {
    filter: invert(82%);
    border: none;
    box-shadow: none;
}
.y-tip .bTitle {
    filter: invert(82%);
}
/*----------*/
}
