css:
@-moz-document domain("stackoverflow.com") {
body, .top-bar { font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" }
.top-bar { background-color: #404040; }
#hireme, #newsletter-ad { display: none !important; }
pre { font-family: "Inconsolata", Consolas, monospace; }
.top-bar{border:none;}
.top-bar .navigation .-item._current .-link { color: #ffffff; }
.top-bar .navigation .-link { color: #909090; }
.top-bar .searchbar input[type="text"].f-input { font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
  background-color: #353535; box-shadow: none; border:none; border-radius:0; color: #3b4045; }
.top-bar .searchbar input[type="text"].f-input:hover{box-shadow:none}
.top-bar .navigation .-link:hover, .top-bar .navigation .-link:focus, .top-bar .my-profile:hover, .top-bar .secondary-nav .-item .-link:hover {
  background-color: #272727;
  color:white;
}
.top-bar .my-profile {
    color: #b1b1b1;
}
}
