css:
@-moz-document url("https://app.glip.com/") 
{

.post,
.postslist,
h1.page-header,
h1.stream-header,
div.stream-compose 
{
    background-color: #393c40 !important;
}

h1, h2, h3, h4, label
{
    color: #c4c1c2 !important;
}

body
{
    background-color: #393c40 !important;
}

div.content
{
    background-color: #393c40 !important;
}

.fc-day-number
{
    color: #c4c1c2 !important;
}

.category
{
    color: #c4c1c2 !important;
}

.post_text 
{
    color: #c4c1c2 !important;
}

.actions 
{
    background-color: #393c40 !important;
}

#header-text
{
    color: #c4c1c2 !important;
}

#chat-textarea
{
    background-color: #4a4d53 !important;
    color: #c4c1c2;
}

.relative
{
    color: #4a4d53 !important;
    background-color: #4a4d53 !important;
}

.contentpad
{
	background-color: #393c40;
}

.page-header
{
    color: #c4c1c2;
}

.scrollable_wrap
{
    background-color: #393c40;
}

.team_name
{
    color: #c4c1c2;
}

div.person_name
{
    color: #c4c1c2;
}

.post-body
{
    border-top: 0px solid #4a4d53 !important;
}

.stream-compose .compose_type, .stream-compose #chat-textarea
{
    border-top: 1px solid #4a4d53 !important;
    border-bottom: 1px solid #4a4d53 !important;
    border-left: 1px solid #4a4d53 !important;
    border-right: 1px solid #4a4d53 !important;
    border-radius: 0px;
}

.stream-compose.focused .compose_type, .stream-compose.focused #chat-textarea
{
    border-top: 1px solid #4a4d53 !important;
    border-bottom: 1px solid #4a4d53 !important;
    border-left: 1px solid #4a4d53 !important;
    border-right: 1px solid #4a4d53 !important;
    border-radius: 0px;
}

.markdown.details
{
    color: #c4c1c2 !important;
}

.filename
{
    color: #c4c1c2 !important;
}

img.emoji, object.emoji 
{
    width: 35px;
    height: 35px;
    padding: 0 1px;
    vertical-align: -4px;
}

#topbar
{
    background-color: #393c40;
    border-bottom: 1px #393c40 solid;
    height: 50px;
}

#tb_search
{
    background-color: #393c40;
    border-bottom: 1px #393c40 solid;
    height: 25px;
 
}

#topbar_search_input
{
    background-color: rgb(150, 150, 150);
    height: 30px;
}

.post .interactive_message_item
{
    border-left: 0px;
    padding: 10px;
    padding-bottom: 5px;
    margin-top: 10px;
    margin-bottom: 10px;
    background-color: #fafafa00;
    border-bottom-right-radius: 4px;
    border-top: 0px #EEE solid;
    border-bottom: 0px #EEE solid;
    position: relative;
    border-right: 0px #EEE solid;
}

}
