css:
@-moz-document domain("wikipedia.org") {
.mw-body {
    max-width: 70ch  !important;
    font-size: 1.6em !important;
}

.mw-body-content  {
    line-height: 1.8 !important;
}

* {
    color: #333 !important;
    border: none !important;
}

a, a:hover, a:active, a:focus {
	/* text-decoration: underline !important; */
    text-decoration: none !important;
    text-decoration-skip: ink !important;
    border-bottom: 1px solid #333333 !important;
}

.mw-body-content sub, .mw-body-content sup, span.reference {
    font-size: 50% !important;
    opacity: 0.2 !important;
    margin-top: 0.5em !important;
    margin-left: -0.2em !important;
    position: absolute !important;
}

.mw-body-content sup a {
	text-decoration: none !important;
    border-bottom: none !important;
}

.mw-body-content sup a:before {
    content: '[' !important;
    position: absolute !important;
    color: white !important;
    left: 0 !important;
}

.mw-body-content sup a:after {
    content: ']' !important;
    position: absolute !important;
    color: white !important;
    right: 0 !important;
}

.referencetooltip a, .mwe-popups a, #toc a, #mw-panel a {
	text-decoration: none !important;
    border-bottom: none !important;
}

.mw-editsection, #p-views, #mw-head, #siteSub, .toctitle .toctoggle, .mw-content-ltr .tocnumber, .mbox-image, .mbox-textsmall-div, .after-portlet-lang {
	display: none !important;
}

#contentSub, #contentSub *, .dablink, .toctitle, table.ambox, table.ambox * {
	margin-left: 0 !important;
	margin-right: 0 !important;
    padding-left: 0 !important;
    background: none !important;
    text-align: left !important;
}

h2 {
	font-size: 1.5em !important;
}

#mw-panel {
	zoom: 1.4 !important;
}
}
