css:
@-moz-document url-prefix("https://try.gogs.io/") {
/* Main */

body {
  background-color: #1d1f21 !important;
  color: #e0e0e0;
}

body:not(.full-width) {
  background-color: #1d1f21 !important;
}

footer {
  background-color: #282a2e;
  color: #e0e0e0;
  border-top: 1px solid #373b41;
}

footer .container .fa {
  color: #81a2be;
}

a {
  color: #81a2be;
}

a:hover {
  color: #81a2be;
  opacity: 0.6;
}

hr {
  background-color: #e0e0e0 !important;
}

/* Navigation */

.following.bar.light {
  background-color: #282a2e;
  border-bottom: 1px solid #373b41;
}

.ui.secondary.menu .active.item {
  color: #e0e0e0;
  background-color: #1d1f21;
  font-weight: bold;
}

.ui.menu .item {
  color: #e0e0e0;
}

.ui.menu {
  background: #282a2e;
  border: none;
}

.following.bar .top.menu a.item:hover {
  color: #b4b7b4;
}

/* Home */

.home a {
  color: #cc6666;
}

.home .hero .octicon {
  color: #cc6666;
}

.ui.header {
  color: #e0e0e0;
}

.feeds .list ul li.private {
  background-color: #373b41;
  border-bottom: none;
}

.feeds .list ul li:not(:last-child) {
  border-bottom: 1px solid #373b41;
}

.ui.accordion .title:not(.ui) {
  color: #e0e0e0;
}

#auth_password {
  background: #373b41;
  color: #e0e0e0;
}

.ui.checkbox label:hover, .ui.checkbox+label:hover {
  color: #e0e0e0;
  opacity: 0.6;
}

.ui.secondary.menu .dropdown.item:hover, .ui.secondary.menu .link.item:hover, .ui.secondary.menu a.item:hover {
  color: rgba(224, 224, 224, 0.6);
}

/* Explore */

.ui.vertical.menu {
  background-color: #373b41;
}

.ui.vertical.menu .header.item {
  background-color: #373b41;
}

.ui.vertical.menu .active.item {
  background-color: #282a2e;
}

.ui.menu .active.item {
  color: #e0e0e0;
  background: #373b41;
}

.ui.menu a.item:hover {
  color: #fff;
  background-color: #282a2e;
}

.ui.repository.list .item:not(:first-child) {
  border-top: 1px solid #373b41;
}

.ui.menu .active.item:hover, .ui.vertical.menu .active.item:hover {
  background-color: #373b41;
  color: #e0e0e0;
}

.ui.form input:not([type]), .ui.form input:not([type]):focus {
  background-color: #373b41;
  color: #b4b7b4;
}

.ui.form input:not([type]):focus {
  border-top-color: #81a2be;
  border-left-color: #81a2be;
  border-bottom-color: #81a2be;
  color: #e0e0e0;
}

.ui.list .list>.item .description, .ui.list>.item .description {
  color: #969896;
}

.ui.repository.list .item .time {
  color: #969896;
}

.ui .text.grey {
  color: #969896 !important;
}

.ui.user.list .item:not(:first-child) {
  border-top: 1px solid #373b41;
}

.ui.divider:not(.vertical):not(.horizontal) {
  border-bottom: 1px solid #373b41;
}

.ui.list .list>.item .header, .ui.list>.item .header {
  color: #e0e0e0;
}

.ui.pagination.menu .active.item {
  color: #e0e0e0;
  background-color: #373b41;
}

.ui.menu .item.disabled, .ui.menu:hover .item.disabled:hover {
  color: #e0e0e0;
}

.ui.menu:hover .item.disabled:hover {
  background-color: #282a2e !important;
}

.ui.user.list .item .description a {
  color: #81a2be !important;
}

/* Repos */

.repository .header-wrapper {
  background-color: #282a2e;
}

.ui.basic.button, .ui.basic.buttons .button {
  color: #e0e0e0 !important;
}

.ui.tabular.menu .item {
  color: #e0e0e0;
}

.ui.tabular.menu .active.item {
  background: none #373b41;
  color: #e0e0e0;
  border-color: transparent;
}

.ui .text.black {
  color: #e0e0e0;
}

.ui .text.black:hover {
  color: #e0e0e0;
}

.ui.segment {
  background: #282a2e;
}

.ui.input input {
  background: #373b41;
  color: #e0e0e0;
}

.ui.basic.button:hover, .ui.basic.buttons .button:hover {
  background: #e0e0e0 !important;
}

.ui.basic.label {
  background: none #e0e0e0;
}

.ui.dropdown .menu {
  background: #e0e0e0;
}

.reference.column .text.black {
  color: #1d1f21;
}

.ui.table {
  background: #282a2e;
  color: #e0e0e0;
}

.ui.table thead th {
  background: #e0e0e0;
  color: #1d1f21;
}

.repository.file.list #repo-files-table tr:hover {
  background-color: #373b41;
}

.ui.breadcrumb a {
  color: #81a2be;
}

.ui.breadcrumb a:hover {
  color: #81a2be;
  opacity: 0.6;
}

.ui.basic.blue.button, .ui.basic.blue.buttons .button {
  color: #81a2be !important;
  box-shadow: 0 0 0 1px #81a2be inset !important;
}

.ui.basic.blue.button:hover, .ui.basic.blue.buttons .button:hover {
  color: #81a2be !important;
  opacity: 0.6;
}

.ui.menu .item>.label {
  color: #1d1f21;
}

.ui.tabular.menu .item:hover {
  color: #e0e0e0;
  opacity: 0.6;
}

.ui.breadcrumb .divider {
  color: #e0e0e0;
}

.three.wide.text.grey.right.age .time-since.poping.up {
  color: #1d1f21;
}

.ui.attached.header {
  background: #e0e0e0;
}

.ui.top.attached.header {
  background: #e0e0e0;
  color: #282a2e;
}

.ui .warning.header {
  background-color: #de935f !important;
  border-color: #de935f !important;
}

.ui.attached.table {
  border: transparent;
}

.markdown:not(code) .highlight pre, .markdown:not(code) pre {
  background-color: #1d1f21;
}

.hljs {
  color: #e0e0e0 !important;
}

.hljs-built_in, .hljs-builtin-name {
  color: #81a2be !important;
}

.hljs-comment, .hljs-quote {
  color: #969896 !important;
  opacity: 0.7;
}

.hljs-keyword, .hljs-selector-tag, .hljs-subst {
  color: #e0e0e0 !important;
}

.hljs-tag, .hljs-name, .hljs-attribute {
  color: #81a2be !important;
}

.hljs-number, .hljs-literal, .hljs-variable, .hljs-template-variable, .hljs-tag .hljs-attr {
  color: #8abeb7 !important;
}

.hljs-string, .hljs-doctag {
  color: #cc6666 !important;
}

.markdown:not(code) h1, .markdown:not(code) h2, .markdown:not(code) h3, .markdown:not(code) h4, .markdown:not(code) h5, .markdown:not(code) h6 {
  border-bottom: 1px solid rgba(224, 224, 224, 0.6);
}

.ui.basic.small.button {
  background: #282a2e !important;
}

.linenums>li {
  background-color: #282a2e;
}

.lines-num>span {
  background: #373b41;
}

.ui.blue.button:hover, .ui.blue.buttons .button:hover {
  background-color: #81a2be;
  opacity: 0.6;
}

.repository .diff-file-box .code-diff tbody tr.add-code td {
  background-color: rgba(181, 189, 104, 0.1) !important;
  opacity: 0.6;
}

.ui .info.segment.top {
  background-color: #282a2e !important;
}

.tag-code.nl-0.ol-0>.lines-code>pre>code {
  color: #1d1f21 !important;
}

.hljs-title, .hljs-section, .hljs-selector-id {
  color: #cc6666 !important;
}

.ui.checkbox label, .ui.checkbox+label {
  color: #e0e0e0;
}

.ui.checkbox input:focus~label {
  color: #e0e0e0 !important;
}

.ui.red.button, .ui.red.buttons .button {
  background-color: #cc6666;
}

.ui.green.button:hover, .ui.green.buttons .button:hover {
  background-color: #cc6666;
  opacity: 0.6;
}

.repository .diff-file-box .code-diff tbody tr.del-code td {
  background-color: rgba(204, 102, 102, 0.1) !important;
}

.ui.secondary.pointing.menu a.item:hover {
  background-color: #282a2e;
  color: #e0e0e0 !important;
}

.markdown:not(code) table tr {
  background-color: #1d1f21;
}

.markdown:not(code) table tr:nth-child(2n) {
  background-color: #373b41;
}

.markdown:not(code) table th, .markdown:not(code) table td {
  border: none !important;
}

.markdown:not(code) blockquote {
  color: #b4b7b4 !important;
}

.markdown:not(code) code, .markdown:not(code) tt {
  background-color: #373b41;
}

/* Issues */

.ui.basic.green.active.button, .ui.basic.green.buttons .active.button {
  box-shadow: 0 0 0 1px #b5bd68 inset !important;
  color: #b5bd68 !important;
}

.ui.basic.button:hover, .ui.basic.buttons .button:hover {
  background: #282a2e !important;
  color: #e0e0e0 !important;
}

.ui.basic.red.active.button, .ui.basic.red.buttons .active.button {
  box-shadow: 0 0 0 1px #cc6666 inset !important;
  color: #cc6666 !important;
}

.ui.compact.small.menu {
  background: #282a2e !important;
}

.issue.list>.item .title {
  color: #e0e0e0;
}

.ui.black.label, .ui.black.labels .label {
  color: #e0e0e0;
  background-color: #282a2e !important;
}

.issue.list>.item .desc {
  color: #e0e0e0;
  opacity: 0.6;
}

.issue.list>.item .title:hover {
  color: #e0e0e0;
  opacity: 0.6;
}

.repository.view.issue .comment-list .comment .content .header {
  background-color: #e0e0e0;
}

.ui .text.grey a {
  color: #1d1f21 !important;
}

.ui.attached.segment {
  border: transparent;
}

.ui.warning.message {
  background-color: #e0e0e0;
}

.ui.green.label, .ui.green.labels .label {
  background-color: #b5bd68 !important;
  border-color: #b5bd68 !important;
  color: #e0e0e0 !important;
}

.ui.green.button, .ui.green.buttons .button {
  background-color: #b5bd68 !important;
  color: #e0e0e0 !important;
}

.ui.basic.red.button, .ui.basic.red.buttons .button {
  color: #cc6666 !important;
  box-shadow: 0 0 0 1px #cc6666 inset !important
}

.ui.basic.green.button, .ui.basic.green.buttons .button {
  color: #e0e0e0 !important;
  box-shadow: none !important;
}

.ui.red.label, .ui.red.labels .label {
  background-color: #cc6666 !important;
  border-color: inherit !important;
}

.ui.form textarea {
  background: #e0e0e0;
}

.ui.basic.green.button:hover, .ui.basic.green.buttons .button:hover {
  box-shadow: 0 0 0 1px #b5bd68 inset !important;
  color: #b5bd68 !important
}

/* Pull requests */

.ui.header .sub.header {
  color: #e0e0e0 !important;
}

.editor-toolbar a {
  color: #81a2be !important;
}

/* Wiki */

.repository.wiki.view .markdown {
  background: #282a2e;
}

/* Profile drop down */

.ui.menu .dropdown.item .menu {
  background: #e0e0e0;
}

.ui.menu .ui.dropdown .menu>.item:hover, .ui.menu .ui.dropdown .menu>.selected.item {
  background: #b4b7b4 !important;
  opacity: 0.6;
}

/* Profile */

.ui.card, .ui.cards>.card {
  background: #282a2e;
  box-shadow: none;
}

.ui.card>.extra a:not(.ui), .ui.cards>.card>.extra a:not(.ui) {
  color: #81a2be;
}

.ui.card>.extra, .ui.cards>.card>.extra {
  border-top: 1px solid #373b41 !important;
}

.user.profile .ui.card .extra.content ul li:not(:last-child) {
  border-bottom: 1px solid #373b41 !important;
}

.ui.secondary.pointing.menu .active.item {
  color: #e0e0e0;
}

/* Settings */

.ui.form .field>label {
  color: #e0e0e0;
}

#website {
  background: #373b41;
  color: #e0e0e0;
}

.inline.box.field>label {
  color: #e0e0e0 !important;
}

.inline.box.field>input {
  background: #373b41;
  color: #e0e0e0;
}

#old_password, #password, #retype, #email, #description {
  background: #373b41;
  color: #e0e0e0;
}

label[for=org_name] {
  color: #e0e0e0 !important;
}

.ui.red.message {
  color: #cc6666;
}

.ui.very.basic.striped.table>thead>tr>th {
  color: #e0e0e0;
}

.ui.form .inline.field>label, .ui.form .inline.field>p, .ui.form .inline.fields .field>label, .ui.form .inline.fields .field>p, .ui.form .inline.fields>label {
  color: #e0e0e0;
}

.ui.very.basic.select.selectable.table>thead>tr>th {
  color: #e0e0e0;
}

.ui.teal.button, .ui.teal.buttons .button {
  background-color: #8abeb7;
}

.ui.form input[type=file], .ui.form input[type=number], .ui.form input[type=url], .ui.form input[type=password] {
  color: #e0e0e0;
  background: #373b41;
}

.ui.selection.dropdown {
  color: #e0e0e0;
  background: #373b41;
}

.organization.members .list .item {
  border-bottom: 1px solid #373b41 !important;
}

.settings .hook.list>.item:not(:first-child) {
  border-top: 1px solid #373b41 !important;
}

.settings .key.list .item:not(:first-child) {
  border-top: 1px solid #373b41 !important;
}

/* Commit */

.repository .diff-box .file {
  color: #969896;
}

.ui.blue.button, .ui.blue.buttons .button {
  background-color: #81a2be;
  color: #e0e0e0 !important;
}

.ui.basic.grey.button, .ui.basic.grey.buttons .button {
  color: #282a2e !important;
}

.added-code {
  color: #1d1f21;
  background-color: #b5bd68;
}

.removed-code {
  color: #1d1f21;
  background-color: #cc6666;
}

.ui.blue.label, .ui.blue.labels .label {
  background-color: #81a2be !important;
  color: #e0e0e0 !important;
}

.repository .diff-file-box .file-body.file-code .lines-num {
  background: none;
}

.repository .diff-file-box .code-diff tbody tr.tag-code td {
  background-color: #e0e0e0 !important;
}

.hljs-meta {
  color: #373b41 !important;
}

}
