css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamerankings.com") {

#rbx_netxp1_parent,
.gs,
#footer,
.ad,
iframe,
img[height="1"],
#header,
#gne_nav {
display: none !important;
}

a {
outline: none !important;
}

body {
background: #204178 !important;
font-size: 62.5% !important;
}

#container {
background: #E6E6E6 !important;
margin: 0 30px !important;
width: auto !important;
border-left: 1px solid #202020 !important;
border-right: 1px solid #202020 !important;
font-size: 1.5em !important;
}

#content {
background: #fff !important;
padding: 0 10px !important;
width: 880px !important;
border: 0 !important;
}


#searchbox {
float: none !important;
width: auto !important;
height: auto !important;
overflow: hidden !important;
background: #fff !important;
border: 0 !important;
color: #333 !important;
padding: 0 !important;
margin: 0 !important;
}
#searchbox a {
color: #333 !important;
}
#searchbox form {
width: auto !important;
overflow: hidden !important;
margin: 6px 0 !important;
}
#searchbox label{
float: left !important;
margin-left: 10px !important;
}
#searchbox input.text,
#searchbox select {
margin: 2px 5px 0 0 !important;
}
form button {
width: auto !important;
height: auto !important;
padding: 0 4px !important;
line-height: normal !important;
font-size: 11px !important;
font-weight: bold !important;
border: 0 !important;
background: #d8e1f6 !important;
}


#quicknav {
float: none !important;
width: auto !important;
overflow: hidden !important;
height: auto !important;
overflow: hidden !important;
background: #EBEAED url(http://i279.photobucket.com/albums/kk133/rabidtapeworm/quicknav.gif) repeat-x top left !important;
border-top: 1px solid #A7A6AA !important;
border-bottom: 1px solid #A7A6AA !important;
color: #fff !important;
}
#quicknav p {
overflow: hidden !important;
padding: 4px 0 !important;
margin: 0 !important;
}
#quicknav a {
height: auto !important;
font-size: .85em !important;
line-height: 1.7em !important;;
background: transparent !important;
color: #383838 !important;
padding: 0 7px !important;
margin: 0 0 0 3px !important;
text-decoration: none !important;
border: 1px solid transparent !important;
-moz-border-radius: 3px !important;
}
#quicknav a:hover {
border: 1px solid #CDCCD0 !important;
color: #222 !important;
background: #fff !important;
}



#platformlist {
float: none !important;
width: auto !important;
overflow: hidden !important;
background: #E9E7EB url(http://i279.photobucket.com/albums/kk133/rabidtapeworm/platformlist.gif) repeat-x top left !important;
border-top: 1px solid #C2C0C4 !important;
border-bottom: 1px solid #A7A6AA !important;
color: #333 !important;
padding: 2px 0 3px 10px !important;
}
#platformlist .nav,
#platformlist .nav b {
color: #333 !important;
padding: 0 !important;
}
#platformlist .nav {
margin-top: 2px !important;
}
.jumper {
padding: 0 !important;
}
#jumpmenu {
margin-right: 10px !important;
}
#platformlist .nav a {
padding: 1px 6px !important;
color: #333 !important;
-moz-border-radius: 3px !important;
text-decoration: none !important;
}
#platformlist .nav a:hover {
color: #fff !important;
background: #4f4f4f !important;
}



.crumbs {
float: none !important;
width: auto !important;
overflow: hidden !important;
background: #E7E7E7 url(http://i279.photobucket.com/albums/kk133/rabidtapeworm/breadcrumbs.gif) repeat-x top left !important;
border: 1px solid #CDCDCD !important;
color: #fff !important;
padding: 7px 12px 7px 7px !important;
-moz-border-radius: 4px !important;
font-size: .1em !important;
}
.crumbs a {
color: #666 !important;
font-size: 7.7em !important;
padding: 7px 17px 9px 4px !important;
background: transparent url(http://i279.photobucket.com/albums/kk133/rabidtapeworm/divider.png) no-repeat center right !important;
}
.crumbs a:hover {
color: #2A2A2A !important;
}



.gamespace_nav {
float: none !important;
width: auto !important;
overflow: hidden !important;
background: #E7E7E7 url(http://i279.photobucket.com/albums/kk133/rabidtapeworm/breadcrumbs.gif) repeat-x top left !important;
border: 1px solid #CDCDCD !important;
color: #fff !important;
padding: 6px 0 !important;
-moz-border-radius: 4px !important;
font-size: .95em !important;
}
.gamespace_nav ul li {
background: transparent !important;
margin: 0 5px !important;
height: auto !important;
}
.gamespace_nav ul li a b {
height: auto !important;
line-height: 1.4em !important;
}

.gamespace_nav ul li a b.first,
.gamespace_nav ul li a b.last {
background: transparent !important;
-moz-border-radius: 8px !important;
}

.gamespace_nav ul li a {
height: auto !important;
color: #f0f0f0 !important;
background: #717171 !important;
-moz-border-radius: 8px !important;
}
.gamespace_nav ul li a:hover {
color: #fff !important;
background: #4f4f4f !important;
}

.gamespace_nav ul li.hi,
.gamespace_nav ul li.hi a {
color: #fff !important;
background: #005adc !important;
-moz-border-radius: 8px !important;
background: #08338C url(http://i279.photobucket.com/albums/kk133/rabidtapeworm/nav-hover.gif) repeat-x top left !important;
}


#full_col_wrap,
#full_col_wrap #side_col_wrap {
background: #E6E6E6 !important;
}


#content {
float: none !important;
width: auto !important;
overflow: hidden !important;
}


#content .head {
border: none !important;
margin: 0 0 0 0 !important;
}
#content h1,
#content .head h1 {
font-size: 1.4em !important;
margin: 4px 1px 3px !important;
padding: 0 !important;
}

#content .hub_nav h1 {
margin: 1px 0 !important;
}


#sky_col_wrap {
margin: 0 !important;
}
#sky_col_wrap {
background: transparent !important;
}
#sky_col_wrap #main_col_wrap,
#sky_col_wrap #main_col_wrap #main_col {
width: 880px !important;
}

#content #side_col_wrap {
padding: 0 !important;
}

#full_col_wrap #main_col_wrap,
#full_col_wrap #main_col_wrap #main_col {
width: 565px !important;
margin-right: 10px !important;
}

.pod {
float: none !important;
width: auto !important;
padding: 0 !important;
overflow: hidden !important;
background: #fff !important;
border: 1px solid #ACACAC !important;
-moz-border-radius: 7px !important;
}
#side_col .pod .body {
background: #fff !important;
padding: 0 !important;
width: auto !important;
-moz-border-radius: 0 0 5px 5px !important;
}


#content .pod .head,
#content .pod .head h1,
#board_wrap .head,
#board_wrap .head h1 {
margin: 0 !important;
background: #D8E1F6 !important;
border: none !important;
color: #474747 !important;
-moz-border-radius: 6px 6px 0 0 !important;
}
#content .pod .head h1 {
font-size: 1.2em !important;
padding: 5px 0 6px 9px !important;
}
#content .pod .body .details {
padding: 2px 8px 5px !important;
}

.body tr.even:last-child td {
-moz-border-radius: 0 0 5px 5px !important;
}

#content thead th,
#content tr.head th {
background: #0A3A98 !important;
font-weight: bold !important;
}
#content col.date {
width: 80px !important;
}



#content .hub_nav {
float: none !important;
margin: 2px 0 10px !important;
padding: 0 !important;
width: auto !important;
}
#content .hub_nav .box_tl {
overflow: hidden !important;
background: #fff !important;
border: 1px solid #ACACAC !important;
-moz-border-radius: 7px !important;
}
#content .hub_nav .box_tr {
padding: 0 !important;
}
#content .hub_nav .box_bl,
#content .hub_nav .box_tr,
#content .hub_nav .box_br {
background: transparent !important;
}

input {
background: red !important;
}

}
