css:
@-moz-document domain("osnews.com") {
#nav1 a {
    color: #8dc000;
}
/* larger font size */
body {
    font-size: 15px;
}
.otitle1, .newstitle1, .newstitle2, .collapsedtitle, .p2title {
    font-size: 20px;
}
.newssubheader1, .newssubheader2,
.newsfooter1, .newsfooter2 {
    font-size: 13px;
}
#q, #username, #password,
#register,
#header legend,
#header p,
#nav2 a,
#rss {
    font-size: 12px !important;
}
#nav1 a {
    font-size: 14px;
}
label[for=password] {
    text-align: right;
    width: 93px;
}
#submit-search, #login_btn {
    width: auto;
}
/* fluid layout */
.newscontent1 {
    width: auto !important;
}
#main {
    width: auto;
}
body {
    max-width: 45em;
    width: 100%;
    margin: auto;
}
#header h1 a {
    position: absolute;
}
#search {
    float: right;
    width: 160px;
    margin-top: -6px;
}
#search legend span {
    width: 144px;
}
#login {
    float: right;
    width: 254px;
    margin-top: -6px;
    margin-bottom: 6px;
}
#login legend span {
    width: 231px;
}
#register a:first-child {
    padding-left: 55px;
    background-color: #e55300;
}
#header label {
    margin-top: 21px;
}
#header input {
    margin: 2px 0;
}
#q, #username, #password {
    width: 90px;
}
}
