css:
@-moz-document domain("youtube.com") {
.ytd-masthead{
    background: #333 !important;
}

#logo-icon-container{
    filter: grayscale(1) !important;
}

#title.ytd-shelf-renderer {
    color: #fff !important;
}

#guide-inner-content{
    background: #333 !important;    
}

a:link{
    color:#fff !important;
}

.guide-icon.ytd-guide-entry-renderer {
    color: #fff !important;
}

.ytd-page-manager{
    background: #333 !important;    
}

#masthead-container.ytd-app::after {
    bottom: -5px !important;
	box-shadow: inset 4px 4px 8px 3px rgba(35, 35, 35, 1) !important;    
    height:5px !important;
}

#avatar img{
    filter: grayscale(1) !important;
}

.ytd-subscribe-button-renderer, .ytd-button-renderer{
    background:#fff !important;
    color:#111 !important;                                            	
}

.ytd-subscribe-button-renderer span{
    color:#555 !important;                                            	
}

.ytd-button-renderer span{
    color:#555 !important;                                            	
}

#sections.ytd-guide-renderer > .ytd-guide-renderer:not(:last-child) {
    border-bottom: 1px solid #111;
}

.ytd-guide-entry-renderer:active {
    color: #555 !important;
}

#title-annotation.ytd-shelf-renderer {
    color: #ddd !important;
}

.badge-style-type-live-now.ytd-badge-supported-renderer {
    color: #555 !important;
}

.badge-style-type-live-now.ytd-badge-supported-renderer {
    color: #2080d2 !important;
    border: 1px solid #1761b5 !important;
}

#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: #777 !important;
}

#description-text.ytd-video-renderer {
    color: #eee !important;
}

#ytd-badge-supported-renderer{
    color:#111 !important;
}

.ytd-topbar-menu-button-renderer svg{
    filter: brightness(100) !important;
}

.ytd-notification-topbar-button-renderer svg{
    filter: brightness(100) !important;
}

#guide-icon.ytd-masthead {
    fill: #fff !important;
}

.ytd-topbar-logo-renderer svg{
    display:none !important;
}

#logo-icon-container.ytd-topbar-logo-renderer {
    width: 80px;
    height: 24px;
    background: url(http://diylogodesigns.com/blog/wp-content/uploads/2016/05/youtube-logo-black-color-png.png) !important;
    background-size: 90%!important;
    background-position: 3px!important;
    background-repeat: no-repeat !important;
    filter:invert(1) !important;
}

.ytd-guide-entry-renderer img{
    
}

#country-code.ytd-topbar-logo-renderer {
    color: #fff !important;
}

input.ytd-searchbox{
	color:#fff !important;    
}

#container.ytd-searchbox, ytd-searchbox[mode="legacy-centered"], #container.ytd-searchbox {
    border: 1px solid #555 !important; 
    padding: 5px 9px !important; 
    box-shadow:none !important;
}

.ytd-searchbox{
	background:#222 !important;
}

.gsst_a{
    filter: invert(1) !important;
}

#search-icon-legacy.ytd-searchbox {
    border: 1px solid #555 !important;
    border-left: none !important;
}

.ytd-searchbox svg{
    filter:invert(1) !important;
}

.ytd-grid-video-renderer{color:#bbb !important;}

.ytd-video-primary-info-renderer span{color:#fff !important;}

.style-scope {color:#ccc !important;}

#ytd-button-renderer > .style-scope{
    color:#333 !important
}

.gsst_a, .gsst_e{

}

.gsst_e img{
    filter: invert(1) !important;
}

.ytd-searchbox svg{
    filter:invert(1) !important;
	filter: brightness(1) !important;    
}

.gsst_a{
    filter: invert(1) !important;
}







}
