/* ----------- Each News Story ----------- */
#news div.report {
background:#dae4d9!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px!important;
margin:10px!important;
/*padding-left:65px!important;*/
}

#news-side-bottom  {
background:#dae4d9!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px!important;
margin:10px 10px 10px 10px!important;
padding:10px!important;
}

/* ----------- Avatars ---------- */
div.iconleft img.avatar, div.iconright img.avatar {
margin-left:5px!important;
margin-top:5px!important;
}

/* ----------- Love Box ---------- */
div.report div.love {
margin-left:5px!important;
margin-top:5px!important;
}

/* ---------- Fix Sidebar Names ---------- */
div.smallnews div.report div.line0, div.smallnews div.report div.line1 {
padding-top:5px!important;
}

/* ---------- Ads ---------- */
[class="section"] {
display:none!important;
}

/* ----------- Titles ----------- */
[id="news-side-top"] > h2 {
background:#e6ede4!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px!important;
padding:5px!important;
font-size:13pt!important;
}

#news h1.cat {
background:#e6ede4!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px!important;
padding:5px!important;
font-size:18pt!important;
}

#news-side-bottom > h2 {
background:#e6ede4!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px 5px 0px 0px!important;
padding:5px!important;
margin:-11px -11px 11px -11px!important;
font-size:13pt!important;
}

/* ----------- Make New Items Not Red ---------- */
#news div sup.new, #news div.report strong.new {
color:#048dd4!important;
}

/* ---------- Navigation At Bottom ---------- */
div.bubbleview div.nav span.shadow span {
background:transparent!important;
border:0px!important;
}

div.bubbleview div.nav a {
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAN7k3eLn4d3j2+Hm397j3Nrg2OLm4Nvi2t/l3uPo4tzi2+Dl39fe1dvh2eTp49ng2OTo4tnf1/j599jf1uXp5Obq5QAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoFRVFOVASWAMCwIQgnI0xRNNExMCADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
padding:5px!important;
text-decoration:none!important;
position:relative!important;
font-size:8pt!important;
margin:0px 2px 0px 2px!important;
}

div.bubbleview div.nav a:hover {
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAObr5ubq5evu6ens5+ru6ebq5OPn4efs5unt6evv6vr7+ejs5+Hm4OXp5Ons6Ozv7OPo4uLn4OTp4+zv6wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoPI805QIBOIMywEERSNJEGREERMCADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
padding:5px!important;
text-decoration:none!important;
position:relative!important;
font-size:8pt!important;
margin:0px 2px 0px 2px!important;
}

div.bubbleview div.nav a:active {
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAM3WyszVydHYztje19PZ0dTb0+Dk4ODk3+/w7tXb1Nre2cnTyN/i3N3i3Nbc1d7i3c7Xzc/Wy9zg2dzh3N3g2tfd1t/j3gAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUV4BIAESQQReJU1aBI1NQwj2UdBhICADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
padding:5px!important;
text-decoration:none!important;
position:relative!important;
font-size:8pt!important;
margin:0px 2px 0px 2px!important;
}

div.bubbleview div.nav del {
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAN7k3eLn4d3j2+Hm397j3Nrg2OLm4Nvi2t/l3uPo4tzi2+Dl39fe1dvh2eTp49ng2OTo4tnf1/j599jf1uXp5Obq5QAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoFRVFOVASWAMCwIQgnI0xRNNExMCADs=) repeat-x!important;
border:1px solid #cfd6cd!important;
-moz-border-radius:6px!important;
color:#BBBBBB!important;
width:auto!important;
padding:5px!important;
text-decoration:none!important;
position:relative!important;
font-size:8pt!important;
margin:0px 2px 0px 2px!important;
}


div.bubbleview div.nav strong {
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAN7k3eLn4d3j2+Hm397j3Nrg2OLm4Nvi2t/l3uPo4tzi2+Dl39fe1dvh2eTp49ng2OTo4tnf1/j599jf1uXp5Obq5QAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoFRVFOVASWAMCwIQgnI0xRNNExMCADs=) repeat-x!important;
border:1px solid #cfd6cd!important;
-moz-border-radius:6px!important;
width:auto!important;
padding:5px!important;
text-decoration:none!important;
position:relative!important;
font-size:8pt!important;
margin:0px 2px 0px 2px!important;
}

/* ---------- Comments ---------- */
div.thought {
padding:2px 2px 2px 2px!important;
min-height:90px!important;
}

div.nest {
padding-left:15px!important;
}

#news #comments .thought, #news .comments .thought {
border: 6px solid #a6b2a6 !important;
margin-top:6px!important;
-moz-border-radius: 5px !important;
-moz-border-bottom-colors: #a6b2a6 #d3ded3!important;
-moz-border-top-colors: #a6b2a6 #d3ded3!important;
-moz-border-left-colors: #a6b2a6 #d3ded3 !important;
-moz-border-right-colors: #a6b2a6 #d3ded3!important;
padding: 0px !important;
background-color: #d3ded3!important;
overflow: hidden !important;
/* ---------- Arrow to the right of your icon ---------- */
/*background-image: url(data:image/gif;base64,R0lGODlhDQANAJECAKaypuTp4////wAAACH5BAEAAAIALAAAAAANAA0AAAIihI8nEO0PxHo0SkabvTlsXkniBI0ippmn85lY63qqC6tAAQA7) !important;
background-repeat: no-repeat !important;
background-position: 59px 12px !important;*/
}

#news #comments .thought .author, #news .comments .thought .author {
background: transparent !important;
margin-left: 70px !important;
padding: 4px !important;
display: inline-block !important;
padding-bottom: 0px !important;
}

#news #comments .thought .author .avatar, #news .comments .thought .author .avatar {
margin-left: 65px !important;
margin-top:-6px!important;
}

#news #comments .thought .body:not([class="preview body"]), #news .comments .thought .body:not([class="preview body"]) {
margin: 0px !important;
margin-left: 70px !important;
padding: 10px !important;
background-color: #e4e9e3 !important;
min-height:40px!important;
-moz-border-radius: 5px !important;
border: 1px solid #a6b2a6 !important;
border-left-color: #a6b2a6 !important;
border-top-width: 1px !important;
border-top-color: #a6b2a6 !important;
margin-top: -28px !important;
padding-top: 35px !important;
}

div.thought div.body {
margin-top:10px!important;
}

#news #comments .thought .links, #news .comments .thought .links {
margin-left: 70px !important;
}

/* ---------- Replybox ---------- */
textarea:not([id="newsarticle"]):not([id="newssummary"])/*, [style="font-size: 8.25pt;"], .dpToolbar*/  {
margin-left:-1px!important;
margin-top:25px!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px!important;
margin-bottom:-52px!important;
}

.dpToolbar {
margin-top:25px!important;
margin-bottom:-25px!important;
}
   
/* ---------- Reply button ---------- */
a.replybutton {
background:url(data:image/gif;base64,) no-repeat!important;
width:50px!important;
height:27px!important;
margin-left:-6px!important;
margin-bottom:-8px!important;
}

a.replybutton:hover {
background:url(data:image/gif;base64,) no-repeat!important;
width:50px!important;
height:27px!important;
margin-left:-6px!important;
margin-bottom:-8px!important;
}

a.replybutton:active {
background:url(data:image/gif;base64,) no-repeat!important;
width:50px!important;
height:27px!important;
margin-left:-6px!important;
margin-bottom:-8px!important;
}

/* ----------- The buttons ---------- */
[type="button"], [type="submit"]:not([value=""]) {
-moz-appearance:none!important;
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAN7k3eLn4d3j2+Hm397j3Nrg2OLm4Nvi2t/l3uPo4tzi2+Dl39fe1dvh2eTp49ng2OTo4tnf1/j599jf1uXp5Obq5QAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoFRVFOVASWAMCwIQgnI0xRNNExMCADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
height:27px!important;
}

[type="button"]:hover, [type="submit"]:not([value=""]):hover {
-moz-appearance:none!important;
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAObr5ubq5evu6ens5+ru6ebq5OPn4efs5unt6evv6vr7+ejs5+Hm4OXp5Ons6Ozv7OPo4uLn4OTp4+zv6wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoPI805QIBOIMywEERSNJEGREERMCADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
height:27px!important;
}

[type="button"]:active, [type="submit"]:not([value=""]):active{
-moz-appearance:none!important;
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAM3WyszVydHYztje19PZ0dTb0+Dk4ODk3+/w7tXb1Nre2cnTyN/i3N3i3Nbc1d7i3c7Xzc/Wy9zg2dzh3N3g2tfd1t/j3gAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUV4BIAESQQReJU1aBI1NQwj2UdBhICADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
height:27px!important;
}

/*div.deviousthought div.moodarea textarea, */div.deviousthought div.moodarea div.preview {
position:relative!important;
top:52px!important;
left:-68px!important;
height:196px!important;
margin: 0px !important;
margin-left: 70px !important;
margin-right: -69px !important;
padding: 5px !important;
min-height: 0px !important;
-moz-border-radius: 5px !important;
border: 1px solid #a6b2a6 !important;
border-left-color: #a6b2a6 !important;
border-top-width: 1px !important;
border-top-color: #a6b2a6 !important;
margin-top: -52px !important;
overflow:auto!important;
}

[type="button"] {
margin-top:52px!important;
}

/* ---------- Links On Comment Area ---------- */
div.deviousthought span.clinks {
margin-top:60px!important;
}

/* ---------- Hide Comment Moods ---------- */

div.deviousthought div.moodarea td.t2 div.browser, div.deviousthought div.moodarea td.t2, div.deviousthought div.moodarea th {
display:none!important;
}

/* ----------- Gets rid of the dotted outline on links when clicked ----------- */
a {
outline:none!important;
}

/* ---------- Submit News Page ---------- */
div.bubbleview div.altview, div.bubbleview div.box div.boxtop, div.bubbleview div.collapsed-thought {
background:#dae4d9!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px!important;
margin:10px!important;
}

[id="newsarticle"] {
height:500px!important;
}

div.pager-panes {
overflow-x:hidden!important;
}
}
