css:
@-moz-document url("https://www.findtubes.com") {
.flickity-enabled {
	position: relative
}
.flickity-enabled:focus {
	outline: 0
}
.flickity-viewport {
	overflow: hidden;
	position: relative;
	height: 100%
}
.flickity-slider {
	position: absolute;
	width: 100%;
	height: 100%
}
.flickity-enabled.is-draggable {
	-webkit-tap-highlight-color: transparent;
	tap-highlight-color: transparent;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none
}
.flickity-enabled.is-draggable .flickity-viewport {
	cursor: move;
	cursor: -webkit-grab;
	cursor: grab
}
.flickity-enabled.is-draggable .flickity-viewport.is-pointer-down {
	cursor: -webkit-grabbing;
	cursor: grabbing
}
.flickity-prev-next-button {
	position: absolute;
	top: 50%;
	width: 44px;
	height: 44px;
	border: none;
	border-radius: 50%;
	background: #151515;
	background: hsla(0, 0%, 100%, .75);
	cursor: pointer;
	transform: translateY(-50%)
}
.flickity-prev-next-button:hover {
	background: #151515
}
.flickity-prev-next-button:focus {
	outline: 0;
	box-shadow: 0 0 0 5px #09f
}
.flickity-prev-next-button:active {
	opacity: .6
}
.flickity-prev-next-button.previous {
	left: 10px
}
.flickity-prev-next-button.next {
	right: 10px
}
.flickity-rtl .flickity-prev-next-button.previous {
	left: auto;
	right: 10px
}
.flickity-rtl .flickity-prev-next-button.next {
	right: auto;
	left: 10px
}
.flickity-prev-next-button:disabled {
	opacity: .3;
	cursor: auto
}
.flickity-prev-next-button svg {
	position: absolute;
	left: 20%;
	top: 20%;
	width: 60%;
	height: 60%
}
.flickity-prev-next-button .arrow {
	fill: #333
}
.flickity-page-dots {
	position: absolute;
	width: 100%;
	bottom: -25px;
	padding: 0;
	margin: 0;
	list-style: none;
	text-align: center;
	line-height: 1
}
.flickity-rtl .flickity-page-dots {
	direction: rtl
}
.flickity-page-dots .dot {
	display: inline-block;
	width: 10px;
	height: 10px;
	margin: 0 8px;
	background: #151515;
	border-radius: 50%;
	opacity: .25;
	cursor: pointer
}
.flickity-page-dots .dot.is-selected {
	opacity: 1
}
a,
abbr,
acronym,
address,
applet,
article,
aside,
audio,
b,
big,
blockquote,
body,
button,
canvas,
caption,
center,
cite,
code,
dd,
del,
details,
dfn,
div,
dl,
dt,
em,
embed,
fieldset,
figcaption,
figure,
footer,
form,
h1,
h2,
h3,
h4,
h5,
h6,
header,
html,
i,
iframe,
img,
ins,
kbd,
label,
legend,
li,
mark,
menu,
nav,
object,
ol,
