	padding: 0;
	vertical-align: top
}
article,
aside,
details,
figcaption,
figure,
footer,
header,
menu,
nav,
section {
	display: block
}
body {
	line-height: 1
}
ol,
ul {
	list-style: none
}
blockquote,
q {
	quotes: none
}
blockquote:after,
blockquote:before,
q:after,
q:before {
	content: ""
}
table {
	border-collapse: collapse;
	border-spacing: 0
}
html {
	box-sizing: border-box
}
*,
:after,
:before {
	box-sizing: inherit
}
button {
	cursor: pointer
}
button:focus {
	box-shadow: inset 0 0 10px #000;
	outline: none
}
input[type=search]::-webkit-search-cancel-button,
input[type=search]::-webkit-search-decoration,
input[type=search]::-webkit-search-results-button,
input[type=search]::-webkit-search-results-decoration {
