.triangle-animated {
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translateX(-50%) translateY(-50%);
	padding-top: 22px
}
.triangle-animated .triangle {
	transition: all .7s ease-in-out;
	stroke-dasharray: 240;
	stroke-dashoffset: 480;
	stroke: #fff;
	transform: translateY(0)
}
.triangle-animated:hover .triangle {
	stroke-dashoffset: 0;
	opacity: 1;
	stroke: #fff;
	z-index: 2
}
.triangle-animated:hover .circle {
	stroke-dashoffset: 0;
	opacity: 1
}
.triangle-animated svg {
	height: 100px;
	width: 100px
}
