[data-pbw-tooltip] {
	position: relative
}
[data-pbw-tooltip]:after,
[data-pbw-tooltip]:before {
	bottom: 100%;
	filter: alpha(opacity=0);
	left: 50%;
	opacity: 0;
	pointer-events: none;
	position: absolute;
	transform: translate(-50%, 10px);
	transform-origin: top;
	transition: all .3s;
	z-index: 2
}
[data-pbw-tooltip]:after {
	background: #151515;
	border-radius: 1px;
	box-shadow: 0 0 20px 1px rgba(0, 0, 0, .5);
	color: #444;
	content: attr(data-pbw-tooltip);
	font-size: 12px;
	margin-bottom: 10px;
	padding: 5px 10px;
	white-space: nowrap
}
[data-pbw-tooltip]:before {
	background-color: #333;
	background-size: 100% auto;
	content: "";
	height: 5px;
	margin-bottom: 5px;
	width: 15px
}
[data-pbw-tooltip]:hover:after,
[data-pbw-tooltip]:hover:before {
	filter: alpha(opacity=100);
	opacity: 1;
	pointer-events: auto;
	transform: translate(-50%);
	transition-delay: 1.5s, .1s
}
[data-pbw-tooltip][data-pbw-tooltip-break]:after {
	white-space: normal
}
[data-pbw-tooltip-pos=down]:after,
[data-pbw-tooltip-pos=down]:before {
	bottom: auto;
	left: 50%;
	top: 100%;
	transform: translate(-50%, -10px)
}
[data-pbw-tooltip-pos=down]:after {
	margin-top: 10px
}
[data-pbw-tooltip-pos=down]:before {
	background-color: #333;
	background-size: 100% auto;
	height: 5px;
	margin-bottom: 0;
	margin-top: 5px;
	width: 15px
}
[data-pbw-tooltip-pos=down]:hover:after,
[data-pbw-tooltip-pos=down]:hover:before {
	transform: translate(-50%)
}
[data-pbw-tooltip-pos=left]:after,
[data-pbw-tooltip-pos=left]:before {
	bottom: auto;
	left: auto;
	right: 100%;
	top: 50%;
	transform: translate(10px, -50%)
}
[data-pbw-tooltip-pos=left]:after {
	margin-right: 10px
}
[data-pbw-tooltip-pos=left]:before {
	background-color: #333;
	background-size: 100% auto;
	height: 5px;
	margin-bottom: 0;
	margin-right: 5px;
	width: 15px
}
[data-pbw-tooltip-pos=left]:hover:after,
[data-pbw-tooltip-pos=left]:hover:before {
	transform: translateY(-50%)
}
[data-pbw-tooltip-pos=right]:after,
[data-pbw-tooltip-pos=right]:before {
	bottom: auto;
	left: 100%;
	top: 50%;
	transform: translate(-10px, -50%)
}
[data-pbw-tooltip-pos=right]:after {
	margin-left: 10px
}
[data-pbw-tooltip-pos=right]:before {
	background-color: #333;
	background-size: 100% auto;
	height: 5px;
	margin-bottom: 0;
	margin-left: 5px;
	width: 15px
}
[data-pbw-tooltip-pos=right]:hover:after,
[data-pbw-tooltip-pos=right]:hover:before {
	transform: translateY(-50%)
}
[data-pbw-tooltip-length]:after {
	white-space: normal
}
[data-pbw-tooltip-length=small]:after {
	width: 80px
}
[data-pbw-tooltip-length=medium]:after {
	width: 150px
}
[data-pbw-tooltip-length=large]:after {
	width: 260px
}
[data-pbw-tooltip-length=fit]:after {
	width: 100%
}
.locale-selector-wrapper {
