	.fold-out-filter-button {
		padding: 10px 15px
	}
	.search .search-bar #search_query div .add-clear-span a:link,
	.search .search-bar #search_query div .add-clear-span a:visited {
		top: 0!important;
		line-height: 40px!important;
		height: 40px
	}
	.search .search-bar #search_query div .add-clear-span a:link i,
	.search .search-bar #search_query div .add-clear-span a:visited i {
		line-height: 40px!important
	}
	.navigation.row {
		margin: 0
	}
	.navigation.row>.column {
		text-align: left
	}
	.filter_form .filter_container {
		max-height: 400px;
		overflow-y: auto;
		left: 10%;
		right: 10%;
		position: fixed;
		width: 80%
	}
	.filter_form .filter_container .content-filter-container {
		margin: 0 0 5px;
		min-height: 0;
		width: 100%
	}
	.filter_form .filter_container .content-filter-container h3 {
		font-size: 24px;
		line-height: 110%
	}
	.filter_form .filter_container .content-filter-container .content-filter-widget {
		height: auto;
		display: none!important;
		opacity: 0!important;
		font-size: 18px
	}
	.filter_form .filter_container .content-filter-container .content-filter-widget.active {
		display: block!important;
		opacity: 1!important
	}
	.filter_form .filter_container .content-filter-container .filter_duration_widget {
		left: auto;
		right: auto;
		width: 85%;
		padding: 10px;
		position: relative;
		display: block;
		margin: 0 auto;
		overflow: hidden
	}
	i.mobile-category-list {
		right: 0;
		top: -45px;
		height: 45px;
		width: 45px;
		line-height: 45px;
		margin: 0;
		padding: 0;
		font-size: 14px
	}
	i.mobile-category-list .mobile-category-list-description {
		display: none
	}
	.category-select .category-group h3 {
		font-size: 16px
	}
	.category-select .category-group .category {
		font-size: 14px
	}
}
@media screen and (orientation:landscape) {
	.category-select {
		width: 380px
	}
	.category-select .category-select-list {
		width: 335px;
		padding: 5px 60px 5px 5px
	}
	.category-select .category-group span {
		margin-right: 65px
	}
	.category-select .mobile-category-anchors {
		width: 60px
	}
	.category-select .mobile-category-anchors li {
		width: 30px
	}
}
@media screen and (max-width:1023px) and (orientation:landscape) {
	.category-list.category-list-all {
		padding-right: 145px
	}
	.category-list.category-list-all .mobile-category-anchors {
		width: 135px
	}
}
@media only screen and (orientation:landscape) and (min-device-width:320px) and (max-device-width:1366px) and (orientation:landscape),
only screen and (orientation:portrait) and (min-device-width:320px) and (max-device-width:1366px) and (orientation:landscape) {
	.category-list.category-list-all {
		padding-right: 145px
	}
	.category-list.category-list-all .mobile-category-anchors {
		width: 135px
	}
}
